ejb-operation.pre-activation-actions:
//System.out.println("###### INGRESÓ A ACTIVADOR MLNFCargarGrupozZonas #######");
es.indra.sicc.dtos.cra.DTOSeleccionPaisMarcaCanal dtoEntrada = dts.getProperty("DTO_INPUT");
//System.out.println("###### CONSTRUYÓ dtoEntrada EN ACTIVADOR MLNFCargarGrupozZonas #######");

ejb-operation.post-activation-actions:
//System.out.println("###### INGRESÓ A POST ACTIVACIÓN EN ACTIVADOR MLNFCargarGrupozZonas #######");
es.indra.sicc.util.DTOSalida dtoSalida = dts.getLastResult();
es.indra.sicc.dtos.cra.DTOCargarGruposZonasActividades resultado = new es.indra.sicc.dtos.cra.DTOCargarGruposZonasActividades();
resultado.setGruposZonas(dtoSalida.getResultado());
//System.out.println("###### AGREGÓ RESULTADO DE LLAMADA A MONMantenimientoGruposZonas AL DTOCargarGruposZonasActividades #######");
dts.setProperty("resultadoIntermedio", resultado);
//System.out.println("###### SETEO RESULTADO INTERMEDIO Y SALIÓ DE POST ACTIVACIÓN EN ACTIVADOR MLNFCargarGrupozZonas #######");
