select gen_pais.VAL_I18N,  cro.FEC_PREV, act.COD_ACTI, gen.VAL_I18N as actividad, cor.VAL_ANIO as anio,per.VAL_NOMB_PERI as periodo, gen2.VAL_I18N as grupo, 0 as CANTIDAD  from cra_crono_grupo_zona cro,  	 cra_cabec_grupo_zona cab, 	 cra_activ act, 	( 	 Select g.VAL_OID, g.VAL_I18N 	 From 	 	 gen_i18n_sicc g 	 Where	 			g.ATTR_ENTI='CRA_ACTIV' 		and g.IDIO_OID_IDIO=  1  	) gen, 	( 	 Select g.VAL_OID, g.VAL_I18N 	 From 	 	 gen_i18n_sicc g 	 Where	 			g.ATTR_ENTI='CRA_CABEC_GRUPO_ZONA' 		and g.IDIO_OID_IDIO=  1  	) gen2, 	( 	 Select g.VAL_OID, g.VAL_I18N 	 From 	 	 gen_i18n_sicc g 	 Where	 			g.ATTR_ENTI='SEG_PAIS' 		and g.IDIO_OID_IDIO=  1  	) gen_pais, 	cra_perio per, 	seg_perio_corpo cor where  		cab.OID_CABE_GRUP_ZONA=cro.CGZO_OID_CABE_GRUP_ZONA 	and per.OID_PERI=cro.PERD_OID_PERI 	and per.peri_OID_peri=cor.OID_PERI 	and cro.CACT_OID_ACTI=act.OID_ACTI 	and gen.VAL_OID(+)=act.OID_ACTI 	and gen2.VAL_OID(+)=cab.OID_CABE_GRUP_ZONA 	and gen_pais.VAL_OID(+)=cab.PAIS_OID_PAIS 	and cab.PAIS_OID_PAIS=  1  	and cab.MARC_OID_MARC =  1  	and cab.CANA_OID_CANA =  1  	and cro.PERD_OID_PERI in (  57  ) 	and cro.CACT_OID_ACTI in (  1  )  	and to_date(cro.FEC_PREV) = to_date( '02/12/2004' ,  'dd/MM/yyyy'  ) order by  	actividad, anio, periodo, grupo