select cod_regi, cod_grup, cod_acti, des_regi,   actividad, periodo, zonas, fecha_prev, fecha_fina, n_fecha_prev,n_fecha_fina,dia_fecha_prev,dia_fecha_fina   from  ( 	select cod_regi, cod_grup, des_regi,  cod_acti, actividad, periodo, cod_zona, 	     TO_CHAR(fecha_prev,'dd-mm') fecha_prev,  		 TO_CHAR(fecha_fina,'dd-mm') fecha_fina, 		TO_CHAR(fecha_prev,'d') n_fecha_prev, 		TO_CHAR(fecha_fina,'d') n_fecha_fina, 		LPAD(TO_CHAR(fecha_prev,'DY'),2) dia_fecha_prev, 		LPAD(TO_CHAR(fecha_fina,'DY'),2) dia_fecha_fina, 		pq_apl_rep.FN_008_CONCAT_ZONA(oid_peri, OID_REGI, OID_CRON_GRUP_ZONA,  1 ,  1 ,  1 ) as zonas,  0 as CANTIDAD 	from ( 			select reg.cod_regi, cab.cod_grup, act.COD_ACTI, reg.OID_REGI,   cro.OID_CRON_GRUP_ZONA, per.VAL_NOMB_PERI periodo, per.oid_peri, reg.des_regi,  zon.cod_zona, 			        gen_act.val_i18n AS actividad, cro.fec_prev fecha_prev, cro.fec_fina fecha_fina 			from zon_regio reg, 				 zon_zona zon,  				 cra_crono_grupo_zona cro, 				 cra_cabec_grupo_zona cab, 				 cra_detal_grupo_zona det, 				 cra_perio per, 				 cra_activ act, 			    ( 				 SELECT gen.val_oid, gen.val_i18n 			     FROM v_gen_i18n_sicc gen 			     WHERE gen.attr_enti = 'CRA_ACTIV' 			     	   AND gen.idio_oid_idio =  1  			     ) gen_act 			where zon.ZORG_OID_REGI=reg.OID_REGI 				  and cro.CGZO_OID_CABE_GRUP_ZONA=cab.OID_CABE_GRUP_ZONA 				  and cab.OID_CABE_GRUP_ZONA=det.CGZO_OID_CABE_GRUP_ZONA 				  and det.ZZON_OID_ZONA=zon.OID_ZONA 				  and cro.PERD_OID_PERI=per.OID_PERI 				  and cro.CACT_OID_ACTI=act.OID_ACTI  				  and act.oid_acti=gen_act.val_oid 				  and cro.PERD_OID_PERI=  62  				  and reg.pais_oid_pais =  1  				  and reg.marc_oid_marc =  1  	  			  and reg.cana_oid_cana =  1    	  			  and zon.pais_oid_pais =  1  	  			  AND zon.marc_oid_marc =  1  	  			  AND zon.cana_oid_cana =  1  	  			  and cab.pais_oid_pais =  1  	  			  and cab.marc_oid_marc =  1  	  			  and cab.cana_oid_cana =  1  	) )a group by cod_regi, cod_grup, cod_acti, des_regi,   actividad, periodo, fecha_prev, fecha_fina,  zonas, n_fecha_prev,n_fecha_fina, dia_fecha_prev, dia_fecha_fina  order by des_regi, cod_grup, zonas, cod_acti