SELECT * FROM ( 	SELECT oid_curs, 		   plantilla, 		   des_curs, 		   COUNT(oid_clie)cantidad,    		   IND_ASIS ASISTENCIA, 		   cod_zona, 		   modalidad, 		   fec_disp_curs, 		   FEC_LANZ, 		   fec_fin_curs, 		   desc_periodo_n, 		   SUM(periodo_n)periodo_n, 		   desc_periodo_n_1, 		   SUM(periodo_n_1)periodo_n_1, 		   desc_periodo_n_2, 		   SUM(periodo_n_2)periodo_n_2, 		   desc_periodo_n_3, 		   SUM(periodo_n_3)periodo_n_3, 		   desc_periodo_n_4, 		   SUM(periodo_n_4)periodo_n_4, 		   desc_periodo_n_5, 		   SUM(periodo_n_5)periodo_n_5, 		   desc_periodo_n_6, 		   SUM(periodo_n_6)periodo_n_6, 		   desc_periodo_n_7, 		   SUM(periodo_n_7)periodo_n_7, 		   desc_periodo_n_8, 		   SUM(periodo_n_8)periodo_n_8, 		   desc_periodo_n_9, 		   SUM(periodo_n_9)periodo_n_9,  		   SUM(1) over (PARTITION BY oid_curs, modalidad ORDER BY cod_zona) control 	FROM  	( 		SELECT DISTINCT cur.oid_curs, 			   cur.DES_CURS, 			  zon.cod_zona, 			  DECODE(his.ind_apta,1,'APTA','INVITADA')modalidad,  			  cur.CPLC_OID_CABE_PLAN_CURS plantilla, 			  cur.FEC_DISP_CURS, 			  cur.FEC_LANZ, 			  cur.FEC_FIN_CURS, 			  his.IND_ASIS, 			  cli.OID_CLIE, 		    PERIODOS.DESC_PER_N desc_periodo_n,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N 		  )periodo_n, 		  PERIODOS.DESC_PER_N1 desc_periodo_n_1,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N1 		  )periodo_n_1, 		  PERIODOS.DESC_PER_N2 desc_periodo_n_2,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N2 		  )periodo_n_2, 		  PERIODOS.DESC_PER_N3 desc_periodo_n_3,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N3 		  )periodo_n_3, 		   PERIODOS.DESC_PER_N4 desc_periodo_n_4,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N4 		  )periodo_n_4, 		  PERIODOS.DESC_PER_N5 desc_periodo_n_5,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N5 		  )periodo_n_5, 		  PERIODOS.DESC_PER_N6 desc_periodo_n_6,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N6 		  )periodo_n_6,           PERIODOS.DESC_PER_N7 desc_periodo_n_7,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N7 		  ) periodo_n_7, 		  PERIODOS.DESC_PER_N8 desc_periodo_n_8,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N8 		  )periodo_n_8, 		   PERIODOS.DESC_PER_N9 desc_periodo_n_9,  		  ( 			  SELECT SUM(SOL.VAL_PREC_CATA_TOTA_LOCA) 			  FROM ped_solic_cabec sol 			  WHERE sol.CLIE_OID_CLIE = cli.oid_clie 					AND SOL.IND_TS_NO_CONSO  < > 1  	  	    					AND sol.perd_oid_peri = PERIODOS.OID_PER_N9 		  )periodo_n_9 						  		  		  		   		FROM edu_matri_curso cur, 			 edu_histo_curso his,  			 mae_clien cli, 			 mae_clien_unida_admin un_adm, 			 zon_terri_admin ter_adm, 			 zon_secci sec, 			 zon_zona zon, 			 ( 			  SELECT OID_PER_N, 				  				OID_PER_N1, 								OID_PER_N2, 								OID_PER_N3, 								OID_PER_N4, 								OID_PER_N5, 								OID_PER_N6, 								OID_PER_N7, 								OID_PER_N8, 								OID_PER_N9, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N 								) DESC_PER_N, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N1 								) DESC_PER_N1, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N2 								) DESC_PER_N2, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N3 								) DESC_PER_N3, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N4 								) DESC_PER_N4, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N5 								) DESC_PER_N5, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N6 								) DESC_PER_N6, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N7 								) DESC_PER_N7, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N8 								) DESC_PER_N8, 								( 								 		   SELECT PER.VAL_NOMB_PERI 										   FROM CRA_PERIO PER 										   WHERE PER.OID_PERI = OID_PER_N9 								) DESC_PER_N9 				FROM						  				(							  						SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,1) OID_PER_N, 							   			  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,2) OID_PER_N1, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,3) OID_PER_N2, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,4) OID_PER_N3, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,5) OID_PER_N4, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,6) OID_PER_N5, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,7) OID_PER_N6, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,8) OID_PER_N7, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,9) OID_PER_N8, 										  PQ_APL_REP.FN_008_CALC_OID_PER_ANT(PERI.PAIS_OID_PAIS,PERI.MARC_OID_MARC,PERI.CANA_OID_CANA,PERI.OID_PERI,10) OID_PER_N9				   						FROM CRA_PERIO PERI 						WHERE PERI.OID_PERI =  57 						  						)	 		) PERIODOS						  WHERE his.MCUR_OID_CURS = cur.OID_CURS 			  AND his.CLIE_OID_CLIE = cli.OID_CLIE 			  AND un_adm.CLIE_OID_CLIE = cli.oid_clie 			  AND un_adm.ZTAD_OID_TERR_ADMI = ter_adm.OID_TERR_ADMI 			  AND ter_adm.ZSCC_OID_SECC = sec.OID_SECC 			  AND sec.ZZON_OID_ZONA = zon.OID_ZONA 			   AND cur.cana_oid_cana =  1        	 	   AND cur.marc_oid_marc =  1  	       		   AND cur.pais_oid_pais =  1        		   AND zon.oid_zona =  1   			   AND his.ind_apta =  1      			     AND cur.OID_CURS =  1     		    AND cur.CPLC_OID_CABE_PLAN_CURS =  1   	) 	GROUP BY  		oid_curs, 		plantilla, 		   des_curs, 		   cod_zona, 		   modalidad, 		   fec_disp_curs, 		   fec_lanz, 		   fec_fin_curs, 		   IND_ASIS, 		   desc_periodo_n, 		   desc_periodo_n_1, 		   desc_periodo_n_2, 		   desc_periodo_n_3, 		   desc_periodo_n_4, 		   desc_periodo_n_5, 		   desc_periodo_n_6, 		   desc_periodo_n_7, 		   desc_periodo_n_8, 		   desc_periodo_n_9    ORDER BY periodo_n  a  ) WHERE control  < =  5 