SELECT MARCA, CANAL, DIA, DIGITAD, VEN_ING, DOC_INGRESO, ERROR_ING, HORA_ING, COD_VEN_VER, DOC_VERI, HORA_VER,  	   ING_HORA, NVL(VER_HORA, 0) VER_HORA, NVL(((ING_HORA*100)/2500 + (VER_HORA*100)/2500),0) EVAL FROM 	( 	SELECT MARCA, CANAL, DIA, NOMBREUSUARIO, DIGITAD, SUM(VEN_ING) AS VEN_ING, SUM(DOC_INGRESO) AS DOC_INGRESO,  		   (DECODE(SUM(VEN_ING),0,0,SUM(ERROR_ING) / SUM(VEN_ING)))*100 AS ERROR_ING,  		   NVL(SUM((HOR_ING_FIN-HOR_ING_INI)*24), 0) AS HORA_ING,  		   NVL(SUM(COD_VEN_VER),0) AS COD_VEN_VER, 		   NVL(SUM(DOC_VERI),0) AS DOC_VERI,  		   NVL(SUM((HOR_VER_FIN-HOR_VER_INI)*24),0) HORA_VER, 		   DECODE(SUM((HOR_ING_FIN-HOR_ING_INI)*24), 0 ,0,SUM(VEN_ING) / SUM((HOR_ING_FIN-HOR_ING_INI)*24)) ING_HORA, 		   DECODE(SUM((HOR_VER_FIN-HOR_VER_INI)*24), 0 ,0, SUM(COD_VEN_VER) / SUM((HOR_VER_FIN-HOR_VER_INI)*24)) VER_HORA	 	FROM	    	(	    		   SELECT  		   		  MAR.DES_MARC AS MARCA, GEN_SEG_CAN.VAL_I18N AS CANAL,  				  TO_DATE(EST_DIG.FEC_INIC_SESI,'DD.MM.YYYY') AS DIA, USU.VAL_NOMB_USUA AS NOMBREUSUARIO, 		   		  USU.VAL_NOM1 ||' '|| USU.VAL_NOM2||' '|| USU.VAL_APEL_PATE AS DIGITAD, 		   		  ( 		   	   	   SELECT EST_DIG.NUM_CV 			   	   FROM PED_PROCE PED_PRO 			   	   WHERE PED_PRO.COD_PROC = 'P115' 			         	 AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC			  		   		  ) VEN_ING, 		   		  ( 		   	   	   SELECT EST_DIG.NUM_OC 		   	   	   FROM PED_PROCE PED_PRO 			   	   WHERE PED_PRO.COD_PROC = 'P115' 	   		   		 	 AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC			  		   		  ) DOC_INGRESO, 		   		  ( 		       	   SELECT EST_DIG.NUM_ERRO_CV 			   	   FROM PED_PROCE PED_PRO 			   	   WHERE PED_PRO.COD_PROC = 'P115' 	   		    	 	 AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC			  		   		  ) ERROR_ING,     	   		  ( 		   	   	   SELECT EST_DIG.FEC_INIC_SESI  			   	   FROM PED_PROCE PED_PRO 			   	   WHERE PED_PRO.COD_PROC = 'P115' 	   		   		 	 AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC			  		   		  ) HOR_ING_INI, 		   		  ( 		   	   	   SELECT EST_DIG.FEC_FIN_SESI 			   	   FROM PED_PROCE PED_PRO 			   	   WHERE PED_PRO.COD_PROC = 'P115' 			    	 	 AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC			  		   		   ) HOR_ING_FIN, 		   		   ( 		   	   	   	SELECT EST_DIG.NUM_CV 			   		FROM PED_PROCE PED_PRO 			   		WHERE PED_PRO.COD_PROC = 'P120' 			    	 	  AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC 		   		   ) COD_VEN_VER,	 		   		   ( 		   	   	   	SELECT EST_DIG.NUM_OC  			   		FROM PED_PROCE PED_PRO 			   		WHERE PED_PRO.COD_PROC = 'P120' 			   	     	  AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC 		   		   ) DOC_VERI, 		   		   ( 		       	   	SELECT EST_DIG.FEC_INIC_SESI 			   		FROM PED_PROCE PED_PRO 			   		WHERE PED_PRO.COD_PROC = 'P120' 			         	  AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC 		   		   ) HOR_VER_INI, 		   		   ( 		   	   	   	SELECT EST_DIG.FEC_FIN_SESI 			   		FROM PED_PROCE PED_PRO 			   		WHERE PED_PRO.COD_PROC = 'P120' 			   		 AND PED_PRO.OID_PROC = EST_DIG.PROC_OID_PROC			  		   		   ) HOR_VER_FIN	   	    		   FROM SEG_MARCA MAR, 		        PED_TIPO_SOLIC TIP_SOL, 				SEG_CANAL CAN, 				SEG_ACCES ACC, 				SEG_SUBAC SUBA, 				PED_ESTAD_DIGIT EST_DIG, 				V_REP_USUAR_LARGO USU, 				PED_TIPO_SOLIC_PAIS TIP_SOL_PAI, 				( 				SELECT GEN.VAL_OID , GEN.VAL_I18N 				FROM V_GEN_I18N_SICC GEN 				WHERE GEN.ATTR_ENTI = 'SEG_CANAL' 				      AND GEN.IDIO_OID_IDIO =  1  			    ) GEN_SEG_CAN, 				( 				SELECT GEN.VAL_OID , GEN.VAL_I18N 				FROM V_GEN_I18N_SICC GEN 				WHERE GEN.ATTR_ENTI = 'PED_TIPO_SOLIC' 				      AND GEN.IDIO_OID_IDIO =  1  			    ) GEN_TIP_SOL	  			WHERE MAR.OID_MARC = TIP_SOL.MARC_OID_MARC 				  AND GEN_TIP_SOL.VAL_OID(+) = TIP_SOL.OID_TIPO_SOLI 				  AND GEN_SEG_CAN.VAL_OID(+) = CAN.OID_CANA  				  AND ACC.CANA_OID_CANA = CAN.OID_CANA 				  AND SUBA.ACCE_OID_ACCE = ACC.OID_ACCE 				  AND USU.VAL_NOMB_USUA = EST_DIG.VAL_USUA 				  AND EST_DIG.TSPA_OID_TIPO_SOLI_PAIS = TIP_SOL_PAI.OID_TIPO_SOLI_PAIS 				  AND TIP_SOL_PAI.TSOL_OID_TIPO_SOLI = TIP_SOL.OID_TIPO_SOLI 				  AND SUBA.OID_SBAC = TIP_SOL.SBAC_OID_SBAC 				  AND TIP_SOL_PAI.PAIS_OID_PAIS =  1   				   				  AND MAR.OID_MARC =  1  				  AND CAN.OID_CANA =  1  				   AND ACC.OID_ACCE IN ( 1 ) 				    				   AND SUBA.OID_SBAC IN ( 1 )   				  AND to_date(EST_DIG.FEC_INIC_SESI) >= to_date( '01/01/2004' ,  'dd/MM/yyyy'  )  				  AND to_date(EST_DIG.FEC_INIC_SESI)  < = to_date( '01/01/2004' ,  'dd/MM/yyyy'  ) 		) GROUP BY MARCA, 	  	 CANAL, 		 DIA, 		 NOMBREUSUARIO, 		 DIGITAD )   