SELECT COMB.PAIS_OID_PAIS, 	   COMB.VAL_I18N PAIS, 	   COMB.OID_MARC, 	   COMB.DES_MARC MARCA, 	   COMB.OID_CANA, 	   COMB.CANAL CANAL, 	   COMB.OID_PERI, 	   COMB.PERIODO, 	   COMB.OID_ACCE, 	   COMB.ACCESO, 	   COMB.OID_SBAC, 	   COMB.SUBACCESO, 	   COMB.OID_REGI, 	   COMB.REGION, 	   COMB.OID_ZONA, 	   COMB.ZONA, 	   SOLIC.SECCION, 	   SOLIC.DESC_TIP_SOL, 	   SOLIC.TIP_CLI, 	   SOLIC.SUBT_CLI, 	   SOLIC.COD_CLIE, 	   SOLIC.FEC_CREACION, 	   SOLIC.HOR_CREACION, 	   SOLIC.FEC_FACT, 	   SOLIC.HOR_FACT, 	   SOLIC.TIP_DOC, 	   SOLIC.NUM_DOC_LEG,      	  solic.val_nume_soli, 	   SOLIC.UNIDADES,   	   SOLIC.VAL_TOTA_PAGA_LOCA FROM (	 	SELECT TIP_SOL_PAIS.PAIS_OID_PAIS, 		   MARC.OID_MARC, 		   CANAL.OID_CANA, 		   PERIO.OID_PERI, 		   ACCE.OID_ACCE, 		   SUBAC.OID_SBAC, 		   ZONA.OID_ZONA, 		   REG.OID_REGI, 		   TIP_SOL.OID_TIPO_SOLI, 		   GEN_TIP_SOL.VAL_I18N DESC_TIP_SOL, 		   TIP_SOL_PAIS.OID_TIPO_SOLI_PAIS, 		   SOL_CAB.OID_SOLI_CABE, 		   SOL_CAB.VAL_NUME_SOLI, 		   SOL_CAB.SOCA_OID_SOLI_CABE, 		   GEN_TIP_CLI.VAL_I18N TIP_CLI, 		   GEN_SUBT_CLI.VAL_I18N SUBT_CLI, 		   MAE_CLI.COD_CLIE, 		   ( 		   SELECT TO_DATE(SEG_PED.FEC, 'DD/MM/YYYY') 		   FROM PED_SEGUI_PEDID SEG_PED 		   WHERE SEG_PED.HISE_OID_HITO = 1 		   		 AND SOL_CAB.OID_SOLI_CABE = SEG_PED.SOCA_OID_SOLI_CABE 		   ) FEC_CREACION,		    		   ( 		   SELECT TO_CHAR(SEG_PED.FEC, 'HH24:MI:SS') 		   FROM PED_SEGUI_PEDID SEG_PED 		   WHERE SEG_PED.HISE_OID_HITO = 1 		   		 AND SOL_CAB.OID_SOLI_CABE = SEG_PED.SOCA_OID_SOLI_CABE 		   ) HOR_CREACION,		    		   SOL_CAB.VAL_TOTA_PAGA_LOCA, 		   SEC.DES_SECCI SECCION,		    		   SOL_CAB.NUM_UNID_ATEN_TOTA UNIDADES, 		   GEN_TIP_DOC.DES_TIPO_DOCU TIP_DOC, 		   ( 		   SELECT TO_DATE(SEG_PED.FEC, 'DD/MM/YYYY') 		   FROM PED_SEGUI_PEDID SEG_PED 		   WHERE HISE_OID_HITO = 5 		   		 AND SOL_CAB.SOCA_OID_SOLI_CABE = SEG_PED.SOCA_OID_CONS 		   ) FEC_FACT, 		   ( 		   SELECT TO_CHAR(SEG_PED.FEC, 'HH24:MI:SS') 		   FROM PED_SEGUI_PEDID SEG_PED 		   WHERE HISE_OID_HITO = 5 		   		 AND SOL_CAB.SOCA_OID_SOLI_CABE = SEG_PED.SOCA_OID_CONS 		   ) HOR_FACT, 		   ( 		   SELECT FAC_DOC.NUM_DOCU_LEGA 		   FROM FAC_DOCUM_CONTA_CABEC FAC_DOC 		   WHERE SOL_CAB.OID_SOLI_CABE = FAC_DOC.SOCA_OID_SOLI_CABE 		   		 AND ROWNUM = 1		   		    		   ) NUM_DOC_LEG		 		 				  		     		   	  	FROM SEG_CANAL CANAL, 		 SEG_MARCA MARC, 		 SEG_ACCES ACCE, 		 SEG_SUBAC SUBAC, 		 PED_TIPO_SOLIC TIP_SOL, 		 PED_TIPO_SOLIC_PAIS TIP_SOL_PAIS, 		 CRA_PERIO PERIO, 		 ZON_ZONA ZONA, 		 ZON_REGIO REG, 		 PED_SOLIC_CABEC SOL_CAB, 		 MAE_CLIEN MAE_CLI, 		 MAE_CLIEN_TIPO_SUBTI MAE_CLI_TIP, 		 MAE_TIPO_CLIEN MAE_TIP_CLI, 		 MAE_SUBTI_CLIEN MAE_SUB_CLI, 		 ZON_SECCI SEC, 		 FAC_TIPO_DOCUM GEN_TIP_DOC, 		 ( 		 SELECT GEN.VAL_OID, GEN.VAL_I18N 		 FROM V_GEN_I18N_SICC GEN 		 WHERE GEN.ATTR_ENTI = 'MAE_TIPO_CLIEN' 		 	   AND GEN.IDIO_OID_IDIO =  1  		 ) GEN_TIP_CLI, 		 ( 		 SELECT GEN.VAL_OID, GEN.VAL_I18N 		 FROM V_GEN_I18N_SICC GEN 		 WHERE GEN.ATTR_ENTI = 'MAE_SUBTI_CLIEN' 		 	   AND GEN.IDIO_OID_IDIO =  1  		 ) GEN_SUBT_CLI, 		 ( 		 SELECT GEN.VAL_OID, GEN.VAL_I18N 		 FROM V_GEN_I18N_SICC GEN 		 WHERE GEN.ATTR_ENTI = 'PED_TIPO_SOLIC' 		 	   AND GEN.IDIO_OID_IDIO =  1  		 ) GEN_TIP_SOL 	WHERE ACCE.CANA_OID_CANA = CANAL.OID_CANA 		  AND SUBAC.ACCE_OID_ACCE = ACCE.OID_ACCE 		  AND TIP_SOL.SBAC_OID_SBAC = SUBAC.OID_SBAC(+) 		  AND TIP_SOL.ACCE_OID_ACCE = ACCE.OID_ACCE 		  AND TIP_SOL.MARC_OID_MARC = MARC.OID_MARC 		  AND PERIO.CANA_OID_CANA = CANAL.OID_CANA 		  AND PERIO.MARC_OID_MARC = MARC.OID_MARC 		  AND ZONA.CANA_OID_CANA = CANAL.OID_CANA 		  AND ZONA.MARC_OID_MARC = MARC.OID_MARC 		  AND ZONA.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 		  AND REG.OID_REGI = ZONA.ZORG_OID_REGI 		  AND SOL_CAB.ZZON_OID_ZONA = ZONA.OID_ZONA 		  AND SOL_CAB.PERD_OID_PERI = PERIO.OID_PERI 		  AND MAE_SUB_CLI.OID_SUBT_CLIE = MAE_CLI_TIP.SBTI_OID_SUBT_CLIE 		  AND MAE_CLI_TIP.CLIE_OID_CLIE = MAE_CLI.OID_CLIE 		  AND MAE_TIP_CLI.OID_TIPO_CLIE = MAE_CLI_TIP.TICL_OID_TIPO_CLIE 		  AND MAE_SUB_CLI.TICL_OID_TIPO_CLIE = MAE_TIP_CLI.OID_TIPO_CLIE 		  AND SOL_CAB.TIDO_OID_TIPO_DOCU = GEN_TIP_DOC.OID_TIPO_DOCU(+) 		  AND ZONA.OID_ZONA = SEC.ZZON_OID_ZONA 		  AND MAE_TIP_CLI.OID_TIPO_CLIE = GEN_TIP_CLI.VAL_OID(+) 		  AND MAE_SUB_CLI.OID_SUBT_CLIE = GEN_SUBT_CLI.VAL_OID(+) 		  AND TIP_SOL.OID_TIPO_SOLI = GEN_TIP_SOL.VAL_OID(+) 		  AND TIP_SOL_PAIS.TSOL_OID_TIPO_SOLI = TIP_SOL.OID_TIPO_SOLI 		  AND SOL_CAB.TSPA_OID_TIPO_SOLI_PAIS = TIP_SOL_PAIS.OID_TIPO_SOLI_PAIS 		  AND SOL_CAB.CLIE_OID_CLIE = MAE_CLI.OID_CLIE 		  AND SOL_CAB.FEC_FACT IS NOT NULL 		  AND SOL_CAB.IND_TS_NO_CONSO = 1 		  AND SOL_CAB.IND_PEDI_PRUE  < > 1  		  AND PERIO.OID_PERI IN (  57  ) 		  AND TIP_SOL_PAIS.PAIS_OID_PAIS =  1  		  AND CANAL.OID_CANA =  1  		  AND MARC.OID_MARC =  1  		   AND ACCE.OID_ACCE IN (  1  )  		   AND SUBAC.OID_SBAC IN (  1  ) 	  		   AND ZONA.OID_ZONA IN (  1  ) 	   		   AND REG.OID_REGI IN (  1  )  		   AND TIP_SOL.OID_TIPO_SOLI IN (  1  )  	) SOLIC, 	(  	SELECT DISTINCT   TIP_SOL_PAIS.PAIS_OID_PAIS, 		   			GEN_PAIS.VAL_I18N, 		   			MARC.OID_MARC, 					MARC.DES_MARC, 		   			CANAL.OID_CANA, 		   			GEN_CANAL.VAL_I18N CANAL, 					PERIO.OID_PERI, 					PERIO.VAL_NOMB_PERI PERIODO, 					ACCE.OID_ACCE, 					GEN_ACCESO.VAL_I18N ACCESO, 					SUBAC.OID_SBAC, 					GEN_SUBA.VAL_I18N SUBACCESO, 					REG.OID_REGI, 					REG.DES_REGI REGION, 					ZONA.OID_ZONA, 					ZONA.DES_ZONA ZONA 	FROM SEG_CANAL CANAL, 		 SEG_MARCA MARC, 		 SEG_ACCES ACCE, 		 SEG_SUBAC SUBAC, 		 PED_TIPO_SOLIC TIP_SOL, 		 PED_TIPO_SOLIC_PAIS TIP_SOL_PAIS, 		 CRA_PERIO PERIO, 		 ZON_ZONA ZONA, 		 ZON_REGIO REG, 		 ( 		 SELECT GEN.VAL_OID, GEN.VAL_I18N 		 FROM V_GEN_I18N_SICC GEN 		 WHERE GEN.ATTR_ENTI = 'SEG_SUBAC' 		 	   AND GEN.IDIO_OID_IDIO =  1  		 ) GEN_SUBA, 		 ( 		 SELECT GEN.VAL_OID, GEN.VAL_I18N 		 FROM V_GEN_I18N_SICC GEN 		 WHERE GEN.ATTR_ENTI = 'SEG_ACCES' 		 	   AND GEN.IDIO_OID_IDIO =  1  		 ) GEN_ACCESO, 		 ( 		 SELECT GEN.VAL_OID, GEN.VAL_I18N  		 FROM V_GEN_I18N_SICC GEN 		 WHERE GEN.ATTR_ENTI = 'SEG_CANAL' 		 	   AND GEN.IDIO_OID_IDIO =  1  		 ) GEN_CANAL, 		 ( 		 SELECT GEN.VAL_OID, GEN.VAL_I18N 		 FROM V_GEN_I18N_SICC GEN 		 WHERE GEN.ATTR_ENTI = 'SEG_PAIS' 		 	   AND GEN.IDIO_OID_IDIO =  1  		 ) GEN_PAIS		 	WHERE CANAL.OID_CANA = GEN_CANAL.VAL_OID(+) 		  AND ACCE.OID_ACCE = GEN_ACCESO.VAL_OID(+) 		  AND ACCE.CANA_OID_CANA = CANAL.OID_CANA 		  AND SUBAC.ACCE_OID_ACCE = ACCE.OID_ACCE 		  AND TIP_SOL.SBAC_OID_SBAC = SUBAC.OID_SBAC(+) 		  AND TIP_SOL.ACCE_OID_ACCE = ACCE.OID_ACCE 		  AND TIP_SOL.MARC_OID_MARC = MARC.OID_MARC 		  AND PERIO.CANA_OID_CANA = CANAL.OID_CANA 		  AND PERIO.MARC_OID_MARC = MARC.OID_MARC 		  AND ZONA.CANA_OID_CANA = CANAL.OID_CANA 		  AND ZONA.MARC_OID_MARC = MARC.OID_MARC 		  AND ZONA.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 		  AND REG.OID_REGI = ZONA.ZORG_OID_REGI 		  AND TIP_SOL_PAIS.TSOL_OID_TIPO_SOLI = TIP_SOL.OID_TIPO_SOLI 		  AND TIP_SOL_PAIS.PAIS_OID_PAIS = GEN_PAIS.VAL_OID(+) 		  AND SUBAC.OID_SBAC = GEN_SUBA.VAL_OID(+)  		  AND PERIO.OID_PERI IN (  57  ) 		  AND TIP_SOL_PAIS.PAIS_OID_PAIS =  1  		  AND CANAL.OID_CANA =  1  		  AND MARC.OID_MARC =  1  		   AND ACCE.OID_ACCE IN (  1  )  		   AND SUBAC.OID_SBAC IN (  1  ) 	  		   AND ZONA.OID_ZONA IN (  1  ) 	   		   AND REG.OID_REGI IN (  1  )  		   AND TIP_SOL.OID_TIPO_SOLI IN (  1  )  )  COMB WHERE SOLIC.PAIS_OID_PAIS(+) = COMB.PAIS_OID_PAIS 	  AND SOLIC.OID_MARC(+) = COMB.OID_MARC 	  AND SOLIC.OID_CANA(+) = COMB.OID_CANA	 	  AND SOLIC.OID_PERI(+) = COMB.OID_PERI 	  AND SOLIC.OID_ACCE(+) = COMB.OID_ACCE 	  AND SOLIC.OID_SBAC(+) = COMB.OID_SBAC 	  AND SOLIC.OID_ZONA(+) = COMB.OID_ZONA 	  AND SOLIC.OID_REGI(+) = COMB.OID_REGI	 ORDER BY COMB.VAL_I18N,          COMB.DES_MARC, 		 COMB.CANAL,          COMB.PERIODO, 		 COMB.ACCESO, 		 COMB.SUBACCESO, 		 COMB.REGION, 		 COMB.ZONA,          SOLIC.SECCION, 		 SOLIC.DESC_TIP_SOL, 		 SOLIC.TIP_CLI,          SOLIC.SUBT_CLI, 		 SOLIC.COD_CLIE