SELECT COMB.PAIS_OID_PAIS,  	   COMB.PAIS,   	   COMB.OID_MARC, 	   COMB.DES_MARC,  	   COMB.OID_CANA,    	   COMB.CANAL, 	   COMB.OID_PERI, 	   COMB.PERIODO, 	   COMB.OID_ACCE, 	   COMB.ACCESO, 	   COMB.OID_REGI, 	   COMB.REGION, 	   COMB.OID_ZONA, 	   COMB.ZONA, 	   COMB.OID_TIPO_CLIE, 	   COMB.DES_TIPO_CLIE, 	   COMB.OID_SUBT_CLIE, 	   COMB.DES_SUBT_CLIE, 	   COMB.OID_TIPO_CLAS, 	   COMB.DES_TIPO_CLAS, 	   SOLIC.UBIGEO, 	   COMB.OID_TIPO_DESP, 	   COMB.DES_TIPO_DESP, 	   COMB.OID_CLAS, 	   COMB.DES_CLAS, 	   SOLIC.CANTIDAD, 	   SOLIC.TASA, 	   SOLIC.TOT_FLE_LOC COSTO_FLETE, 	   SOLIC.IMP_FLE_TOT_LOC FLETE_COBRADO FROM 	(  	SELECT  TIP_SOL_PAIS.PAIS_OID_PAIS, 			  		MARC.OID_MARC, 					CANAL.OID_CANA, 			   		PERIO.OID_PERI, 					ACCE.OID_ACCE, 					REG.OID_REGI, 					ZONA.OID_ZONA, 					MAE_TIP_CLI.OID_TIPO_CLIE, 					MAE_SUB_TIP_CLI.OID_SUBT_CLIE, 					MAE_TIP_CLA_CLI.OID_TIPO_CLAS, 					MAE_CLA.OID_CLAS, 					TIP_DES.OID_TIPO_DESP, 					PED_FLE.VEPO_UBIG UBIGEO, 					COUNT(*) CANTIDAD, 					ped_fle.VAL_TASA TASA, 					SUM(SOL_CAB.VAL_TOTA_FLET_LOCA) TOT_FLE_LOC,			 					SUM(SOL_CAB.VAL_IMPO_FLET_TOTA_LOCA) IMP_FLE_TOT_LOC				 		FROM SEG_CANAL CANAL, 			 SEG_MARCA MARC, 			 SEG_ACCES ACCE, 			 PED_TIPO_SOLIC TIP_SOL, 			 PED_TIPO_SOLIC_PAIS TIP_SOL_PAIS, 			 CRA_PERIO PERIO, 			 ZON_ZONA ZONA, 			 ZON_REGIO REG, 			 MAE_TIPO_CLIEN MAE_TIP_CLI, 			 MAE_SUBTI_CLIEN MAE_SUB_TIP_CLI, 			 MAE_TIPO_CLASI_CLIEN MAE_TIP_CLA_CLI, 	 		 PED_TIPO_DESPA TIP_DES, 			 MAE_CLASI MAE_CLA, 			 PED_SOLIC_CABEC SOL_CAB, 			 PED_FLETE PED_FLE  		WHERE ACCE.CANA_OID_CANA = CANAL.OID_CANA 			  AND TIP_SOL.ACCE_OID_ACCE = ACCE.OID_ACCE 			  AND TIP_SOL.MARC_OID_MARC = MARC.OID_MARC 			  AND PERIO.CANA_OID_CANA = CANAL.OID_CANA 			  AND PERIO.MARC_OID_MARC = MARC.OID_MARC 			  AND ZONA.CANA_OID_CANA = CANAL.OID_CANA 			  AND ZONA.MARC_OID_MARC = MARC.OID_MARC 			  AND ZONA.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 			  AND REG.OID_REGI = ZONA.ZORG_OID_REGI 			  AND TIP_SOL_PAIS.TSOL_OID_TIPO_SOLI = TIP_SOL.OID_TIPO_SOLI 			  AND MAE_TIP_CLI.OID_TIPO_CLIE = TIP_SOL.TICL_OID_TIPO_CLIE 			  AND SOL_CAB.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 			  AND SOL_CAB.PERD_OID_PERI = PERIO.OID_PERI 			  AND SOL_CAB.SBTI_OID_SUBT_CLIE = MAE_SUB_TIP_CLI.OID_SUBT_CLIE 			  AND SOL_CAB.TICL_OID_TIPO_CLIE = MAE_TIP_CLI.OID_TIPO_CLIE 			  AND SOL_CAB.TSPA_OID_TIPO_SOLI_PAIS = TIP_SOL_PAIS.OID_TIPO_SOLI_PAIS 			  AND SOL_CAB.ZZON_OID_ZONA = ZONA.OID_ZONA 			  AND SOL_CAB.TIDS_OID_TIPO_DESP = TIP_DES.OID_TIPO_DESP 	  		  AND TIP_DES.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 			  AND MAE_TIP_CLI.OID_TIPO_CLIE = MAE_SUB_TIP_CLI.TICL_OID_TIPO_CLIE 			  AND MAE_TIP_CLA_CLI.SBTI_OID_SUBT_CLIE = MAE_SUB_TIP_CLI.OID_SUBT_CLIE(+) 			  AND TIP_DES.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 			  AND MAE_CLA.TCCL_OID_TIPO_CLAS = MAE_TIP_CLA_CLI.OID_TIPO_CLAS 	  		  AND PED_FLE.CANA_OID_CANA = CANAL.OID_CANA 			  AND PED_FLE.TIDS_OID_TIPO_DESP = TIP_DES.OID_TIPO_DESP 			  AND PED_FLE.MARC_OID_MARC = MARC.OID_MARC 			  AND PED_FLE.SBTI_OID_SUBT_CLIE = MAE_SUB_TIP_CLI.OID_SUBT_CLIE 			  AND PED_FLE.CLAS_OID_CLAS = MAE_CLA.OID_CLAS 		      AND PED_FLE.TCCL_OID_TIPO_CLAS = MAE_TIP_CLA_CLI.OID_TIPO_CLAS 			  AND PED_FLE.ZZON_ZONA = ZONA.OID_ZONA 			  AND PED_FLE.TICL_OID_TIPO_CLIE = MAE_TIP_CLI.OID_TIPO_CLIE	 			  AND SOL_CAB.FEC_FACT IS NOT NULL 			  AND SOL_CAB.IND_TS_NO_CONSO = 1 			  AND SOL_CAB.IND_PEDI_PRUE  < > 1 			  AND TIP_SOL_PAIS.PAIS_OID_PAIS =  1  		  AND CANAL.OID_CANA =  1  			  AND MARC.OID_MARC =  1  			  AND PERIO.OID_PERI IN( 57 ) 			   AND ACCE.OID_ACCE IN ( 1 )  			   AND REG.OID_REGI IN ( 1 )  	 		   AND ZONA.OID_ZONA IN ( 1 ) 	 	 		   AND TIP_DES.OID_TIPO_DESP IN ( 1 )  	 		   AND MAE_TIP_CLI.OID_TIPO_CLIE IN ( 1 )  	 		   AND MAE_SUB_TIP_CLI.OID_SUBT_CLIE IN ( 1 ) 	 	 		   AND MAE_TIP_CLA_CLI.OID_TIPO_CLAS IN ( 1 )  			   AND MAE_CLA.OID_CLAS IN ( 1 )  			   	GROUP BY TIP_SOL_PAIS.PAIS_OID_PAIS, 			 MARC.OID_MARC, 		     CANAL.OID_CANA, 			 PERIO.OID_PERI, 			 ACCE.OID_ACCE, 			 REG.OID_REGI, 			 ZONA.OID_ZONA, 			 MAE_TIP_CLI.OID_TIPO_CLIE, 		     MAE_SUB_TIP_CLI.OID_SUBT_CLIE, 			 MAE_TIP_CLA_CLI.OID_TIPO_CLAS, 			 MAE_CLA.OID_CLAS, 			 TIP_DES.OID_TIPO_DESP,  		     PED_FLE.VEPO_UBIG, 			 ped_fle.val_tasa 	) SOLIC, 	( 	SELECT DISTINCT TIP_SOL_PAIS.PAIS_OID_PAIS, 			  		GEN_PAIS.VAL_I18N PAIS, 			  		MARC.OID_MARC, 					MARC.DES_MARC, 			  	    CANAL.OID_CANA, 			   		GEN_CANAL.VAL_I18N CANAL, 					PERIO.OID_PERI, 					PERIO.VAL_NOMB_PERI PERIODO, 					ACCE.OID_ACCE, 					GEN_ACCESO.VAL_I18N ACCESO, 					REG.OID_REGI, 					REG.DES_REGI REGION, 					ZONA.OID_ZONA, 					ZONA.DES_ZONA ZONA, 					MAE_TIP_CLI.OID_TIPO_CLIE, 					GEN_TIP_CLI.VAL_I18N DES_TIPO_CLIE, 					MAE_SUB_TIP_CLI.OID_SUBT_CLIE, 					GEN_SUB_CLI.VAL_I18N DES_SUBT_CLIE, 					MAE_TIP_CLA_CLI.OID_TIPO_CLAS, 					GEN_TIP_CLA.VAL_I18N DES_TIPO_CLAS, 					MAE_CLA.OID_CLAS, 					GEN_CLASI.VAL_I18N DES_CLAS, 	                TIP_DES.OID_TIPO_DESP, 					GEN_PED_TIP_DES.VAL_I18N DES_TIPO_DESP 		FROM SEG_CANAL CANAL, 			 SEG_MARCA MARC, 			 SEG_ACCES ACCE, 			 PED_TIPO_SOLIC TIP_SOL, 			 PED_TIPO_SOLIC_PAIS TIP_SOL_PAIS, 			 CRA_PERIO PERIO, 			 ZON_ZONA ZONA, 			 ZON_REGIO REG, 			 MAE_TIPO_CLIEN MAE_TIP_CLI, 			 MAE_SUBTI_CLIEN MAE_SUB_TIP_CLI, 			 MAE_TIPO_CLASI_CLIEN MAE_TIP_CLA_CLI, 	 		 PED_TIPO_DESPA TIP_DES, 			 MAE_CLASI MAE_CLA, 			 PED_FLETE PED_FLE, 			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N 			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'PED_TIPO_DESPA' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_PED_TIP_DES, 			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N 			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'MAE_CLASI' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_CLASI, 			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N 			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'MAE_TIPO_CLASI_CLIEN' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_TIP_CLA,		  			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N 			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'MAE_SUBTI_CLIEN' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_SUB_CLI, 			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N 			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'MAE_TIPO_CLIEN' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_TIP_CLI, 			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N 			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'SEG_ACCES' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_ACCESO, 			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N  			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'SEG_CANAL' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_CANAL, 			 ( 			 SELECT GEN.VAL_OID, GEN.VAL_I18N 			 FROM V_GEN_I18N_SICC GEN 			 WHERE GEN.ATTR_ENTI = 'SEG_PAIS' 			 	   AND GEN.IDIO_OID_IDIO =  1  			 ) GEN_PAIS		 		WHERE CANAL.OID_CANA = GEN_CANAL.VAL_OID(+) 			  AND ACCE.OID_ACCE = GEN_ACCESO.VAL_OID(+) 			  AND ACCE.CANA_OID_CANA = CANAL.OID_CANA 			  AND TIP_SOL.ACCE_OID_ACCE = ACCE.OID_ACCE 			  AND TIP_SOL.MARC_OID_MARC = MARC.OID_MARC		   			  AND PERIO.CANA_OID_CANA = CANAL.OID_CANA 			  AND PERIO.MARC_OID_MARC = MARC.OID_MARC 			  AND ZONA.CANA_OID_CANA = CANAL.OID_CANA 			  AND ZONA.MARC_OID_MARC = MARC.OID_MARC 			  AND ZONA.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 			  AND REG.OID_REGI = ZONA.ZORG_OID_REGI 			  AND TIP_SOL_PAIS.TSOL_OID_TIPO_SOLI = TIP_SOL.OID_TIPO_SOLI 			  AND MAE_TIP_CLI.OID_TIPO_CLIE = TIP_SOL.TICL_OID_TIPO_CLIE 			  AND TIP_SOL_PAIS.PAIS_OID_PAIS = GEN_PAIS.VAL_OID(+) 			  AND MAE_TIP_CLI.OID_TIPO_CLIE = GEN_TIP_CLI.VAL_OID(+) 			  AND MAE_SUB_TIP_CLI.OID_SUBT_CLIE = GEN_SUB_CLI.VAL_OID(+) 			  AND MAE_TIP_CLA_CLI.OID_TIPO_CLAS = GEN_TIP_CLA.VAL_OID(+) 			  AND MAE_CLA.OID_CLAS = GEN_CLASI.VAL_OID(+) 			  AND TIP_DES.OID_TIPO_DESP = GEN_PED_TIP_DES.VAL_OID(+)		   			  AND PED_FLE.CANA_OID_CANA = CANAL.OID_CANA 			  AND PED_FLE.TIDS_OID_TIPO_DESP = TIP_DES.OID_TIPO_DESP 			  AND PED_FLE.MARC_OID_MARC = MARC.OID_MARC 			  AND PED_FLE.SBTI_OID_SUBT_CLIE = MAE_SUB_TIP_CLI.OID_SUBT_CLIE 			  AND PED_FLE.CLAS_OID_CLAS = MAE_CLA.OID_CLAS 		      AND PED_FLE.TCCL_OID_TIPO_CLAS = MAE_TIP_CLA_CLI.OID_TIPO_CLAS 			  AND PED_FLE.ZZON_ZONA = ZONA.OID_ZONA 			  AND PED_FLE.TICL_OID_TIPO_CLIE = MAE_TIP_CLI.OID_TIPO_CLIE		   	  		  AND TIP_DES.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 			  AND MAE_TIP_CLI.OID_TIPO_CLIE = MAE_SUB_TIP_CLI.TICL_OID_TIPO_CLIE 			  AND MAE_TIP_CLA_CLI.SBTI_OID_SUBT_CLIE = MAE_SUB_TIP_CLI.OID_SUBT_CLIE(+) 			  AND TIP_DES.PAIS_OID_PAIS = TIP_SOL_PAIS.PAIS_OID_PAIS 			  AND MAE_CLA.TCCL_OID_TIPO_CLAS = MAE_TIP_CLA_CLI.OID_TIPO_CLAS 			  AND TIP_SOL_PAIS.PAIS_OID_PAIS =  1  			  AND CANAL.OID_CANA =  1  			  AND MARC.OID_MARC =  1  			  AND PERIO.OID_PERI IN( 57 ) 			   AND ACCE.OID_ACCE IN ( 1 )  			   AND REG.OID_REGI IN ( 1 )  	 		   AND ZONA.OID_ZONA IN ( 1 ) 	 	 		   AND TIP_DES.OID_TIPO_DESP IN ( 1 )  	 		   AND MAE_TIP_CLI.OID_TIPO_CLIE IN ( 1 )  	 		   AND MAE_SUB_TIP_CLI.OID_SUBT_CLIE IN ( 1 ) 	 	 		   AND MAE_TIP_CLA_CLI.OID_TIPO_CLAS IN ( 1 )  			   AND MAE_CLA.OID_CLAS IN ( 1 ) 			   	) COMB WHERE SOLIC.PAIS_OID_PAIS(+) = COMB.PAIS_OID_PAIS 	  AND SOLIC.OID_MARC(+) = COMB.OID_MARC 	  AND SOLIC.OID_CANA(+) = COMB.OID_CANA 	  AND SOLIC.OID_PERI(+) = COMB.OID_PERI 	  AND SOLIC.OID_ACCE(+) = COMB.OID_ACCE 	  AND SOLIC.OID_REGI(+) = COMB.OID_REGI 	  AND SOLIC.OID_ZONA(+) = COMB.OID_ZONA 	  AND SOLIC.OID_TIPO_CLIE(+) = COMB.OID_TIPO_CLIE 	  AND SOLIC.OID_SUBT_CLIE(+) = COMB.OID_SUBT_CLIE 	  AND SOLIC.OID_TIPO_CLAS(+) = COMB.OID_TIPO_CLAS 	  AND SOLIC.OID_CLAS(+) = COMB.OID_CLAS 	  AND SOLIC.OID_TIPO_DESP(+) = COMB.OID_TIPO_DESP 	