 select val_i18n, usuarios.COD_USUA AS idusuario, usuarios.VAL_APEL_PATE AS APELLIDOPATERNO, usuarios.VAL_APE_MATE AS APELLIDOMATERNO,usuarios.VAL_NOM1 AS nombre1,usuarios.VAL_NOM2 AS nombre2, 1 as Cantidad  	   from ( 	   select distinct VAL_NOMB_PROG, i18.VAL_I18N, us.IDMEMBER 	   from  	   MEN_FUNCI fu  	   LEFT OUTER JOIN PRINCIPALS pr ON TO_CHAR(fu.OID_FUNC) = pr.NAME  	   INNER JOIN MEMBEROF per on per.IDROLE = pr.IDPRINCIPAL 	   INNER JOIN MEMBEROF us on per.IDMEMBER = us.IDROLE 	   INNER JOIN V_GEN_I18N_SICC i18 on fu.OID_FUNC = i18.VAL_OID  	   where IND_NIVE = 1 	   and IDIO_OID_IDIO =  1  	   and i18.attr_enti='MEN_FUNCI' 	   and us.IDMEMBER   < > 4 	   ),V_REP_USUAR_LARGO usuarios where usuarios.COD_USUA = idmember  group by VAL_I18N,usuarios.COD_USUA,usuarios.VAL_APEL_PATE, usuarios.VAL_APE_MATE,usuarios.VAL_NOM1,usuarios.VAL_NOM2 order by VAL_I18N, usuarios.VAL_APEL_PATE, usuarios.VAL_APE_MATE,usuarios.VAL_NOM1,usuarios.VAL_NOM2