SELECT   usuarios.val_nomb_usua AS nombreusuario,  			         usuarios.val_apel_pate AS apellidopaterno,  			         usuarios.val_ape_mate AS apellidomaterno,  			         usuarios.val_nom1 AS nombre1,  					 usuarios.val_nom2 AS nombre2,  			         1 AS cantidad          FROM V_REP_USUAR_LARGO usuarios             ,               (		 		   		  select distinct idprincipal 		  from propertyvalues 		  where idproperty = 9 		  		and stringvalue in ( 1 ) 		 ) prop9                       ,                                                 (		 		   		  select distinct idprincipal 		  from propertyvalues 		  where idproperty = 24 		  		and stringvalue in ( 1 ) 		 ) prop24  	                ,                                           (		 		   		  select distinct idprincipal 		  from propertyvalues 		  where idproperty = 25 		  		and stringvalue in ( 1 ) 		 ) prop25 		             ,              (                                       select distinct idprincipal 		  from propertyvalues 		  where idproperty = 30 		  		and stringvalue in ( 1 ) 		 ) prop30                     ,                 		 (		 		   		  select distinct idprincipal 		  from propertyvalues 		  where idproperty = 38 		  		and stringvalue in ( 1 ) 		 ) prop38                       ,                (		 		   		  select distinct idprincipal 		  from propertyvalues 		  where idproperty = 39 		  		and stringvalue in ( 1 ) 		 ) prop39                      ,                  (		 		   		  select distinct idprincipal 		  from propertyvalues 		  where idproperty = 40 		  		and stringvalue in ( 1 ) 		 ) prop40           ,           users us    WHERE     usuarios.cod_usua = us.iduser                  and prop24.idprincipal = us.iduser   		   and prop25.idprincipal = us.iduser   	           and prop9.idprincipal = us.iduser   		  and prop30.idprincipal = us.iduser   		   and prop38.idprincipal = us.iduser   		   and prop39.idprincipal = us.iduser  		   and prop40.idprincipal = us.iduser  		   and USUARIOS.VAL_APEL_PATE =  '1'   		   and USUARIOS.VAL_APE_MATE =  '1'   		   and USUARIOS.VAL_NOM1 =  '1'   		   and USUARIOS.VAL_NOM2 =  '1'   order by nombreusuario,           apellidopaterno,           apellidomaterno,           nombre1,  nombre2