SELECT PC.COD_PERI||' - '|| 	PERI.VAL_NOMB_PERI VAL_NOMB_PERI, I18PAIS.VAL_I18N AS PAIS, MARCA.DES_MARC, I18CANAL.VAL_I18N AS CANAL,  	CIVI.COD_CICL_VIDA,	TOFE.COD_TIPO_OFER,  	PRODU.COD_SAP,   	OFDET.VAL_CODI_VENT, 	I18PRODU.VAL_I18N AS DESC_PROD, 	I18CLIEN.VAL_I18N AS DESC_TIPO_CLIEN, 	BPS.VAL_CRIT, BPS.VAL_BASE_DEST, BPS.VAL_FACT_CORR, BPS.VAL_PROM, BPS.VAL_OBSE, BPS.NUM_UNID, BPS.VAL_PREC_STND				    FROM 	CRA_PERIO PERI, 	MAV_BPS_INTER BPS, 	MAV_DETAL_MAV DETAL_MAV, 	MAV_ENVIO ENVIO, 	PRE_MATRI_FACTU MF, 	PRE_OFERT_DETAL OFDET, 	PRE_CICLO_VIDA CIVI, 	PRE_TIPO_OFERT TOFE, 	MAE_PRODU PRODU, 	SEG_MARCA MARCA, 	SEG_PERIO_CORPO PC, 	( 	 SELECT V.VAL_OID, V.VAL_I18N  	  FROM V_GEN_I18N_SICC V  	   WHERE V.ATTR_ENTI = 'SEG_PAIS'  	    AND V.IDIO_OID_IDIO =  1  	) I18PAIS, 	( 	 SELECT V.VAL_OID, V.VAL_I18N  	  FROM V_GEN_I18N_SICC V  	   WHERE V.ATTR_ENTI = 'SEG_CANAL'  	    AND V.IDIO_OID_IDIO =  1  	) I18CANAL, 	( 	 SELECT V.VAL_OID, V.VAL_I18N  	  FROM V_GEN_I18N_SICC V  	   WHERE V.ATTR_ENTI = 'MAE_PRODU'  	    AND V.IDIO_OID_IDIO =  1  	) I18PRODU, 	( 	 SELECT V.VAL_OID, V.VAL_I18N  	  FROM V_GEN_I18N_SICC V  	   WHERE V.ATTR_ENTI = 'MAE_TIPO_CLIEN'  	    AND V.IDIO_OID_IDIO =  1  	) I18CLIEN WHERE 		MARCA.OID_MARC = PERI.MARC_OID_MARC 	AND BPS.PERD_OID_PERI = PERI.OID_PERI 	AND	BPS.CIVI_OID_CICL_VIDA = CIVI.OID_CICL_VIDA 	AND BPS.TOFE_OID_TIPO_OFER = TOFE.OID_TIPO_OFER 	AND BPS.PROD_OID_PROD = PRODU.OID_PROD 	AND DETAL_MAV.PERD_OID_PERI = BPS.PERD_OID_PERI 	AND DETAL_MAV.TICL_OID_TIPO_CLIE = BPS.TICL_OID_TIPO_CLIE 	AND DETAL_MAV.CIVI_OID_CICL_VIDA = BPS.CIVI_OID_CICL_VIDA 	AND DETAL_MAV.PROD_OID_PROD = BPS.PROD_OID_PROD 	AND DETAL_MAV.VAL_PREC_STND = BPS.VAL_PREC_STND 	AND DETAL_MAV.OID_DETA_MAV = ENVIO.DENV_OID_DETA_MAV 	AND ENVIO.MAFA_OID_MATR_FACT = MF.OID_MATR_FACT 	AND MF.OFDE_OID_DETA_OFER = OFDET.OID_DETA_OFER 	AND OFDET.PROD_OID_PROD = BPS.PROD_OID_PROD        AND PERI.PERI_OID_PERI = PC.OID_PERI 	AND I18PAIS.VAL_OID (+)= PERI.PAIS_OID_PAIS 	AND I18CANAL.VAL_OID (+)= PERI.CANA_OID_CANA 	AND I18PRODU.VAL_OID (+)= BPS.PROD_OID_PROD 	AND I18CLIEN.VAL_OID (+)= BPS.TICL_OID_TIPO_CLIE  	AND PERI.PAIS_OID_PAIS =  1  	AND PERI.MARC_OID_MARC =  1  	AND PERI.CANA_OID_CANA =  1  	AND PERI.OID_PERI =  58  	 AND BPS.CIVI_OID_CICL_VIDA IN (  1  ) 		 	 AND BPS.TOFE_OID_TIPO_OFER IN (  1  )  	 AND PRODU.COD_SAP >=  '1'   	 AND PRODU.COD_SAP  < =  '1'   	 AND BPS.TICL_OID_TIPO_CLIE IN (  1  )  	 AND BPS.NUM_UNID  >   1    ORDER BY 	VAL_NOMB_PERI,  	I18PAIS.VAL_I18N,  	MARCA.DES_MARC,  	I18CANAL.VAL_I18N, 	CIVI.COD_CICL_VIDA,    	TOFE.COD_TIPO_OFER,  	PRODU.COD_SAP, 	OFDET.VAL_CODI_VENT