 SELECT 				GEN_MAT. VAL_I18N TIPO_DESCU,   				PQ_APL_REP.FN_Dto_036_Concat_Marcas (MAT.OID_TIPO_DESC) AS COD_MARC, 				PQ_APL_REP.FN_Dto_036_CONCAT_CANALES (MAT.OID_TIPO_DESC,  1 ) AS COD_CANA,  				PQ_APL_REP.FN_Dto_036_CONCAT_ACCESOS (MAT.OID_TIPO_DESC,  1 ) AS COD_ACCE,  				PQ_APL_REP.FN_Dto_036_CONCAT_SUBACCESOS (MAT.OID_TIPO_DESC,  1 ) AS COD_SBAC, 				PQ_APL_REP.FN_Dto_036_CONCAT_TIPO (MAT. OID_TIPO_DESC,  1 ) AS COD_TIPO_CLIE, 				PQ_APL_REP.FN_Dto_036_CONCAT_SUBTIPO (MAT.OID_TIPO_DESC,  1 ) AS COD_SUBT_CLIE, 				PQ_APL_REP.FN_Dto_036_CONCAT_NACIONAL (MAT.OID_TIPO_DESC) AS ALCAN_ADMI, 				DECODE(param_c_marcas.IND_MODI,1,'1','0') AS b_c_marca, 	   			DECODE(param_c_un_neg.IND_MODI,1,'1','0') AS b_c_un_negocio, 	   			DECODE(param_c_neg.IND_MODI,1,'1','0') AS b_c_negocio,  	   			DECODE(param_c_sup_gen.IND_MODI,1,'1','0') AS b_c_1up_generico,  	   			DECODE(param_c_gen.IND_MODI,1,'1','0') AS b_c_generico,  	   			DECODE(param_c_prod.IND_MODI,1,'1','0') AS b_c_productos,   	   			DECODE(param_a_marcas.IND_MODI,1,'1','0') AS b_a_marcas,  	   			DECODE(param_a_un_neg.IND_MODI,1,'1','0') AS b_a_un_negocio, 	   			DECODE(param_a_neg.IND_MODI,1,'1','0') AS b_a_negocio,  	   			DECODE(param_a_sup_gen.IND_MODI,1,'1','0') AS b_a_sup_generico,  	   			DECODE(param_a_gen.IND_MODI,1,'1','0') AS b_a_generico,  	   			DECODE(param_a_prod.IND_MODI,1,'1','0') AS b_a_productos, 	   			DECODE(PARAM_IND_CULM .VAL_PARA,1,'1','0') AS IND_CULM,  	   			DECODE(PARAM_IND_MORO.VAL_PARA,1,'1','0') AS IND_MORO,  	   			DECODE(PARAM_IND_DEVO .VAL_PARA,1,'1','0') AS IND_CTRL_DEVO,  				NULL AS FEC_INIC,  				NULL AS FEC_FINA,  	   			DECODE(PARAM_IND_DEVO .VAL_PARA,1,'1','0') AS IND_CTRL_ANUL,  	   			NULL AS COD_CLIE,  	   			DECODE(PARAM_IND_D_ACUM .VAL_PARA,1,'1','0') AS IND_DESC_ACUMU,  	   			DECODE(PARAM_IND_AF_FAC.VAL_PARA,1,'1','0') AS IND_AFEC_FACT,  	   			DECODE(PARAM_IND_AF_CC .VAL_PARA,1,'1','0') AS IND_AFEC_CC,  	   			DECODE(PARAM_IND_COMU .VAL_PARA,1,'1','0') AS IND_COMU_PART,  	   			DECODE(PARAM_IND_DESC.IND_MODI,1,'1','0') AS DESC_POR_CLIENTE, 				PQ_APL_REP.FN_Dto_036_Concat_TIPO_CLASI (MAT.OID_TIPO_DESC,  1 ) AS TITO_CLASI, 				PQ_APL_REP.FN_Dto_036_Concat_CLASI (MAT.OID_TIPO_DESC,  1 ) AS CLASI FROM			 				DTO_MATRI_DESCU MAT, 	 			( 					 SELECT GEN.VAL_OID, GEN.VAL_I18N  					 FROM V_GEN_I18N_SICC GEN 					 WHERE GEN.ATTR_ENTI= 'DTO_MATRI_DESCU' 					 AND GEN.IDIO_OID_IDIO= 1  	 	 		 )GEN_MAT, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 14 				 ) param_c_marcas, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 15 				 ) param_c_un_neg, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 16 				 ) param_c_neg, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 17 				 ) param_c_sup_gen, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 18 				 ) param_c_gen, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 19  				 ) param_c_prod, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 20  				 ) param_a_marcas, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 21  				 ) param_a_un_neg, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 22  				 ) param_a_neg, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 23  				 ) param_a_sup_gen, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 24  				 ) param_a_gen, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 25  				 ) param_a_prod, 				 ( 					SELECT 	PTD.MDES_OID_TIPO_DESC, VPB.VAL_PARA 					FROM  									DTO_PARAM_TIPO_DESCU PTD, 									DTO_VALOR_PARAM_BOOLE VPB 					WHERE  									PTD.PADT_OID_PARA = 2 									AND VPB.PTDT_PARA_TIPO_DESC = PTD.OID_PARA_TIPO_DESC 									AND VPB.IND_VALO_DEFE = 1 				 ) PARAM_IND_CULM, 				 ( 					SELECT 	PTD.MDES_OID_TIPO_DESC, VPB.VAL_PARA 					FROM  									DTO_PARAM_TIPO_DESCU PTD, 									DTO_VALOR_PARAM_BOOLE VPB 					WHERE  									PTD.PADT_OID_PARA = 26   									AND VPB.PTDT_PARA_TIPO_DESC = PTD.OID_PARA_TIPO_DESC 									AND VPB.IND_VALO_DEFE = 1 				 )  PARAM_IND_MORO, 				 ( 					SELECT 	PTD.MDES_OID_TIPO_DESC, VPB.VAL_PARA 					FROM  									DTO_PARAM_TIPO_DESCU PTD, 									DTO_VALOR_PARAM_BOOLE VPB 					WHERE  									PTD.PADT_OID_PARA = 27   									AND VPB.PTDT_PARA_TIPO_DESC = PTD.OID_PARA_TIPO_DESC 									AND VPB.IND_VALO_DEFE = 1 				 )  PARAM_IND_DEVO, 				 ( 					SELECT 	PTD.MDES_OID_TIPO_DESC, VPB.VAL_PARA 					FROM  									DTO_PARAM_TIPO_DESCU PTD, 									DTO_VALOR_PARAM_BOOLE VPB 					WHERE  									PTD.PADT_OID_PARA =  28 									AND VPB.PTDT_PARA_TIPO_DESC = PTD.OID_PARA_TIPO_DESC 									AND VPB.IND_VALO_DEFE = 1 				 )  PARAM_IND_D_ACUM, 				 ( 					SELECT 	PTD.MDES_OID_TIPO_DESC, VPB.VAL_PARA 					FROM  									DTO_PARAM_TIPO_DESCU PTD, 									DTO_VALOR_PARAM_BOOLE VPB 					WHERE  									PTD.PADT_OID_PARA = 29   									AND VPB.PTDT_PARA_TIPO_DESC = PTD.OID_PARA_TIPO_DESC 									AND VPB.IND_VALO_DEFE = 1 				 )  PARAM_IND_AF_FAC, 				 ( 					SELECT 	PTD.MDES_OID_TIPO_DESC, VPB.VAL_PARA 					FROM  									DTO_PARAM_TIPO_DESCU PTD, 									DTO_VALOR_PARAM_BOOLE VPB 					WHERE  									PTD.PADT_OID_PARA =30   									AND VPB.PTDT_PARA_TIPO_DESC = PTD.OID_PARA_TIPO_DESC 									AND VPB.IND_VALO_DEFE = 1 				 )  PARAM_IND_AF_CC, 				 ( 					SELECT 	PTD.MDES_OID_TIPO_DESC, VPB.VAL_PARA 					FROM  									DTO_PARAM_TIPO_DESCU PTD, 									DTO_VALOR_PARAM_BOOLE VPB 					WHERE  									PTD.PADT_OID_PARA = 31   									AND VPB.PTDT_PARA_TIPO_DESC = PTD.OID_PARA_TIPO_DESC 									AND VPB.IND_VALO_DEFE = 1 				 )  PARAM_IND_COMU, 				 ( 					SELECT 	P.MDES_OID_TIPO_DESC, P.IND_MODI 					FROM  						  	 DTO_PARAM_TIPO_DESCU p 					WHERE 						  	 	p.PADT_OID_PARA = 11  				 )  PARAM_IND_DESC WHERE   			MAT.OID_TIPO_DESC= GEN_MAT.VAL_OID(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_C_MARCAS.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_C_UN_NEG.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_C_NEG.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_C_SUP_GEN.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_C_GEN.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_C_PROD.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_A_MARCAS.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_A_UN_NEG.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_A_NEG.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_A_SUP_GEN.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_A_GEN.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_A_PROD.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_CULM.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_MORO.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_DEVO.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_D_ACUM.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_AF_FAC.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_AF_CC.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_COMU.MDES_OID_TIPO_DESC(+) 	  			AND MAT.OID_TIPO_DESC = PARAM_IND_DESC.MDES_OID_TIPO_DESC(+) 				AND MAT.PAIS_OID_PAIS =  1  ORDER BY 				TIPO_DESCU    