 SELECT   	codigo, nombre, docu.documento ,tel, direccion, fecha_ingreso,  	PQ_APL_REP.FN_CU_CALC_SALDO_CTA_CTE	(datos.idcliente) AS saldo, 	dat_adic.IND_ACTI AS Activa FROM  	( 	SELECT   		cli.OID_CLIE AS idcliente,cli.COD_CLIE codigo, cli.VAL_APE1 ||' '||cli.VAL_APE2||' '||cli.VAL_NOM1||' '||cli.VAL_NOM2 AS nombre, 		tipo_via.t_v||' '|| dir.VAL_NOMB_VIA ||' '|| dir.NUM_PPAL || DECODE (VAL_OBSE,'','',' - ' || VAL_OBSE) AS DIRECCION, 		cli.FEC_INGR AS fecha_ingreso  	FROM MAE_CLIEN_IDENT id,   		MAE_CLIEN_DIREC dir,  		MAE_CLIEN cli, 		( 		 SELECT via.OID_TIPO_VIA, gen.VAL_I18N AS t_v  		 FROM seg_tipo_via via,  		 	  v_gen_i18n_sicc gen  		 WHERE gen.ATTR_ENTI='SEG_TIPO_VIA'  			   AND gen.VAL_OID=via.oid_tipo_via 			   AND gen.IDIO_OID_IDIO =  1  		) tipo_via 	WHERE cli.OID_CLIE=id.CLIE_OID_CLIE(+) 		AND id.VAL_IDEN_DOCU_PRIN =1  		AND cli.OID_CLIE=dir.CLIE_OID_CLIE(+) 		AND dir.TIVI_OID_TIPO_VIA= tipo_via.oid_tipo_via(+) 		AND dir.TIDC_OID_TIPO_DIRE(+)=1 		 AND cli.PAIS_OID_PAIS IN ( 1 )   		 AND cli.VAL_APE1  LIKE  '1'   		 AND cli.VAL_APE2  LIKE  '1'   		 AND cli.VAL_NOM1  LIKE  '1'   		 AND cli.VAL_NOM2  LIKE  '1'   		 AND cli.COD_CLIE =  '1'   	) datos, 	( 	 SELECT  com.CLIE_OID_CLIE AS oidcli, com.VAL_TEXT_COMU AS tel   	 FROM MAE_CLIEN_COMUN com, mae_tipo_comun tcom 	 WHERE com.TICM_OID_TIPO_COMU=tcom.OID_TIPO_COMU 	 AND tcom.COD_TIPO_COMU='TF' 	) tel, 	( 	 SELECT id.CLIE_OID_CLIE AS IDCLI, id.NUM_DOCU_IDEN AS DOCUMENTO FROM MAE_CLIEN_IDENT id WHERE id.VAL_IDEN_DOCU_PRIN=1 	)docu, 		MAE_CLIEN_DATOS_ADICI dat_adic WHERE  	tel.oidcli(+)=datos.idcliente 	AND dat_adic.clie_oid_clie(+) = datos.idcliente  	AND docu.idcli(+)=datos.idcliente ORDER BY codigo, nombre 