SELECT  	RECU.PAIS, RECU.DES_MARC, RECU.CANAL, 	CASE WHEN (ORIG.OID_PERI = RECU.OID_PERI) THEN '01' 		 WHEN (ORIG.OID_PERI  < > RECU.OID_PERI AND IND_RECU_AUTO = 1 ) THEN '02' 		 WHEN (ORIG.OID_PERI  < > RECU.OID_PERI AND IND_RECU_AUTO = 0 ) THEN '03' 	END AS TIPORECUPERACION,  	RECU.VAL_CODI_VENT, 	RECU.COD_SAP,  	RECU.DESC_PROD, 	RECU.ESTRAT, 	RECU.VAL_FACT_REPE, 	RECU.VAL_NOMB_PERI, 	ORIG.VAL_CODI_VENT AS VAL_CODI_VENT_O, 	ORIG.COD_SAP AS COD_SAP_O, 	ORIG.DESC_PROD AS DESC_PROD_O, 	ORIG.ESTRAT AS ESTRAT_O, 	ORIG.VAL_FACT_REPE AS VAL_FACT_REPE_O, 	ORIG.VAL_NOMB_PERI AS VAL_NOMB_PERI_O, 	RECU.IMP_PREC_CATA, 	ORIG.IMP_PREC_CATA AS IMP_PREC_CATA_O FROM 	( 	SELECT  		  I18PAIS.VAL_I18N PAIS, MARCA.DES_MARC, I18CANAL.VAL_I18N CANAL, 		  OFEDET.VAL_CODI_VENT, PROD.COD_SAP, I18PROD.VAL_I18N AS DESC_PROD,  		  OFEDET.IMP_PREC_CATA, ES.COD_ESTR ESTRAT, OFEDET.VAL_FACT_REPE, PERI.VAL_NOMB_PERI,  		  MF.OID_MATR_FACT, PERI.OID_PERI, PROD.OID_PROD   	FROM 		  CRA_PERIO PERI, 		  PRE_OFERT OFE, 		  PRE_OFERT_DETAL OFEDET, 		  PRE_MATRI_FACTU MF, 		  PRE_MATRI_FACTU_CABEC MFC, 		  MAE_PRODU PROD, 		  PRE_ESTRA ES, 		  SEG_MARCA MARCA, 		  ( 		  SELECT V.VAL_OID, V.VAL_I18N  		  FROM V_GEN_I18N_SICC V  		  WHERE V.ATTR_ENTI = 'MAE_PRODU' 		  AND V.IDIO_OID_IDIO =  1  		  ) I18PROD, 		  ( 		  SELECT V.VAL_OID, V.VAL_I18N  		  FROM V_GEN_I18N_SICC V  		  WHERE V.ATTR_ENTI = 'SEG_PAIS'  		  AND V.IDIO_OID_IDIO =  1  		  ) I18PAIS, 		  ( 		  SELECT V.VAL_OID, V.VAL_I18N  		  FROM V_GEN_I18N_SICC V  		  WHERE V.ATTR_ENTI = 'SEG_CANAL'  		  AND V.IDIO_OID_IDIO =  1  		  ) I18CANAL 	WHERE 		      PERI.OID_PERI = MFC.PERD_OID_PERI 		  AND OFE.MFCA_OID_CABE = MFC.OID_CABE 		  AND MF.MFCA_OID_CABE = MFC.OID_CABE 		  AND OFE.OID_OFER = OFEDET.OFER_OID_OFER  	 		  AND OFEDET.OID_DETA_OFER = MF.OFDE_OID_DETA_OFER 		  AND OFEDET.PROD_OID_PROD = PROD.OID_PROD 		  AND OFE.COES_OID_ESTR = ES.OID_ESTR 	      AND I18PAIS.VAL_OID(+) = PERI.PAIS_OID_PAIS  	      AND I18CANAL.VAL_OID(+) = PERI.CANA_OID_CANA  	      AND I18PROD.VAL_OID(+) = OFEDET.PROD_OID_PROD  		  AND PERI.MARC_OID_MARC = MARCA.OID_MARC  	  	  AND PERI.OID_PERI =  57  	) RECU, 	( 	SELECT  		  I18PAIS.VAL_I18N PAIS, MARCA.DES_MARC, I18CANAL.VAL_I18N CANAL, 		  OFEDET.VAL_CODI_VENT, PROD.COD_SAP, I18PROD.VAL_I18N AS DESC_PROD,  		  OFEDET.IMP_PREC_CATA, ES.COD_ESTR ESTRAT, OFEDET.VAL_FACT_REPE, PERI.VAL_NOMB_PERI, 		  MF.OID_MATR_FACT, PERI.OID_PERI, PROD.OID_PROD  	FROM 		  CRA_PERIO PERI, 		  PRE_OFERT OFE, 		  PRE_OFERT_DETAL OFEDET, 		  PRE_MATRI_FACTU MF, 		  PRE_MATRI_FACTU_CABEC MFC, 		  MAE_PRODU PROD, 		  PRE_ESTRA ES, 		  SEG_MARCA MARCA, 		  ( 		  SELECT V.VAL_OID, V.VAL_I18N  		  FROM V_GEN_I18N_SICC V  		  WHERE V.ATTR_ENTI = 'MAE_PRODU' 		  AND V.IDIO_OID_IDIO =  1  		  ) I18PROD, 		  ( 		  SELECT V.VAL_OID, V.VAL_I18N  		  FROM V_GEN_I18N_SICC V  		  WHERE V.ATTR_ENTI = 'SEG_PAIS'  		  AND V.IDIO_OID_IDIO =  1  		  ) I18PAIS, 		  ( 		  SELECT V.VAL_OID, V.VAL_I18N  		  FROM V_GEN_I18N_SICC V  		  WHERE V.ATTR_ENTI = 'SEG_CANAL'  		  AND V.IDIO_OID_IDIO =  1  		  ) I18CANAL 	WHERE 		      PERI.OID_PERI = MFC.PERD_OID_PERI 		  AND OFE.MFCA_OID_CABE = MFC.OID_CABE 		  AND MF.MFCA_OID_CABE = MFC.OID_CABE 		  AND OFE.OID_OFER = OFEDET.OFER_OID_OFER  	 		  AND OFEDET.OID_DETA_OFER = MF.OFDE_OID_DETA_OFER 		  AND OFEDET.PROD_OID_PROD = PROD.OID_PROD 		  AND OFE.COES_OID_ESTR = ES.OID_ESTR 	      AND I18PAIS.VAL_OID(+) = PERI.PAIS_OID_PAIS  	      AND I18CANAL.VAL_OID(+) = PERI.CANA_OID_CANA  	      AND I18PROD.VAL_OID(+) = OFEDET.PROD_OID_PROD  		  AND PERI.MARC_OID_MARC = MARCA.OID_MARC(+) 		  AND PERI.PAIS_OID_PAIS =  1  		  AND PERI.MARC_OID_MARC =  1  		  AND PERI.CANA_OID_CANA =  1  	) ORIG, 	PRE_MATRI_RECUP MRECU WHERE 	 	ORIG.PAIS = RECU.PAIS 	AND ORIG.DES_MARC = RECU.DES_MARC 	AND ORIG.CANAL = RECU.CANAL 	AND MRECU.MAFA_OID_COD_PPAL = RECU.OID_MATR_FACT   	AND MRECU.MAFA_OID_COD_RECU = ORIG.OID_MATR_FACT    	AND RECU.VAL_CODI_VENT >=  '1'   	AND RECU.VAL_CODI_VENT  < =  '999999'          AND ( ORIG.OID_PERI  < > RECU.OID_PERI AND IND_RECU_AUTO = 0 )         ORDER BY 	RECU.PAIS, RECU.DES_MARC, RECU.CANAL, 	TIPORECUPERACION, 	VAL_CODI_VENT  