 		  SELECT PAIS, 	   SUBG, 	   REGION, 	   ZONA, 	   N_CONCU, 	   TERRI, 	    COD_CLIE, 	   NOMBRE, 	   PERI, 	   TC_TERRI, 	   TC_ZONA, 	   TC_REGION, 	   CASE WHEN (CONTROL = 1) THEN 	   		PE 	   END PE, 	   NUM_PUNTOS 	   FROM ( 	SELECT PAIS, 		   SUBG, 		   REGION, 		   ZONA, 		   N_CONCU, 		   TERRI, 		    COD_CLIE, 		   NOMBRE, 		   PE, 		   PERI, 		   NUM_PUNTOS, 		   TC_TERRI, 		   TC_ZONA, 		   TC_REGION, 		   SUM(1) OVER (PARTITION BY PAIS, SUBG, REGION, ZONA, N_CONCU, TERRI, NOMBRE ORDER BY ROWNUM) CONTROL 	FROM 	( 		SELECT GEN_PAIS.VAL_I18N PAIS, 			   SUBG.DES_SUBG_VENT SUBG, 			   REG.DES_REGI REGION, 			   ZONA.DES_ZONA ZONA, 			   CON_GEN.NUM_CONC N_CONCU, 			   TER.COD_TERR TERRI, 			      CLI.COD_CLIE, 			   CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2 NOMBRE, 			   METAS.VAL_META/PUNTOS.VAL_FACT_CONV*PUNTOS.NUM_PUNT_ASIG PE, 			   PERI.VAL_NOMB_PERI PERI, 			   SUM(SOL_CON.NUM_PUNT) NUM_PUNTOS, 			   COUNT(DISTINCT METAS.CLIE_OID_CLIE) OVER (PARTITION BY GEN_PAIS.VAL_I18N, SUBG.DES_SUBG_VENT, REG.DES_REGI, ZONA.DES_ZONA, CON_GEN.NUM_CONC, TER.COD_TERR) TC_TERRI, 	   		   COUNT(DISTINCT METAS.CLIE_OID_CLIE) OVER (PARTITION BY GEN_PAIS.VAL_I18N, SUBG.DES_SUBG_VENT, REG.DES_REGI, ZONA.DES_ZONA) TC_ZONA, 	   		   COUNT(DISTINCT METAS.CLIE_OID_CLIE) OVER (PARTITION BY GEN_PAIS.VAL_I18N, SUBG.DES_SUBG_VENT, REG.DES_REGI) TC_REGION 		FROM INC_CONCU_PARAM_GENER CON_GEN, 			 INC_OBTEN_PUNTO PUNTOS, 			 INC_METAS_TIPO_VENTA METAS, 			 MAE_CLIEN_UNIDA_ADMIN CLI_UA, 			 ZON_SUB_GEREN_VENTA SUBG, 			 ZON_REGIO REG, 			 ZON_ZONA ZONA, 			 INC_SOLIC_CONCU_PUNTA SOL_CON, 			 ZON_SECCI SEC, 			 ZON_TERRI_ADMIN TER_ADM, 			 ZON_TERRI TER, 			 MAE_CLIEN CLI, 			 ( 			 SELECT P.OID_PERI, P.FEC_INIC 			 FROM CRA_PERIO P 			 WHERE P.PAIS_OID_PAIS IN ( 1,2,3 )  			 	          AND P.MARC_OID_MARC IN ( 1 )    			 ) PER_INI, 			 ( 			 SELECT P.OID_PERI, P.FEC_FINA 			 FROM CRA_PERIO P 			 WHERE P.PAIS_OID_PAIS IN ( 1,2,3 )  			 	        AND P.MARC_OID_MARC IN ( 1 )   			 ) PER_FIN, 			 CRA_PERIO PERI, 			 ( 				 SELECT GEN.VAL_OID, GEN.VAL_I18N 				 FROM V_GEN_I18N_SICC GEN 				 WHERE GEN.ATTR_ENTI = 'SEG_PAIS' 				 	   AND GEN.IDIO_OID_IDIO =  1  					   			 ) GEN_PAIS 		WHERE PUNTOS.COPA_OID_PARA_GRAL = CON_GEN.OID_PARA_GRAL 			  AND SUBG.OID_SUBG_VENT = REG.ZSGV_OID_SUBG_VENT 			  AND REG.OID_REGI = ZONA.ZORG_OID_REGI 			  AND ZONA.OID_ZONA = SEC.ZZON_OID_ZONA 			  AND SEC.OID_SECC = TER_ADM.ZSCC_OID_SECC 			  AND TER.OID_TERR = TER_ADM.TERR_OID_TERR 			  AND METAS.COPA_OID_PARA_GRAL = CON_GEN.OID_PARA_GRAL 			  AND CLI_UA.ZTAD_OID_TERR_ADMI = TER_ADM.OID_TERR_ADMI 			  AND CLI_UA.CLIE_OID_CLIE = METAS.CLIE_OID_CLIE 			  AND METAS.CLIE_OID_CLIE = CLI.OID_CLIE 			  AND SOL_CON.COPA_OID_PARA_GRAL = CON_GEN.OID_PARA_GRAL 	   		  AND SOL_CON.CLIE_OID_CLIE = METAS.CLIE_OID_CLIE 			  AND PERI.OID_PERI = SOL_CON.PERD_OID_PERI 			   			   		 AND CON_GEN.MARC_OID_MARC = SUBG.MARC_OID_MARC 	 		 			  AND CLI_UA.IND_ACTI = 1 			  AND CON_GEN.IND_ACTI = 1 			  AND PER_INI.OID_PERI = CON_GEN.PERD_OID_PERI_DESD 			  AND PER_FIN.OID_PERI = CON_GEN.PERD_OID_PERI_HAST 			  AND PERI.FEC_INIC >= PER_INI.FEC_INIC 			  AND PERI.FEC_FINA  <  = PER_FIN.FEC_FINA 			  AND PERI.PAIS_OID_PAIS = CON_GEN.PAIS_OID_PAIS 			 			AND PERI.MARC_OID_MARC = CON_GEN.MARC_OID_MARC  			  AND CON_GEN.PAIS_OID_PAIS = GEN_PAIS.VAL_OID(+) 			   			   			   			   			AND CON_GEN.NUM_CONC IN  ( '1','2','05/002' )  			AND CON_GEN.PAIS_OID_PAIS IN ( 1,2,3 )  			       AND CON_GEN.MARC_OID_MARC IN ( 1 )    			   AND SUBG.OID_SUBG_VENT IN  ( 1 )   			   and reg.oid_regi in  ( 1 )    			      and zona.OID_ZONA in ( 1 )    			     			   	GROUP BY GEN_PAIS.VAL_I18N, 		     SUBG.DES_SUBG_VENT, 			 REG.DES_REGI, 			 ZONA.DES_ZONA, 		  	 CON_GEN.NUM_CONC, 			 TER.COD_TERR, 			  			 METAS.CLIE_OID_CLIE, 			  CLI.COD_CLIE, 	         CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2, 			 PERI.VAL_NOMB_PERI, 			 METAS.VAL_META/PUNTOS.VAL_FACT_CONV*PUNTOS.NUM_PUNT_ASIG  	) ) 	 