 SELECT PAIS, 	   SUBG, 	   REGION, 	   TERRI, 	   N_CONCU, 	   COD_CLIE, 	   NOMBRE, 	   PERI, 	   TC_REGION, 	   CASE WHEN (CONTROL = 1) THEN 	   		PE 	   END PE, 	   NUM_PUNTOS FROM ( 	SELECT PAIS, 		   SUBG, 		   REGION, 		   N_CONCU, 		   TERRI, 		   COD_CLIE, 		   NOMBRE, 		   PERI, 		   PE, 		   NUM_PUNTOS, 		   TC_REGION, 			SUM(1) OVER (PARTITION BY PAIS, SUBG, REGION, N_CONCU, NOMBRE ORDER BY ROWNUM) CONTROL 	FROM	    	(SELECT NOM_PAIS.VAL_I18N PAIS, 			SUBG.DES_SUBG_VENT SUBG, 			REG.DES_REGI REGION, 			PAR_GEN.NUM_CONC N_CONCU, 			TERR.COD_TERR TERRI, 			CLIEN.COD_CLIE COD_CLIE, 			CLIEN.VAL_APE1 || ' ' || CLIEN.VAL_APE2 || ' ' || CLIEN.VAL_NOM1 || ' ' || CLIEN.VAL_NOM2 NOMBRE, 			PERI.VAL_NOMB_PERI PERI, 			METAS.VAL_META/PUNTO.VAL_FACT_CONV*PUNTO.NUM_PUNT_ASIG PE, 			SUM(SOLIC_PUNT.NUM_PUNT) NUM_PUNTOS, 			COUNT(DISTINCT METAS.CLIE_OID_CLIE) OVER (PARTITION BY NOM_PAIS.VAL_I18N, SUBG.DES_SUBG_VENT, REG.DES_REGI) TC_REGION 			 	 FROM INC_CONCU_PARAM_GENER PAR_GEN, 		  INC_OBTEN_PUNTO PUNTO, 		  INC_METAS_TIPO_VENTA METAS, 		  MAE_CLIEN_UNIDA_ADMIN UNI_ADMIN, 		  ZON_TERRI_ADMIN TERR_ADM, 		  ZON_SECCI SEC, 		  ZON_ZONA ZONA, 		  ZON_REGIO REG, 		  ZON_SUB_GEREN_VENTA SUBG, 		  ZON_TERRI TERR, 		  CRA_PERIO PERI, 		  INC_SOLIC_CONCU_PUNTA SOLIC_PUNT, 		  MAE_CLIEN CLIEN, 		   		  (SELECT PER.OID_PERI, PER.FEC_INIC 		   FROM CRA_PERIO PER 		   WHERE PER.PAIS_OID_PAIS IN( 1,2,3 ) 			        AND PER.MARC_OID_MARC IN ( 1 )    	 		  ) PER1, 		   		  (SELECT PER.OID_PERI, PER.FEC_FINA 		   FROM CRA_PERIO PER 		   WHERE PER.PAIS_OID_PAIS IN( 1,2,3 ) 			        AND PER.MARC_OID_MARC IN ( 1 )    	 		  ) PER2, 		   		  (SELECT GEN.VAL_OID, GEN.VAL_I18N 		   FROM V_GEN_I18N_SICC GEN 		   WHERE GEN.ATTR_ENTI = 'SEG_PAIS' 			 AND GEN.IDIO_OID_IDIO =  1  	 		  ) NOM_PAIS 			  	WHERE PUNTO.COPA_OID_PARA_GRAL = PAR_GEN.OID_PARA_GRAL 	   AND METAS.COPA_OID_PARA_GRAL = PAR_GEN.OID_PARA_GRAL 	   AND METAS.CLIE_OID_CLIE = UNI_ADMIN.CLIE_OID_CLIE 	   AND METAS.CLIE_OID_CLIE = CLIEN.OID_CLIE 	   AND METAS.CLIE_OID_CLIE = SOLIC_PUNT.CLIE_OID_CLIE 	   AND UNI_ADMIN.IND_ACTI = 1 	   AND PAR_GEN.IND_ACTI = 1 	   AND UNI_ADMIN.ZTAD_OID_TERR_ADMI = TERR_ADM.OID_TERR_ADMI 	   AND TERR_ADM.TERR_OID_TERR = TERR.OID_TERR  	   AND TERR_ADM.ZSCC_OID_SECC = SEC.OID_SECC 	   AND SEC.ZZON_OID_ZONA = ZONA.OID_ZONA 	   AND ZONA.ZORG_OID_REGI = REG.OID_REGI 	   AND REG.ZSGV_OID_SUBG_VENT = SUBG.OID_SUBG_VENT 	   AND PAR_GEN.PERD_OID_PERI_DESD = PER1.OID_PERI 	   AND PAR_GEN.PERD_OID_PERI_HAST = PER2.OID_PERI 	   AND PERI.FEC_INIC >= PER1.FEC_INIC 	   AND PERI.FEC_FINA  < = PER2.FEC_FINA 	   AND PERI.PAIS_OID_PAIS = PAR_GEN.PAIS_OID_PAIS 	   AND PERI.MARC_OID_MARC = PAR_GEN.MARC_OID_MARC 	   AND PERI.OID_PERI = SOLIC_PUNT.PERD_OID_PERI 	   AND PAR_GEN.PAIS_OID_PAIS = NOM_PAIS.VAL_OID(+) 	   AND PAR_GEN.OID_PARA_GRAL = SOLIC_PUNT.COPA_OID_PARA_GRAL 	   AND PAR_GEN.MARC_OID_MARC = SUBG.MARC_OID_MARC 	   AND PAR_GEN.NUM_CONC IN  ( '1','2','05/002' )  	   AND PAR_GEN.PAIS_OID_PAIS IN ( 1,2,3 )  	          AND PAR_GEN.MARC_OID_MARC IN ( 1 )    	      AND SUBG.OID_SUBG_VENT IN  ( 1 )   	      and REG.OID_REGI IN  ( 1 )    	 	   GROUP BY NOM_PAIS.VAL_I18N, 				SUBG.DES_SUBG_VENT, 				REG.DES_REGI, 				PAR_GEN.NUM_CONC, 			   TERR.COD_TERR, 			   METAS.CLIE_OID_CLIE, 			   CLIEN.COD_CLIE, 			   CLIEN.VAL_APE1 || ' ' || CLIEN.VAL_APE2 || ' ' || CLIEN.VAL_NOM1 || ' ' || CLIEN.VAL_NOM2, 			   PERI.VAL_NOMB_PERI, 			   METAS.VAL_META/PUNTO.VAL_FACT_CONV*PUNTO.NUM_PUNT_ASIG 		) 	)			 