 		( 		SELECT GEN_PAIS.PAIS, 			   'ZONA' AS GRUPO, 			   COUNT(DISTINCT ZON.CLIE_OID_CLIE) AS CANTIDAD_GERENTES, 			    50  AS PORCENTAJE,  			   COUNT(DISTINCT ZON.CLIE_OID_CLIE) *  50   			   / 100 AS GAN_RECONOCIMIENTO  		FROM ZON_ZONA ZON, 			 ( 			  SELECT VAL_OID, VAL_I18N AS PAIS 			  FROM V_GEN_I18N_SICC 			  WHERE ATTR_ENTI = 'SEG_PAIS' 			  		AND IDIO_OID_IDIO = 1 			 )GEN_PAIS 		WHERE ZON.PAIS_OID_PAIS IN ( 1,2,3,4,5,6,7  )  			  AND ZON.MARC_OID_MARC =  1    			  AND ZON.CANA_OID_CANA =  1   			  AND ZON.IND_ACTI = 1 			  AND ZON.CLIE_OID_CLIE IS NOT NULL 			  AND ZON.PAIS_OID_PAIS = GEN_PAIS.VAL_OID(+) 		GROUP BY GEN_PAIS.PAIS 	) 	UNION 	(	   		SELECT GEN_PAIS.PAIS, 			   'REGION', 			   COUNT(DISTINCT REG.CLIE_OID_CLIE), 			    50   AS PORCEANTAJE,  			   COUNT(DISTINCT REG.CLIE_OID_CLIE) *  50   			   / 100  		FROM ZON_REGIO REG, 			 ( 			  SELECT VAL_OID, VAL_I18N AS PAIS 			  FROM V_GEN_I18N_SICC 			  WHERE ATTR_ENTI = 'SEG_PAIS' 			  		AND IDIO_OID_IDIO = 1 			 )GEN_PAIS 		WHERE REG.PAIS_OID_PAIS IN ( 1,2,3,4,5,6,7  )  			  AND REG.MARC_OID_MARC =  1   			  AND REG.CANA_OID_CANA =  1  			  AND REG.IND_ACTI = 1 			  AND REG.CLIE_OID_CLIE IS NOT NULL 			  AND REG.PAIS_OID_PAIS = GEN_PAIS.VAL_OID(+) 		GROUP BY GEN_PAIS.PAIS 			   	) 	UNION 	( 		SELECT GEN_PAIS.PAIS, 			   'SUBGERENCIA', 			   COUNT(DISTINCT SUBG.CLIE_OID_CLIE), 			    50  AS PORCENTAJE, 			   COUNT(DISTINCT SUBG.CLIE_OID_CLIE) *    50   			   / 100 		FROM ZON_SUB_GEREN_VENTA SUBG, 			 ( 			  SELECT VAL_OID, VAL_I18N AS PAIS 			  FROM V_GEN_I18N_SICC 			  WHERE ATTR_ENTI = 'SEG_PAIS' 			  		AND IDIO_OID_IDIO = 1 			 )GEN_PAIS 		WHERE SUBG.PAIS_OID_PAIS IN ( 1,2,3,4,5,6,7  )  			  AND SUBG.MARC_OID_MARC =  1   			  AND SUBG.CANA_OID_CANA =  1  			  AND SUBG.IND_BORR  < > 1 			  AND SUBG.CLIE_OID_CLIE IS NOT NULL 			  AND SUBG.PAIS_OID_PAIS = GEN_PAIS.VAL_OID(+) 		GROUP BY GEN_PAIS.PAIS 	)  