		 SELECT 	num_conc, 	peri_ini, 	peri_fin, 	peri_eval, 	n_recda_efect, 	n_recte_cons, 	frec_acum, 	total_recda, 	porc_recom_ingre FROM 	( 	SELECT 		num_conc, 		peri_ini, 		peri_fin, 		peri_eval, 		total_recda AS n_recda_efect, 		COUNT(recte) AS n_recte_cons, 		sum(COUNT(recte)) OVER (PARTITION BY num_conc, peri_eval ORDER BY total_recda DESC) AS frec_acum, 		total_recda * COUNT(recte) AS total_recda, 		(total_recda * COUNT(recte))*100 /  55  AS porc_recom_ingre	   	FROM 		( 		SELECT 			cpg.num_conc, 			peri1.val_nomb_peri AS peri_ini, 			peri2.val_nomb_peri AS peri_fin, 			peri3.val_nomb_peri AS peri_eval, 			recte.oid_clie_rete AS recte, 			COUNT(recdo.oid_clie_redo) AS total_recda	 		FROM 			inc_concu_param_gener cpg, 			inc_param_gener_premi pgp, 			inc_param_nivel_premi pnp, 			zon_sub_geren_venta subg, 			zon_regio reg, 			zon_zona zona, 			zon_secci sec, 			zon_terri_admin ta, 			mae_clien_unida_admin cli_ua, 			inc_clien_recte recte, 			inc_clien_recdo recdo, 			cra_perio peri1, 			cra_perio peri2, 			cra_perio peri3 		WHERE 			subg.oid_subg_vent = reg.zsgv_oid_subg_vent 			AND reg.oid_regi = zona.zorg_oid_regi 			AND zona.oid_zona = sec.zzon_oid_zona 			AND sec.oid_secc = ta.zscc_oid_secc 			AND cli_ua.ind_acti = 1 			AND ta.oid_terr_admi = cli_ua.ztad_oid_terr_admi 			AND recte.copa_oid_para_gral = cpg.oid_para_gral 			AND recte.clie_oid_clie = cli_ua.clie_oid_clie 			AND pgp.copa_oid_para_gral = cpg.oid_para_gral 			AND pnp.pagp_oid_para_gene_prem = pgp.oid_para_gene_prem 			AND recdo.panp_oid_para_nive_prem = pnp.oid_para_nive_prem 			AND recdo.clr3_oid_clie_rete = recte.oid_clie_rete 			AND cpg.perd_oid_peri_desd = peri1.oid_peri 			AND cpg.perd_oid_peri_hast = peri2.oid_peri 			AND recdo.perd_oid_peri = peri3.oid_peri  			AND cpg.ind_acti = 1 			AND recdo.ind_efec = 1	   			AND cpg.oid_para_gral =  1   			AND subg.pais_oid_pais =  1   			AND subg.marc_oid_marc =  1   			AND subg.cana_oid_cana =  1   		GROUP BY 			cpg.num_conc, 			peri1.val_nomb_peri, 			peri2.val_nomb_peri, 			peri3.val_nomb_peri, 			recte.oid_clie_rete 		)	   	  	GROUP BY 		num_conc, 		peri_ini, 		peri_fin, 		peri_eval, 		total_recda 	ORDER BY 		num_conc, 		n_recda_efect 	) 			  WHERE 	1=1 	  AND n_recda_efect IN  ( 1 )   ORDER BY 	num_conc, 	peri_eval, 	n_recda_efect 	 	