 SELECT num_conc, 	   per_desde, 	   per_hasta, 	   des_subg_vent, 	   des_regi, 	   des_zona, 	   cod_terr, 	     COD_CLIE, 	   nombre_completo, 	   puntaje, 	   CASE WHEN tipo_premio_mon = 1 THEN val_cant 	   		WHEN tipo_premio_mon = 2 THEN puntaje * val_porc / 100 			WHEN tipo_premio_mon = 3 THEN puntaje 	   END AS premio FROM ( 	SELECT con.NUM_CONC, 		   per_ini.val_nomb_peri AS per_desde, 		   per_fin.val_nomb_peri AS per_hasta, 		   subg.DES_SUBG_VENT, 		   reg.DES_REGI, 		   zon.DES_ZONA, 		   ter.COD_TERR, 		     cli.COD_CLIE, 		   cli.VAL_NOM1||' '||cli.VAL_NOM2||' '||cli.VAL_APE1||' '||cli.VAL_APE2 AS nombre_completo, 		   ( 		   	SELECT SUM(cc.NUM_PUNT)  			FROM inc_cuent_corri_punto cc 			WHERE cc.CLIE_OID_CLIE = cli.oid_clie 				  AND cc.COPA_OID_PARA_GRAL = con.oid_para_gral 				  AND (  				  	  cc.TMOV_OID_TIPO_MOVI = 1 OR  					  ( cc.TMOV_OID_TIPO_MOVI = 2 AND ( con.IND_ANUL = 1 OR con.IND_DEVO = 1) ) OR  					  cc.TMOV_OID_TIPO_MOVI = 3 ) 		   )puntaje, 		   mon.TPMO_OID_TIPO_PREM_MONE AS tipo_premio_mon, 		   CASE WHEN mon.MONE_OID_MONE =  2  		   			 THEN val_cant 		   		ELSE val_cant * 				   ( 				   	SELECT cam.VAL_TIPO_CAMB 					FROM seg_tipo_cambi cam 					WHERE cam.MONE_OID_MON1 = mon.MONE_OID_MONE 						  AND cam.MONE_OID_MON2 =  2  				   )   		   END AS val_cant, 		   mon.VAL_PORC 	FROM inc_concu_param_gener con, 		 inc_premi_monet mon, 		 inc_ganad gan, 		 inc_param_nivel_premi niv, 		 inc_param_gener_premi par_pre, 		 mae_clien_unida_admin un_adm, 		 mae_clien cli, 		 zon_terri_admin ter_adm, 		 zon_terri ter, 		 zon_secci sec, 		 zon_zona zon, 		 zon_regio reg, 		 zon_sub_geren_venta subg, 		 cra_perio per_ini, 		 cra_perio per_fin		  	WHERE con.OID_PARA_GRAL = par_pre.COPA_OID_PARA_GRAL 		  AND par_pre.OID_PARA_GENE_PREM = niv.PAGP_OID_PARA_GENE_PREM 		  AND niv.OID_PARA_NIVE_PREM = gan.PANP_OID_PARA_NIVE_PREM 		  AND gan.CLIE_OID_CLIE = un_adm.clie_oid_clie 		  AND un_adm.IND_ACTI = 1 		  AND con.IND_ACTI = 1 		  AND un_adm.CLIE_OID_CLIE = cli.oid_clie 		  AND un_adm.ZTAD_OID_TERR_ADMI = ter_adm.OID_TERR_ADMI 		  AND ter_adm.TERR_OID_TERR = ter.OID_TERR 		  AND ter_adm.ZSCC_OID_SECC = sec.OID_SECC 		  AND sec.ZZON_OID_ZONA = zon.OID_ZONA 		  AND zon.ZORG_OID_REGI = reg.OID_REGI 		  AND reg.ZSGV_OID_SUBG_VENT = subg.OID_SUBG_VENT 		  AND con.PERD_OID_PERI_DESD = per_ini.oid_peri 		  AND con.PERD_OID_PERI_HAST = per_fin.oid_peri 		  AND mon.PANP_OID_PARA_NIVE_PREM = niv.PAGP_OID_PARA_GENE_PREM 		  AND con.PAIS_OID_PAIS =   1   		  AND con.MARC_OID_MARC =   1   		  AND con.CANA_OID_CANA =   1   		 AND SUBG.OID_SUBG_VENT IN  ( 1 )  		  AND REG.OID_REGI IN ( 1 )   		  AND zon.OID_ZONA IN ( 1,2,3,4,5,6,7,8 )  		  AND con.OID_PARA_GRAL =  1    )   