select 	  pais, 	  marca, 	  region, 	  periodo, 	  sum(atendidas)+sum(pendientes)total, 	  sum(atendidas) atendidas, 	  sum(pendientes) pendientes, 	  sum(menos_24) menos24, 	  sum(mas_24_menos_48) entre24_48, 	 sum(mas_48) mas48, 	  sum(tipo_clie_1) tipoclie1,   	  sum(tipo_clie_2) tipoclie2,   	 sum(tipo_clie_3) tipoclie3,   	  sum(tipo_clie_4) tipoclie4,   	  sum(tipo_clie_5) tipoclie5,   	  sum(tipo_clie_6) tipoclie6 from  ( 	select  		   gen_pais.pais pais, 		   mar.DES_MARC marca, 	   	   reg.DES_REGI region,            NULL PERIODO, 		   CASE WHEN (TIPO_EST_CONT.COD_ESTA = '03') THEN 				 1 		   ELSE 				 0 		   END ATENDIDAS, 			CASE WHEN (TIPO_EST_CONT.COD_ESTA  < > '03') THEN 				 1 			ELSE 				 0 			END PENDIENTES, 			CASE WHEN (TIPO_EST_CONT.COD_ESTA = '03') THEN 				 CASE WHEN (nvl(trunc((((86400*(decode(conta.FEC_HORA_CIER,null,sysdate,conta.FEC_HORA_CIER)-conta.VAL_HORA_INIC))/60)/60)/24),0)  <  1) THEN 				 	  1 				 ELSE 				 	  0 				 END 		    ELSE 				 0 			END menos_24, 			CASE WHEN (TIPO_EST_CONT.COD_ESTA = '03') THEN 				 CASE WHEN ((nvl(trunc((((86400*(decode(conta.FEC_HORA_CIER,null,sysdate,conta.FEC_HORA_CIER)-conta.VAL_HORA_INIC))/60)/60)/24),0) >= 1) AND (nvl(trunc((((86400*(decode(conta.FEC_HORA_CIER,null,sysdate,conta.FEC_HORA_CIER)-conta.VAL_HORA_INIC))/60)/60)/24),0)  <  2)) THEN 				 	  1 				 ELSE 				 	  0 				 END 		    ELSE 				 0 			END Mas_24_menos_48, 			CASE WHEN (TIPO_EST_CONT.COD_ESTA = '03') THEN 				 CASE WHEN (nvl(trunc((((86400*(decode(conta.FEC_HORA_CIER,null,sysdate,conta.FEC_HORA_CIER)-conta.VAL_HORA_INIC))/60)/60)/24),0) >= 2) THEN 				 	  1 				 ELSE 				 	  0 				 END 		    ELSE 				 0 			END mas_48, 			CASE WHEN (CONTA.TICL_OID_TIPO_CLIE =  1 ) THEN 				 1 		    ELSE 				 0 			END TIPO_CLIE_1, 			CASE WHEN (CONTA.TICL_OID_TIPO_CLIE =  -1 ) THEN 				 1 		    ELSE 				 0 		    END TIPO_CLIE_2, 			CASE WHEN (CONTA.TICL_OID_TIPO_CLIE =  -1 ) THEN 				 1 		    ELSE 				 0 		    END TIPO_CLIE_3, 			CASE WHEN (CONTA.TICL_OID_TIPO_CLIE =  -1 ) THEN 				 1 		    ELSE 				 0 		    END TIPO_CLIE_4, 			CASE WHEN (CONTA.TICL_OID_TIPO_CLIE =  -1 ) THEN 				 1 		    ELSE 				 0 		    END TIPO_CLIE_5, 			CASE WHEN (CONTA.TICL_OID_TIPO_CLIE =  -1 ) THEN 				 1 		    ELSE 				 0 		    END TIPO_CLIE_6	 	from  		 mae_clien_unida_admin un_adm, 		 zon_terri_admin ter_adm, 		 zon_secci sec, 		 zon_zona zon, 		 zon_regio reg, 		 zon_sub_geren_venta subg, 		 cal_conta conta, 		 cal_motiv_conta mot_conta, 		 seg_marca mar, 		 cal_atrib_estad_conta est_cont, 		 cal_tipo_estad_conta tipo_est_cont, 		 ( 		 select gen.val_oid, gen.val_i18n pais  		 from v_gen_i18n_sicc gen 		 where gen.ATTR_ENTI = 'SEG_PAIS' 		 	   and gen.IDIO_OID_IDIO =  1  		 )gen_pais 	where un_adm.ZTAD_OID_TERR_ADMI = ter_adm.OID_TERR_ADMI 		  and ter_adm.ZSCC_OID_SECC = sec.OID_SECC 		  and sec.ZZON_OID_ZONA = zon.OID_ZONA 		  and zon.ZORG_OID_REGI = reg.OID_REGI 		  and reg.ZSGV_OID_SUBG_VENT = subg.OID_SUBG_VENT 		  AND GEN_PAIS.VAL_OID(+) = SUBG.PAIS_OID_PAIS 		  AND est_cont.TECO_OID_TIPO_ESTA_CONT = tipo_est_cont.OID_TIPO_ESTA_CONT 		  and est_cont.OID_ATRI_ESTA_CONT = conta.ATEC_OID_ATRI_ESTA_CONT				   		  and subg.PAIS_OID_PAIS =  1  		  and subg.MARC_OID_MARC =  1  		  and subg.CANA_OID_CANA =  1  		   and subg.OID_SUBG_VENT =  1   		   and reg.OID_REGI =  1   		  and conta.VAL_HORA_INIC >= to_date( '01/01/2000' ,  'dd/MM/yyyy' ) 		  and conta.VAL_HORA_FIN  < = to_date( '01/01/2099' ,  'dd/MM/yyyy' ) 		  and un_adm.ind_acti = 1  		  and conta.clie_oid_clie = un_adm.CLIE_OID_CLIE 		  and conta.pais_oid_pais = subg.PAIS_OID_PAIS 		  and conta.MOCO_OID_MOTI_CONT = mot_conta.OID_MOTI_CONT 		  and subg.MARC_OID_MARC = mar.OID_MARC 		  and mot_conta.COD_MOTI = '00' 	 		  AND CONTA.TICL_OID_TIPO_CLIE IN ( 1 , -1 , -1   , -1 , -1 , -1 )  ) group by 	  pais, 	  marca, 	  region, 	  periodo order by region