select gen_pais.val_i18n Pais, cab.COD_BUZO, cab.val_desc_cabe_buzo Desc_Buzon, cli.COD_CLIE,        usu.VAL_APEL_PATE||' '||usu.VAL_APE_MATE||' '||usu.VAL_NOM1||' ' ||usu.VAL_NOM2 as NOMBRE_CLIE, 	   det.COD_ACTI, det.VAL_DESC_ACTI, det.FEC_HORA_CREA, to_char(det.FEC_HORA_CREA, 'HH24:MI:SS') as HORA, trunc((((86400*(SYSDATE - det.FEC_HORA_CREA))/60)/60)/24) Dias_Pendientes, 	   con.COD_CONT Contactos, 1 as CANTIDAD from cal_cabec_buzon cab, 	 cal_detal_buzon det, 	 cal_conta con, 	 cal_estat_activ_conta act, 	 mae_clien cli, 	 V_REP_USUAR_LARGO usu, 	 (select gen.VAL_OID, gen.VAL_I18N                   	  from v_gen_i18n_sicc gen 	  where gen.attr_enti = 'SEG_PAIS' 	  		and gen.IDIO_OID_IDIO =  1  	 ) gen_pais where con.OID_CONT = det.CONT_OID_CONT(+)                 	  and cab.OID_CABE_BUZO = det.CABU_OID_CABE_BUZO   	  and cab.PAIS_OID_PAIS =  1            	  and to_date(det.FEC_HORA_CREA) >= to_date( '01/01/2000' ,  'dd/MM/yyyy' ) 	  and to_date(det.FEC_HORA_CREA)  < = to_date( '01/01/2099' ,  'dd/MM/yyyy'  ) 	   and cab.OID_CABE_BUZO =  1   	   and CLI.COD_CLIE =  '1'   	  and det.ESAC_OID_ESTA_ACTI_CONT = act.OID_ESTA_ACTI_CONT       and act.COD_ESTA = 'N'     	  and con.CLIE_OID_CLIE = cli.OID_CLIE  	  and con.COD_OPER = usu.VAL_NOMB_USUA 	  and gen_pais.val_oid = cab.PAIS_OID_PAIS order by gen_pais.val_oid, cab.COD_BUZO, det.FEC_HORA_CREA, cli.cod_clie