 SELECT GEN_PAIS.VAL_I18N PAIS, 	   			  SOCIEDAD.VAL_DENO SOCIEDAD, 	  			  FAC.VAL_NOMB_PERI, 				  FAC.OID_REGI, 	   			  FAC.DES_REGI, 				  REC.COD_OPER, 				  REC.DES_OPERA OPERACION, 				  FAC.COD_ZONA, 				  FAC.GERENTE, 				  FAC.FACTURADO, 				  REC.OPERACION CANT_OPERACION, 				  DECODE(FAC.FACTURADO,0,0,(REC.OPERACION/FAC.FACTURADO)*100) POR_OPER_FACT, 				  DECODE(FAC.UNID_ATEN,0,0,(REC.OPERACION/FAC.UNID_ATEN)*100) PORC_PARTICIPACION, 				  FAC.TOTAL, 				  FAC.UNID_ATEN NUM_UNID_ATEN FROM ( SELECT PER.VAL_NOMB_PERI, 	   			  REG.DES_REGI, 				  ZON.COD_ZONA, 				  PER.OID_PERI, 				  REG.OID_REGI, 				  ZON.OID_ZONA, 				  CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2 GERENTE, 				  SUM(FAC_LIN.NUM_UNID_ATEN) UNID_ATEN, 				  SUM(FAC.VAL_TOTA_PAGA_LOCA) TOTAL, 				  COUNT(DISTINCT FAC.OID_CABE) FACTURADO				  				   FROM 	FAC_DOCUM_CONTA_CABEC FAC, 	FAC_DOCUM_CONTA_LINEA FAC_LIN, 	ZON_REGIO REG, 	ZON_ZONA ZON, 	CRA_PERIO PER, 	MAE_CLIEN CLI, 	PED_SOLIC_CABEC PSC, 	( 	 SELECT FEC_INIC 	 FROM CRA_PERIO 	 WHERE OID_PERI =   57  	)PER_INI, 	( 	 SELECT FEC_FINA 	 FROM CRA_PERIO 	 WHERE OID_PERI = DECODE( -1 ,-1,  57 , -1 )  	)PER_FIN WHERE FAC.OID_CABE = FAC_LIN.DCCA_OID_CABE 	 AND FAC.ZORG_OID_REGI = REG.OID_REGI 	 AND FAC.ZZON_OID_ZONA = ZON.OID_ZONA	 	 AND FAC.PERD_OID_PERI = PER.OID_PERI 	 AND FAC.SOCA_OID_SOLI_CABE = PSC.OID_SOLI_CABE 	 AND PSC.IND_OC = 1 	 AND REG.OID_REGI = ZON.ZORG_OID_REGI 	 AND ZON.CLIE_OID_CLIE = CLI.OID_CLIE(+) 	 AND PER_INI.FEC_INIC  < = PER.FEC_INIC 	 AND PER.FEC_FINA  < = PER_FIN.FEC_FINA 	 AND PER.PAIS_OID_PAIS =  1 		       AND REG.OID_REGI IN  ( 1 )         AND ZON.OID_ZONA IN  ( 1 )   	 AND PER.MARC_OID_MARC IN ( 1 )  	 AND PER.CANA_OID_CANA IN ( 1 ) 	  GROUP BY PER.VAL_NOMB_PERI, 	   			  REG.DES_REGI, 				  ZON.COD_ZONA, 				  PER.OID_PERI, 				  REG.OID_REGI, 				  ZON.OID_ZONA, 				  CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2 ) FAC,				   (				   SELECT PER.VAL_NOMB_PERI, 	   			  REG.DES_REGI, 				  ZON.COD_ZONA, 				  OP.COD_OPER, 				  GEN_OP.OPERACION DES_OPERA,   				  PER.OID_PERI, 				  REG.OID_REGI, 				  ZON.OID_ZONA, 				  SUM(LIN_REC.NUM_UNID_RECL) OPERACION  FROM  	REC_CABEC_RECLA CAB_REC, 	REC_OPERA_RECLA OP_REC, 	REC_LINEA_OPERA_RECLA LIN_REC, 	REC_TIPOS_OPERA T_OP, 	REC_OPERA OP, 	ZON_REGIO REG, 	ZON_ZONA ZON, 	MAE_CLIEN CLI, 	CRA_PERIO PER, 	( 	 SELECT FEC_INIC 	 FROM CRA_PERIO 	 WHERE OID_PERI =    57  	)PER_INI, 	( 	 SELECT FEC_FINA 	 FROM CRA_PERIO 	 WHERE OID_PERI = DECODE(  -1 ,-1,  57 , -1 )  	)PER_FIN, 	( 	 SELECT VAL_OID, VAL_I18N OPERACION 	 FROM V_GEN_I18N_SICC 	 WHERE ATTR_ENTI = 'REC_OPERA' 	 	   AND IDIO_OID_IDIO =  1 	 	)GEN_OP, 	ZON_SECCI SEC, 	ZON_TERRI_ADMIN TER WHERE CASE WHEN (-1 IN ( -1 )) THEN	 	 	 	  OP.IND_ANUL 	     ELSE 		  	 0  		 END  < > 1 		 AND CAB_REC.PERD_OID_PERI_DOCU_REFE = PER.OID_PERI 	 	 AND PER_INI.FEC_INIC  < = PER.FEC_INIC 		 AND PER.FEC_FINA  < = PER_FIN.FEC_FINA 		 AND PER.PAIS_OID_PAIS =  1   		 AND PER.MARC_OID_MARC IN ( 1 )  		 AND PER.CANA_OID_CANA IN ( 1 ) 	  		 AND REG.OID_REGI = ZON.ZORG_OID_REGI 		 AND ZON.OID_ZONA = SEC.ZZON_OID_ZONA 		 AND SEC.OID_SECC = TER.ZSCC_OID_SECC 		 AND CAB_REC.ZTAD_OID_TERR_ADMI = TER.OID_TERR_ADMI 		 AND CAB_REC.CLIE_OID_CLIE = CLI.OID_CLIE 		 AND CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 		 AND OP.OID_OPER = T_OP.ROPE_OID_OPER 		 AND T_OP.OID_TIPO_OPER = OP_REC.TIOP_OID_TIPO_OPER 		 AND LIN_REC.OPRE_OID_OPER_RECL = OP_REC.OID_OPER_RECL 		 AND OP.OID_OPER = GEN_OP.VAL_OID(+)		 		  AND CASE WHEN (-1 IN ( -1 )) THEN 	 	 	  -1 		 ELSE	   	 	 	  OP.OID_OPER 		 END IN ( -1 )  		      AND REG.OID_REGI IN  ( 1 )    		      AND ZON.OID_ZONA IN  ( 1 )   GROUP BY PER.VAL_NOMB_PERI, 	  	 		 REG.DES_REGI, 				  ZON.COD_ZONA, 				  GEN_OP.OPERACION, 				   PER.OID_PERI, 				     OP.COD_OPER, 				  REG.OID_REGI, 				  ZON.OID_ZONA )	REC, ( 		   SELECT SOC.VAL_DENO 		   FROM PRINCIPALS PRI, 		   		PROPERTYVALUES PRO, 		   		SEG_SOCIE SOC			 		   WHERE PRI.NAME = %VALUE_ZONEUSERVALUE%	   		   		 AND PRO.IDPROPERTY = 48 				 AND PRO.IDPRINCIPAL = PRI.IDPRINCIPAL 				 AND SOC.OID_SOCI = PRO.STRINGVALUE ) SOCIEDAD, (  SELECT VAL_OID, VAL_I18N  	 FROM V_GEN_I18N_SICC 	 WHERE ATTR_ENTI = 'SEG_PAIS' 	 	   AND IDIO_OID_IDIO =  1  		   AND VAL_OID =  1 	 ) GEN_PAIS WHERE FAC.OID_PERI = REC.OID_PERI(+) 	  			   AND FAC.OID_REGI = REC.OID_REGI(+) 				   AND FAC.OID_ZONA = REC.OID_ZONA(+)  