  SELECT FACT.PAIS, 	       FACT.VAL_NOMB_PERI,        	EMPRE.EMPRESA,        	FACT.COD_REGI,              FACT.DES_REGI, 	      FACT.COD_ZONA, 	      FACT.DES_ZONA, 	      FACT.GERENTE_ZONA,             FACT.FACTURADO, 	     RECLAMOS.OPERACIONES_ANULADAS, 	     DECODE(FACT.FACTURADO,0,0,(OPERACIONES_ANULADAS/FACT.FACTURADO)*100) POR_ANULACIONES, 	     DECODE(FACT.UNID_ATEN,0,0,(OPERACIONES_ANULADAS/SUM(FACT.UNID_ATEN) OVER (PARTITION BY FACT.VAL_NOMB_PERI, FACT.COD_REGI))*100) POR_PARTICIPACION, 	     FACT.TOTAL FROM 		( 				SELECT PER.OID_PERI, 						   		  REG.OID_REGI, 								  ZONA.OID_ZONA, 								  NVL(COUNT(DISTINCT OP.OID_OPER),0) AS OPERACIONES_ANULADAS 				FROM 					REC_CABEC_RECLA CAB_REC, 					REC_OPERA_RECLA OP_REC, 					REC_OPERA OP, 					REC_TIPOS_OPERA T_OP, 					ZON_REGIO REG, 					ZON_ZONA ZONA, 					ZON_SECCI SEC, 					ZON_TERRI_ADMIN ZTA, 					CRA_PERIO PER, 					( 					 SELECT FEC_INIC 					 FROM CRA_PERIO 					 WHERE OID_PERI =  57  					)PER_INI, 					( 					 SELECT FEC_FINA 					 FROM CRA_PERIO 					 WHERE OID_PERI = DECODE( -1 ,-1, 57 , -1 ) 					)PER_FIN 				WHERE CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 					 AND CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 					 AND OP_REC.TIOP_OID_TIPO_OPER = T_OP.OID_TIPO_OPER 					 AND T_OP.ROPE_OID_OPER = OP.OID_OPER 					 AND CAB_REC.PERD_OID_PERI_RECL = PER.OID_PERI 				 	 AND PER_INI.FEC_INIC  < = PER.FEC_INIC 					 AND PER_FIN.FEC_FINA >= PER.FEC_FINA 					 AND CAB_REC.PAIS_OID_PAIS =  1  					 AND PER.PAIS_OID_PAIS = CAB_REC.PAIS_OID_PAIS 					 AND PER.MARC_OID_MARC  =  1  					 AND PER.CANA_OID_CANA  =  1  					 AND REG.PAIS_OID_PAIS = PER.PAIS_OID_PAIS 					 AND REG.OID_REGI = ZONA.ZORG_OID_REGI 					 AND ZONA.OID_ZONA = SEC.ZZON_OID_ZONA 					 AND SEC.OID_SECC = ZTA.ZSCC_OID_SECC 					 AND CAB_REC.ZTAD_OID_TERR_ADMI = ZTA.OID_TERR_ADMI 					  AND REG.OID_REGI IN ( 1 )  					  AND ZONA.OID_ZONA IN ( 1 )  					 AND OP.OID_OPER =  1043  				GROUP BY PER.OID_PERI, 				                        REG.OID_REGI, 									    ZONA.OID_ZONA 		) RECLAMOS, 	   ( 			   SELECT GEN_PAIS.VAL_I18N PAIS, 			   		   			  REG.OID_REGI, 			   		   			  REG.COD_REGI, 			   		  			  REG.DES_REGI, 								  ZONA.OID_ZONA, 								  ZONA.COD_ZONA, 								  ZONA.DES_ZONA, 								  CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2 GERENTE_ZONA,  								  PER.OID_PERI, 								  PER.VAL_NOMB_PERI, 								  COUNT(DISTINCT FAC.OID_CABE) FACTURADO,         					  	   NVL(SUM(FAC.VAL_TOTA_PAGA_DOCU),0) AS TOTAL, 								   SUM(FAC_LIN.NUM_UNID_ATEN) UNID_ATEN		 						   			   FROM FAC_DOCUM_CONTA_CABEC FAC, 			   			    FAC_DOCUM_CONTA_LINEA FAC_LIN, 							ZON_REGIO REG, 							ZON_ZONA ZONA, 							CRA_PERIO PER, 							MAE_CLIEN CLI, 							( 									 SELECT FEC_INIC 									 FROM CRA_PERIO 									 WHERE OID_PERI =  57  							)PER_INI, 							( 									 SELECT FEC_FINA 									 FROM CRA_PERIO 									 WHERE OID_PERI = DECODE( -1 ,-1, 57 , -1 ) 							)PER_FIN, 							( 							 		 SELECT GEN.VAL_OID, GEN.VAL_I18N 									 FROM V_GEN_I18N_SICC GEN 									 WHERE GEN.ATTR_ENTI = 'SEG_PAIS' 									 	   				 AND GEN.IDIO_OID_IDIO =  1  							) GEN_PAIS							 			  WHERE FAC.OID_CABE = FAC_LIN.DCCA_OID_CABE 			  					AND FAC.ZORG_OID_REGI = REG.OID_REGI 								AND FAC.ZZON_OID_ZONA = ZONA.OID_ZONA 								AND ZONA.ZORG_OID_REGI = REG.OID_REGI 								AND REG.PAIS_OID_PAIS =  1  							    AND PER_INI.FEC_INIC  = PER.FEC_INIC 							    AND PER_FIN.FEC_FINA >= PER.FEC_FINA 								AND FAC.PERD_OID_PERI = PER.OID_PERI 								AND PER.PAIS_OID_PAIS = REG.PAIS_OID_PAIS 								AND GEN_PAIS.VAL_OID(+) = PER.PAIS_OID_PAIS 								AND PER.MARC_OID_MARC =  1  								AND PER.CANA_OID_CANA =  1  								AND ZONA.CLIE_OID_CLIE = CLI.OID_CLIE(+) 		      AND REG.OID_REGI IN ( 1 )  			  AND ZONA.OID_ZONA IN ( 1 )  	  	GROUP BY GEN_PAIS.VAL_I18N, 					  	 				REG.OID_REGI, 					  	 				REG.COD_REGI, 					  	 			  	REG.DES_REGI, 										ZONA.OID_ZONA, 										ZONA.COD_ZONA, 										ZONA.DES_ZONA, 		                                CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2, 										PER.VAL_NOMB_PERI, 										PER.OID_PERI 	 	) FACT,	  	   ( 		   SELECT SOC.VAL_DENO EMPRESA 		   FROM PRINCIPALS PRI, 		   		PROPERTYVALUES PRO, 		   		SEG_SOCIE SOC			 		   WHERE PRI.NAME =  'USUARIO1'  		   		 AND PRO.IDPROPERTY = 48 				 AND PRO.IDPRINCIPAL = PRI.IDPRINCIPAL 				 AND PRO.STRINGVALUE = SOC.OID_SOCI 	   ) EMPRE WHERE RECLAMOS.OID_PERI(+) = FACT.OID_PERI 	  			AND RECLAMOS.OID_REGI(+) = FACT.OID_REGI 				AND RECLAMOS.OID_ZONA(+) = FACT.OID_ZONA 	