 SELECT UNID_RECLA_TOTAL.OID_REGI, 	              UNID_RECLA_TOTAL.DES_REGI, 				  UNID_RECLA_TOTAL.OID_OPER, 				  UNID_RECLA_TOTAL.OPERACION, 				  UNID_RECLA_TOTAL.PERIODO_N, 				  PERIODOS.PERIODO_N_1,  				  PERIODOS.PERIODO_N_2,    				  PERIODOS.PERIODO_N_3, 				  UNID_RECLA_TOTAL.UNIDADES_RECLAMADAS, 				  PERIODOS_N.UNID_RECLA_N UNIDADES_N, 				  PERIODOS_N_1.UNID_RECLA_N_1 UNIDADES_N_1, 				  PERIODOS_N_2.UNID_RECLA_N_2 UNIDADES_N_2, 				  PERIODOS_N_3.UNID_RECLA_N_3 UNIDADES_N_3 FROM (				   				SELECT REG.OID_REGI,  					   			  REG.DES_REGI,  								  OP.COD_OPER OID_OPER, 		                   		  GEN_OP.OPERACION, 		                   		  PERIODOS.VAL_NOMB_PERI PERIODO_N, 		                   		  NVL (SUM (LIN_REC.NUM_UNID_RECL), 0) UNIDADES_RECLAMADAS 		       FROM REC_CABEC_RECLA CAB_REC, 		                   REC_OPERA_RECLA OP_REC, 		                   REC_LINEA_OPERA_RECLA LIN_REC, 		                   REC_TIPOS_OPERA T_OP, 		                   REC_OPERA OP, 		                   ZON_TERRI_ADMIN TER_ADM, 		                   ZON_SECCI SEC, 		                   ZON_ZONA ZON, 		                   ZON_REGIO REG, 						   CRA_PERIO PERIODOS, 		                   ( 								   SELECT VAL_OID, VAL_I18N OPERACION 		  	                       FROM V_GEN_I18N_SICC 				                   WHERE ATTR_ENTI = 'REC_OPERA'  										 		    AND IDIO_OID_IDIO =  1  							 ) GEN_OP 		             WHERE CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 		               AND CAB_REC.ZTAD_OID_TERR_ADMI = TER_ADM.OID_TERR_ADMI 		               AND SEC.OID_SECC = TER_ADM.ZSCC_OID_SECC 		               AND ZON.OID_ZONA = SEC.ZZON_OID_ZONA 		               AND REG.OID_REGI = ZON.ZORG_OID_REGI 		               AND OP_REC.PERD_OID_PERI_RECL = PERIODOS.OID_PERI 		               AND LIN_REC.OPRE_OID_OPER_RECL = OP_REC.OID_OPER_RECL 		               AND OP_REC.TIOP_OID_TIPO_OPER = T_OP.OID_TIPO_OPER 		               AND OP.OID_OPER = T_OP.ROPE_OID_OPER 		               AND GEN_OP.VAL_OID(+) = OP.OID_OPER 		               AND CAB_REC.PAIS_OID_PAIS =  1  				       AND OP.OID_OPER =  1058   					   AND PERIODOS.OID_PERI =  57  		          GROUP BY REG.OID_REGI, 		                   REG.DES_REGI, 		                   OP.COD_OPER, 		                   GEN_OP.OPERACION, 		                   PERIODOS.VAL_NOMB_PERI ) UNID_RECLA_TOTAL, ( 				SELECT REG.OID_REGI,  								  OP.COD_OPER OID_OPER, 		                   		  NVL (SUM (LIN_REC.NUM_UNID_RECL), 0) UNID_RECLA_N, 		                   		  PERIODOS.VAL_NOMB_PERI PERIODO_N 		       FROM REC_CABEC_RECLA CAB_REC, 		                   REC_OPERA_RECLA OP_REC, 		                   REC_LINEA_OPERA_RECLA LIN_REC, 		                   REC_TIPOS_OPERA T_OP, 		                   REC_OPERA OP, 		                   ZON_TERRI_ADMIN TER_ADM, 		                   ZON_SECCI SEC, 		                   ZON_ZONA ZON, 		                   ZON_REGIO REG, 						   CRA_PERIO PERIODOS 		             WHERE CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 		               AND CAB_REC.ZTAD_OID_TERR_ADMI = TER_ADM.OID_TERR_ADMI 		               AND SEC.OID_SECC = TER_ADM.ZSCC_OID_SECC 		               AND ZON.OID_ZONA = SEC.ZZON_OID_ZONA 		               AND REG.OID_REGI = ZON.ZORG_OID_REGI 		               AND CAB_REC.PERD_OID_PERI_DOCU_REFE = PERIODOS.OID_PERI 		               AND OP_REC.PERD_OID_PERI_RECL = PERIODOS.OID_PERI 		               AND LIN_REC.OPRE_OID_OPER_RECL = OP_REC.OID_OPER_RECL 		               AND OP_REC.TIOP_OID_TIPO_OPER = T_OP.OID_TIPO_OPER 		               AND OP.OID_OPER = T_OP.ROPE_OID_OPER 		               AND CAB_REC.PAIS_OID_PAIS =  1  					   AND OP.OID_OPER =  1058                                                                                                                                        					   AND PERIODOS.OID_PERI =  57         GROUP BY REG.OID_REGI, 		                   OP.COD_OPER, 		                   PERIODOS.VAL_NOMB_PERI ) PERIODOS_N, ( 			SELECT REG.OID_REGI,  							  OP.COD_OPER OID_OPER, 	                   		  NVL (SUM (LIN_REC.NUM_UNID_RECL), 0) UNID_RECLA_N_1, 	                   		  PERIODOS_N_1.VAL_NOMB_PERI PERIODO_N_1 	       FROM REC_CABEC_RECLA CAB_REC, 	                   REC_OPERA_RECLA OP_REC, 	                   REC_LINEA_OPERA_RECLA LIN_REC, 	                   REC_TIPOS_OPERA T_OP, 	                   REC_OPERA OP, 	                   ZON_TERRI_ADMIN TER_ADM, 	                   ZON_SECCI SEC, 	                   ZON_ZONA ZON, 	                   ZON_REGIO REG, 					    ( 					   		  	  SELECT PE.OID_PERI , PE.VAL_NOMB_PERI  	                              FROM CRA_PERIO PE 	                             WHERE PE.OID_PERI = 	                                      ( 										     SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,2) 	                                         FROM CRA_PERIO P 	                                         WHERE P.OID_PERI =  57  											) 						) PERIODOS_N_1 	             WHERE CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 	               AND CAB_REC.ZTAD_OID_TERR_ADMI = TER_ADM.OID_TERR_ADMI 	               AND SEC.OID_SECC = TER_ADM.ZSCC_OID_SECC 	               AND ZON.OID_ZONA = SEC.ZZON_OID_ZONA 	               AND REG.OID_REGI = ZON.ZORG_OID_REGI 	               AND CAB_REC.PERD_OID_PERI_DOCU_REFE = PERIODOS_N_1.OID_PERI 	               AND OP_REC.PERD_OID_PERI_RECL =  57  	               AND LIN_REC.OPRE_OID_OPER_RECL = OP_REC.OID_OPER_RECL 	               AND OP_REC.TIOP_OID_TIPO_OPER = T_OP.OID_TIPO_OPER 	               AND OP.OID_OPER = T_OP.ROPE_OID_OPER 	               AND CAB_REC.PAIS_OID_PAIS =  1  	               AND OP.OID_OPER =  1058                       GROUP BY REG.OID_REGI, 	                   OP.COD_OPER, 	                   PERIODOS_N_1.VAL_NOMB_PERI ) PERIODOS_N_1, ( 			SELECT REG.OID_REGI,  							  OP.COD_OPER OID_OPER, 	                   		  NVL (SUM (LIN_REC.NUM_UNID_RECL), 0) UNID_RECLA_N_2, 	                   		  PERIODOS_N_2.VAL_NOMB_PERI PERIODO_N_2 	       FROM REC_CABEC_RECLA CAB_REC, 	                   REC_OPERA_RECLA OP_REC, 	                   REC_LINEA_OPERA_RECLA LIN_REC, 	                   REC_TIPOS_OPERA T_OP, 	                   REC_OPERA OP, 	                   ZON_TERRI_ADMIN TER_ADM, 	                   ZON_SECCI SEC, 	                   ZON_ZONA ZON, 	                   ZON_REGIO REG, 					    ( 					   		  	  SELECT PE.OID_PERI , PE.VAL_NOMB_PERI  	                              FROM CRA_PERIO PE 	                             WHERE PE.OID_PERI = 	                                      ( 										     SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,3) 	                                         FROM CRA_PERIO P 	                                         WHERE P.OID_PERI =  57  											) 						) PERIODOS_N_2 	             WHERE CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 	               AND CAB_REC.ZTAD_OID_TERR_ADMI = TER_ADM.OID_TERR_ADMI 	               AND SEC.OID_SECC = TER_ADM.ZSCC_OID_SECC 	               AND ZON.OID_ZONA = SEC.ZZON_OID_ZONA 	               AND REG.OID_REGI = ZON.ZORG_OID_REGI 	               AND CAB_REC.PERD_OID_PERI_DOCU_REFE = PERIODOS_N_2.OID_PERI 	               AND OP_REC.PERD_OID_PERI_RECL =  57  	               AND LIN_REC.OPRE_OID_OPER_RECL = OP_REC.OID_OPER_RECL 	               AND OP_REC.TIOP_OID_TIPO_OPER = T_OP.OID_TIPO_OPER 	               AND OP.OID_OPER = T_OP.ROPE_OID_OPER 	               AND CAB_REC.PAIS_OID_PAIS =  1  	               AND OP.OID_OPER =  1058                       GROUP BY REG.OID_REGI, 	                   OP.COD_OPER, 	                   PERIODOS_N_2.VAL_NOMB_PERI ) PERIODOS_N_2, ( 				SELECT REG.OID_REGI,  								  OP.COD_OPER OID_OPER, 		                   		  NVL (SUM (LIN_REC.NUM_UNID_RECL), 0) UNID_RECLA_N_3, 		                   		  PERIODOS.PERIODO_N_3 		       FROM REC_CABEC_RECLA CAB_REC, 		                   REC_OPERA_RECLA OP_REC, 		                   REC_LINEA_OPERA_RECLA LIN_REC, 		                   REC_TIPOS_OPERA T_OP, 		                   REC_OPERA OP, 		                   ZON_TERRI_ADMIN TER_ADM, 		                   ZON_SECCI SEC, 		                   ZON_ZONA ZON, 		                   ZON_REGIO REG, 						   ( 									   SELECT *  									   FROM  					                           (SELECT PE.OID_PERI OID_PERI_N_1, PE.VAL_NOMB_PERI PERIODO_N_1 					                              FROM CRA_PERIO PE 					                             WHERE PE.OID_PERI = 					                                      (SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,2) 					                                         FROM CRA_PERIO P 					                                        WHERE P.OID_PERI =  57 )) , 					                           (SELECT PE.OID_PERI OID_PERI_N_2, PE.VAL_NOMB_PERI PERIODO_N_2 					                              FROM CRA_PERIO PE 					                             WHERE PE.OID_PERI = 					                                      (SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,3) 					                                         FROM CRA_PERIO P 					                                        WHERE P.OID_PERI =  57 )) , 					                           (SELECT PE.OID_PERI OID_PERI_N_3, PE.VAL_NOMB_PERI PERIODO_N_3 					                              FROM CRA_PERIO PE 					                             WHERE PE.OID_PERI = 					                                      (SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,4) 					                                         FROM CRA_PERIO P 					                                        WHERE P.OID_PERI =  57 ))  							) PERIODOS 		             WHERE CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 		               AND CAB_REC.ZTAD_OID_TERR_ADMI = TER_ADM.OID_TERR_ADMI 		               AND SEC.OID_SECC = TER_ADM.ZSCC_OID_SECC 		               AND ZON.OID_ZONA = SEC.ZZON_OID_ZONA 		               AND REG.OID_REGI = ZON.ZORG_OID_REGI 		               AND ((CAB_REC.PERD_OID_PERI_DOCU_REFE  < > PERIODOS.OID_PERI_N_1)  					   AND (CAB_REC.PERD_OID_PERI_DOCU_REFE  < >  57 )  					   AND (CAB_REC.PERD_OID_PERI_DOCU_REFE  < > PERIODOS.OID_PERI_N_2)) 		               AND OP_REC.PERD_OID_PERI_RECL =  57  		               AND LIN_REC.OPRE_OID_OPER_RECL = OP_REC.OID_OPER_RECL 		               AND OP_REC.TIOP_OID_TIPO_OPER = T_OP.OID_TIPO_OPER 		               AND OP.OID_OPER = T_OP.ROPE_OID_OPER 		               AND CAB_REC.PAIS_OID_PAIS =  1  		               AND OP.OID_OPER =  1058               		          GROUP BY REG.OID_REGI, 		                   OP.COD_OPER, 		                   PERIODOS.PERIODO_N_3 ) PERIODOS_N_3, ( 		   SELECT *  		   FROM                           (SELECT PE.OID_PERI OID_PERI_N_1, PE.VAL_NOMB_PERI PERIODO_N_1                             FROM CRA_PERIO PE                            WHERE PE.OID_PERI =                                     (SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,2)                                        FROM CRA_PERIO P                                       WHERE P.OID_PERI =  57 )) ,                          (SELECT PE.OID_PERI OID_PERI_N_2, PE.VAL_NOMB_PERI PERIODO_N_2                             FROM CRA_PERIO PE                            WHERE PE.OID_PERI =                                     (SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,3)                                        FROM CRA_PERIO P                                       WHERE P.OID_PERI =  57 )) ,                          (SELECT PE.OID_PERI OID_PERI_N_3, PE.VAL_NOMB_PERI PERIODO_N_3                             FROM CRA_PERIO PE                            WHERE PE.OID_PERI =                                     (SELECT PQ_APL_REP.FN_008_CALC_OID_PER_ANT(P.PAIS_OID_PAIS,P.MARC_OID_MARC,P.CANA_OID_CANA,P.OID_PERI,4)                                        FROM CRA_PERIO P                                       WHERE P.OID_PERI =  57 ))  ) PERIODOS WHERE UNID_RECLA_TOTAL.OID_REGI = PERIODOS_N.OID_REGI(+) 	  			 AND UNID_RECLA_TOTAL.OID_OPER = PERIODOS_N.OID_OPER(+) 				 AND UNID_RECLA_TOTAL.OID_REGI = PERIODOS_N_1.OID_REGI(+) 	  			 AND UNID_RECLA_TOTAL.OID_OPER = PERIODOS_N_1.OID_OPER(+) 				 AND UNID_RECLA_TOTAL.OID_REGI = PERIODOS_N_2.OID_REGI(+) 	  			 AND UNID_RECLA_TOTAL.OID_OPER = PERIODOS_N_2.OID_OPER(+) 				 AND UNID_RECLA_TOTAL.OID_REGI = PERIODOS_N_3.OID_REGI(+) 	  			 AND UNID_RECLA_TOTAL.OID_OPER = PERIODOS_N_3.OID_OPER(+) ORDER BY DES_REGI, OPERACION 			 