 SELECT COD_ZONA, 		DES_ZONA, 	   RECLAMOS, 	   FACTURACION,       TRUNC(DECODE(NVL(FACTURACION,0), 0, 0,(RECLAMOS/FACTURACION)*100), 2) PORCENTAJE	  FROM ( 	SELECT ZONA.COD_ZONA, 			ZONA.DES_ZONA, 		   NVL(COUNT(OP_REC.OID_OPER_RECL), 0) RECLAMOS, 		   ( 	             SELECT COUNT (*)                     FROM FAC_DOCUM_CONTA_CABEC fac, 				    CRA_PERIO PER                     WHERE fac.zzon_oid_zona = zona.oid_zona    		 		  AND FAC.PERD_OID_PERI = PER.OID_PERI          			  AND per.fec_inic >= per_ini.fec_inic 				  AND per.fec_fina  < = per_fin.fec_fina 		     	 	  AND PER.pais_oid_pais =  1  		               AND per.cana_oid_cana =  1 	 				  AND per.marc_oid_marc =  1  		   ) FACTURACION 	FROM REC_CABEC_RECLA CAB_REC, 		 CRA_PERIO PERI, 		 REC_OPERA OP, 		 REC_TIPOS_OPERA TIP_OP, 		 REC_OPERA_RECLA OP_REC, 		 ZON_ZONA ZONA, 	     ( 			 SELECT FEC_INIC 			 FROM CRA_PERIO 			 WHERE OID_PERI =  57 		 		 )PER_INI, 		 ( 			 SELECT FEC_FINA 			 FROM CRA_PERIO 			 WHERE 1 = 1   			  AND OID_PERI = CASE WHEN (-1=  -1 ) THEN			 			  	  		   	 	  	   		   						   ( 																	   		SELECT OID_PERI 																			FROM 																			( 																				 SELECT OID_PERI 																	   			 FROM 																	   			 ( 																				 SELECT * 																				 FROM CRA_PERIO P 																				 WHERE P.PAIS_OID_PAIS =  1 			 			 																					  AND P.CANA_OID_CANA =  1 	 																					  AND P.MARC_OID_MARC =  1  																					  AND FEC_INIC  < = SYSDATE 																					  AND FEC_FINA >= SYSDATE  																				 ) 																				 ORDER BY FEC_FINA DESC 																		)  																		WHERE ROWNUM =1 																	   )  														ELSE 																	   -1  														END 		 )PER_FIN , 		 ZON_TERRI_ADMIN ZTA, 		 ZON_SECCI SEC		 WHERE PERI.FEC_INIC >= PER_INI.FEC_INIC 		  AND PERI.FEC_FINA  < = PER_FIN.FEC_FINA  		  AND CAB_REC.PERD_OID_PERI_RECL = PERI.OID_PERI 	      AND CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 	      AND TIP_OP.ROPE_OID_OPER = OP.OID_OPER 		  AND CAB_REC.ZTAD_OID_TERR_ADMI = ZTA.OID_TERR_ADMI 		  AND ZTA.ZSCC_OID_SECC = SEC.OID_SECC 		  AND SEC.ZZON_OID_ZONA = ZONA.OID_ZONA 	      AND OP_REC.TIOP_OID_TIPO_OPER = TIP_OP.OID_TIPO_OPER 	      AND CAB_REC.PAIS_OID_PAIS = PERI.PAIS_OID_PAIS 		  AND CAB_REC.PAIS_OID_PAIS =  1 			  		  AND PERI.CANA_OID_CANA =  1 	 		  AND PERI.MARC_OID_MARC =  1  		 AND OP.OID_OPER =  1058  											 		   AND TIP_OP.OID_TIPO_OPER = ( '1' )	 				 	GROUP BY ZONA.COD_ZONA, ZONA.DES_ZONA,ZONA.OID_ZONA,per_ini.fec_inic,per_FIN.fec_FINA		   	 ORDER BY RECLAMOS DESC )	   WHERE ROWNUM  < =  23 		  