SELECT  	   COD_CONSULTORA, 	   NOMBRE_CON, 	   RECLAMOS, 	   MONTO, 	   CANTIDAD FROM ( 	SELECT CLI.COD_CLIE as COD_CONSULTORA, 		   CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2 as NOMBRE_CON, 		   COUNT(OP_REC.OID_OPER_RECL) RECLAMOS, 		   SUM(REC_LIN.NUM_UNID_RECL*REC_LIN.VAL_PREC) MONTO, 		   SUM(REC_LIN.NUM_UNID_RECL) CANTIDAD 	FROM REC_CABEC_RECLA CAB_REC, 		 CRA_PERIO PERI, 		 REC_OPERA OP, 		 REC_TIPOS_OPERA T_OP, 		 REC_OPERA_RECLA OP_REC, 		 REC_LINEA_OPERA_RECLA REC_LIN, 		 MAE_CLIEN CLI, 		( 		 SELECT FEC_INIC 		 FROM CRA_PERIO 		 WHERE OID_PERI =  57  		)PER_INI, 		( 		 SELECT FEC_FINA 		 FROM CRA_PERIO 		 WHERE OID_PERI = decode( -1 , -1,	 57 ,  -1 ) 		)PER_FIN	 	WHERE CAB_REC.OID_CABE_RECL = OP_REC.CARE_OID_CABE_RECL 		  AND PERI.FEC_INIC >= PER_INI.FEC_INIC 		  AND PERI.FEC_FINA  < = PER_FIN.FEC_FINA  		  AND OP_REC.TIOP_OID_TIPO_OPER = T_OP.OID_TIPO_OPER 		  AND T_OP.ROPE_OID_OPER = OP.OID_OPER 		  AND OP_REC.PERD_OID_PERI_RECL = PERI.OID_PERI 	      AND OP_REC.OID_OPER_RECL = REC_LIN.OPRE_OID_OPER_RECL 		  AND CLI.OID_CLIE = CAB_REC.CLIE_OID_CLIE 		  AND CAB_REC.PAIS_OID_PAIS =  1  		  AND PERI.MARC_OID_MARC = 1  		  AND PERI.CANA_OID_CANA= 1  		  AND OP.OID_OPER IN ( 1061 ) 		   AND T_OP.OID_TIPO_OPER IN ( 1 )  	GROUP BY CLI.COD_CLIE, 		     CLI.VAL_APE1||' '||CLI.VAL_APE2||' '||CLI.VAL_NOM1||' '||CLI.VAL_NOM2 	ORDER BY RECLAMOS DESC ) WHERE ROWNUM  < =  4  