EGO, 					 CCC_CUENT_CONTA CCC 				WHERE DAT.IND_FACT_CCC = 'F' 					  AND (DAT.VAL_TIPO_ASIE = 'VM' OR DAT.VAL_TIPO_ASIE = 'AB') 					  AND (CCC.COD_CUEN_SAP = '410101001' OR CCC.COD_CUEN_SAP = '410301003' OR CCC.COD_CUEN_SAP = '01') 					  AND NEGO.OID_NEGO  <  > 500 					  AND PER_COR.OID_PERI = DAT.PERI_OID_PERI 					  AND MAR_PRO.OID_MARC_PROD = DAT.MAPR_OID_MARC_PROD 					  AND ACCE.CANA_OID_CANA = CANAL.OID_CANA 					  AND SUBA.ACCE_OID_ACCE = ACCE.OID_ACCE 					  AND SUBA.OID_SBAC = DAT.SBAC_OID_SBAC 					  AND NEGO.OID_NEGO = DAT.NEGO_OID_NEGO 					  AND DAT.CUCO_OID_CUEN_CONT = CCC.OID_CUEN_CONT 					  AND ACCE.IND_ACCE = 'B'    AND TO_DATE(DAT.FEC_CONT) = TO_DATE( '01/01/2006' , 'dd/MM/yyyy' )        AND DAT.VAL_PERI_CONT =  1 	    AND DAT.VAL_EJER_CONT =  1 		    AND PER_COR.OID_PERI =  1                           AND PER_COR.TIPE_OID_TIPO_PERI =  1 	   	  AND NEGO.PAIS_OID_PAIS =  1 	   								 				GROUP BY NEGO.PAIS_OID_PAIS, 					   DAT.SOCI_OID_SOCI, 					   DAT.VAL_TIPO_ASIE, 					   'BELCENTER', 					   SUBA.OID_SBAC, 					   NEGO.OID_NEGO 			) 			UNION ALL  			( 				SELECT NEGO.PAIS_OID_PAIS PAIS, 					   DAT.SOCI_OID_SOCI SOCI, 					   CASE WHEN (DAT.VAL_TIPO_ASIE = 'VM') THEN 					   		'VENTA BRUTA' 					   ELSE 					   	    'DEVOLUCIONES' 					   END AS GRUPO, 					   'CANALES' SUBGRUPO,	 					   CANAL.OID_CANA,		    					   NEGO.OID_NEGO, 					   SUM(NVL(DAT.IMP_DATO_SAP, 0)) IMPORTE 				FROM INT_DATOS_SAPFI DAT, 					 SEG_PERIO_CORPO PER_COR, 				 	 SEG_MARCA_PRODU MAR_PRO, 					 SEG_CANAL CANAL, 					 SEG_ACCES ACCE, 					 SEG_SUBAC SUBA, 					 MAE_NEGOC NEGO, 					 CCC_CUENT_CONTA CCC	  				WHERE DAT.IND_FACT_CCC = 'F' 					  AND (DAT.VAL_TIPO_ASIE = 'VM' OR DAT.VAL_TIPO_ASIE = 'AB') 					  AND (CCC.COD_CUEN_SAP = '410101001' OR CCC.COD_CUEN_SAP = '410301003' OR CCC.COD_CUEN_SAP = '01') 					  AND NEGO.OID_NEGO  <  > 500 					  AND PER_COR.OID_PERI = DAT.PERI_OID_PERI 					  AND MAR_PRO.OID_MARC_PROD = DAT.MAPR_OID_MARC_PROD 					  AND ACCE.CANA_OID_CANA = CANAL.OID_CANA 					  AND SUBA.ACCE_OID_ACCE = ACCE.OID_ACCE 					  AND SUBA.OID_SBAC = DAT.SBAC_OID_SBAC 					  AND NEGO.OID_NEGO = DAT.NEGO_OID_NEGO 					  AND DAT.CUCO_OID_CUEN_CONT = CCC.OID_CUEN_CONT 					  AND ACCE.IND_ACCE = 'O' 	   AND TO_DATE(DAT.FEC_CONT) = TO_DATE( '01/01/2006' , 'dd/MM/yyyy' )     					   AND DAT.VAL_PERI_CONT =  1 	  	 					  AND DAT.VAL_EJER_CONT =  1 		   	             AND PER_COR.OID_PERI =  1       					                      AND PER_COR.TIPE_OID_TIPO_PERI =  1 	   					  AND NEGO.PAIS_OID_PAIS =  1 	 				GROUP BY NEGO.PAIS_OID_PAIS, 					   DAT.SOCI_OID_SOCI, 					   DAT.VAL_TIPO_ASIE, 					   'CANALES', 					   CANAL.OID_CANA, 					   NEGO.OID_NEGO 			) 			UNION ALL 			( 				SELECT PAIS, 					   SOCI, 					   'VENTA NETA', 					   'MARCA' AS SUBGRUPO, 					   MARCA, 					   OID_NEGO, 					   SUM(IMPORTE) IMPORTE 				FROM 					( 						SELECT NEGO.PAIS_OID_PAIS PAIS, 							   DAT.SOCI_OID_SOCI SOCI, 							   MAR_PRO.OID_MARC_PROD MARCA, 							   NEGO.OID_NEGO, 	           			   	   CASE WHEN ( DAT.VAL_TIPO_ASIE = 'VM') THEN 								   SUM(NVL(DAT.IMP_DATO_SAP, 0)) 							   ELSE 								   SUM(NVL(DAT.IMP_DATO_SAP, 0))*-1 							   END AS IMPORTE 						FROM INT_DATOS_SAPFI DAT, 							 SEG_PERIO_CORPO PER_COR, 						 	 SEG_MARCA_PRODU MAR_PRO, 							 SEG_CANAL CANAL, 							 SEG_ACCES ACCE, 							 SEG_SUBAC SUBA, 							 MAE_NEGOC NEGO, 							 CCC_CUENT_CONTA CCC 						WHERE DAT.IND_FACT_CCC = 'F' 							  AND (CCC.COD_CUEN_SAP = '410101001' OR CCC.COD_CUEN_SAP = '410301003' OR CCC.COD_CUEN_SAP = '01') 							  AND NEGO.OID_NEGO  <  > 500 							  AND (DAT.VAL_TIPO_ASIE = 'VM' OR DAT.VAL_TIPO_ASIE = 'AB') 							  AND PER_COR.OID_PERI = DAT.PERI_OID_PERI 							  AND MAR_PRO.OID_MARC_PROD = DAT.MAPR_OID_MARC_PROD 							  AND ACCE.CANA_OID_CANA = CANAL.OID_CANA 							  AND SUBA.ACCE_OID_ACCE = ACCE.OID_ACCE 							  AND SUBA.OID_SBAC = DAT.SBAC_OID_SBAC 							  AND NEGO.OID_NEGO = DAT.NEGO_OID_NEGO 							  AND DAT.CUCO_OID_CUEN_CONT = CCC.OID_CUEN_CONT 							  AND (ACCE.IND_ACCE = 'G' OR ACCE.IND_ACCE = 'C')        AND TO_DATE(DAT.FEC_CONT) = TO_DATE( '01/01/2006' , 'dd/MM/yyyy' )     	  AND DAT.VAL_PERI_CONT =  1 	  	 	  AND DAT.VAL_EJER_CONT =  1 		   	 AND PER_COR.OID_PERI =  1                        AND PER_COR.TIPE_OID_TIPO_PERI =  1 	      AND NEGO.PAIS_OID_PAIS =  1 	 						GROUP BY NEGO.PAIS_OID_PAIS, 							   DAT.SOCI_OID_SOCI, 							   DAT.VAL_TIPO_ASIE, 							   MAR_PRO.OID_MARC_PROD, 							   NEGO.OID_NEGO 					) 					GROUP BY PAIS, 							 SOCI, 							 'VENTA NETA', 							 'MARCA', 							 MARCA, 							 OID_NEGO 				) 				UNION ALL 				(			 					SELECT PAIS, 						   SOCI, 						   'VENTA NETA', 						   'BELCENTER' AS SUBGRUPO, 						   BELCENTER, 						   OID_NEGO, 						   SUM(IMPORTE) IMPORTE 					FROM 				    	( 						SELECT NEGO.PAIS_OID_PAIS PAIS, 							   DAT.SOCI_OID_SOCI SOCI, 							   SUBA.OID_SBAC BELCENTER, 							   NEGO.OID_NEGO, 							   CASE WHEN (DAT.VAL_TIPO_ASIE = 'VM') THEN 							   	   SUM(NVL(DAT.IMP_DATO_SAP, 0)) 							   ELSE 							   	   SUM(NVL(DAT.IMP_DATO_SAP, 0))*-1 						 	   END IMPORTE 						FROM INT_DATOS_SAPFI DAT, 							 SEG_PERIO_CORPO PER_COR, 						 	 SEG_MARCA_PRODU MAR_PRO, 							 SEG_CANAL CANAL, 							 SEG_ACCES ACCE, 							 SEG_SUBAC SUBA, 							 MAE_NEGOC NEGO, 							 CCC_CUENT_CONTA CCC 						WHERE DAT.IND_FACT_CCC = 'F' 							  AND (DAT.VAL_TIPO_ASIE = 'VM' OR DAT.VAL_TIPO_ASIE = 'AB') 							  AND (CCC.COD_CUEN_SAP = '410101001' OR CCC.COD_CUEN_SAP = '410301003' OR CCC.COD_CUEN_SAP = '01') 							  AND NEGO.OID_NEGO  <  > 500 							  AND PER_COR.OID_PERI = DAT.PERI_OID_PERI 							  AND MAR_PRO.OID_MARC_PROD = DAT.MAPR_OID_MARC_PROD 							  AND ACCE.CANA_OID_CANA = CANAL.OID_CANA 							  AND SUBA.ACCE_OID_ACCE = ACCE.OID_ACCE 							  AND SUBA.OID_SBAC = DAT.SBAC_OID_SBAC 							  AND NEGO.OID_NEGO = DAT.NEGO_OID_NEGO 							  AND DAT.CUCO_OID_CUEN_CONT = CCC.OID_CUEN_CONT 							  AND ACCE.IND_ACCE = 'B'    AND TO_DATE(DAT.FEC_CONT) = TO_DATE( '01/01/2006' , 'dd/MM/yyyy' )       AND DAT.VAL_PERI_CONT =  1 	  	   AND DAT.VAL_EJER_CONT =  1 		    AND PER_COR.OID_PERI =  1                           AND PER_COR.TIPE_OID_TIPO_PERI =  1 	      AND NEGO.PAIS_OID_PAIS =  1  						GROUP BY NEGO.PAIS_OID_PAIS, 							   DAT.SOCI_OID_SOCI, 								   DAT.VAL_TIPO_ASIE, 								   SUBA.OID_SBAC, 								   NEGO.OID_NEGO 						) 				GROUP BY PAIS, 						 SOCI, 						 'VENTA NETA', 						 'BELCENTER', 						 BELCENTER, 						 OID_NEGO 	    	) 			UNION ALL  			( 				SELECT PAIS, 					   SOCI, 					   'VENTA NETA', 					   'CANALES' AS SUBGRUPO, 					   OID_CANA, 					   OID_NEGO, 					   SUM(IMPORTE) IMPORTE 				FROM 			    	( 	   				SELECT NEGO.PAIS_OID_PAIS PAIS, 						   DAT.SOCI_OID_SOCI SOCI, 						   CANAL.OID_CANA,		    						   NEGO.OID_NEGO, 						   CASE WHEN (DAT.VAL_TIPO_ASIE = 'VM') THEN 						   		SUM(NVL(DAT.IMP_DATO_SAP, 0)) 						   ELSE 						   	   SUM(NVL(DAT.IMP_DATO_SAP, 0))*-1 						   END AS IMPORTE 					FROM INT_DATOS_SAPFI DAT, 						 SEG_PERIO_CORPO PER_COR, 					 	 SEG_MARCA_PRODU MAR_PRO, 						 SEG_CANAL CANAL, 						 SEG_ACCES ACCE, 						 SEG_SUBAC SUBA, 						 MAE_NEGOC NEGO, 						 CCC_CUENT_CONTA CCC	  					WHERE DAT.IND_FACT_CCC = 'F' 						  AND (DAT.VAL_TIPO_ASIE = 'VM' OR DAT.VAL_TIPO_ASIE = 'AB') 						  AND (CCC.COD_CUEN_SAP = '410101001' OR CCC.COD_CUEN_SAP = '410301003' OR CCC.COD_CUEN_SAP = '01') 						  AND NEGO.OID_NEGO  <  > 500 						  AND PER_COR.OID_PERI = DAT.PERI_OID_PERI 						  AND MAR_PRO.OID_MARC_PROD = DAT.MAPR_OID_MARC_PROD 						  AND ACCE.CANA_OID_CANA = CANAL.OID_CANA 						  AND SUBA.ACCE_OID_ACCE = ACCE.OID_ACCE 						  AND SUBA.OID_SBAC = DAT.SBAC_OID_SBAC 						  AND NEGO.OID_NEGO = DAT.NEGO_OID_NEGO 						  AND DAT.CUCO_OID_CUEN_CONT = CCC.OID_CUEN_CONT 						  AND ACCE.IND_ACCE = 'O'     AND TO_DATE(DAT.FEC_CONT) = TO_DATE( '01/01/2006' , 'dd/MM/yyyy' )       AND DAT.VAL_PERI_CONT =  1 	  	  