OID_NEGO 										  AND DAT.CUCO_OID_CUEN_CONT = CCC.OID_CUEN_CONT 										  AND ACCE.IND_ACCE = 'O' 	   AND TO_DATE(DAT.FEC_CONT) = TO_DATE( '01/01/2006' , 'dd/MM/yyyy' )        	  AND DAT.VAL_PERI_CONT =  1 	  	    	  AND DAT.VAL_EJER_CONT =  1 		   	 AND PER_COR.OID_PERI =  1                 AND PER_COR.TIPE_OID_TIPO_PERI =  1 	        AND NEGO.PAIS_OID_PAIS =  1 	 									GROUP BY NEGO.PAIS_OID_PAIS, 										   DAT.SOCI_OID_SOCI, 										   DAT.VAL_TIPO_ASIE, 										   CANAL.OID_CANA, 										   NEGO.OID_NEGO,	    										   CCC.COD_CUEN_SAP 								)	   								GROUP BY PAIS, 										 SOCI, 										 GRUPO, 										 'CANALES', 										 OID_CANA 							)		 			  			) 					)		 		GROUP BY PAIS, 			   SOCI, 			   GRUPO, 			   SUBGRUPO, 			   MARCA 	 ) ) TOTALES, ( SELECT GEN.VAL_OID, GEN.VAL_I18N FROM V_GEN_I18N_SICC GEN WHERE GEN.ATTR_ENTI = 'SEG_PAIS' 	  AND GEN.IDIO_OID_IDIO =  1 	 ) GEN_PAIS, ( SELECT GEN.OID_SOCI VAL_OID, GEN.VAL_DENO VAL_I18N FROM SEG_SOCIE GEN ) GEN_SOCI, MAE_NEGOC NEGO, ( SELECT GEN.VAL_OID, GEN.VAL_I18N FROM V_GEN_I18N_SICC GEN WHERE GEN.ATTR_ENTI = 'MAE_NEGOC' 	  AND GEN.IDIO_OID_IDIO =  1 	 ) GEN_NEGO WHERE DETALLE.PAIS = TOTALES.PAIS(+)			 	  AND DETALLE.SOCI = TOTALES.SOCI(+) 	  AND DETALLE.GRUPO = TOTALES.GRUPO(+)	 	  AND DETALLE.SUBGRUPO = TOTALES.SUBGRUPO(+) 	  AND DETALLE.OID_MARC_PROD = TOTALES.MARCA(+) 	  AND DETALLE.PAIS = GEN_PAIS.VAL_OID(+) 	  AND DETALLE.SOCI = GEN_SOCI.VAL_OID 	  AND DETALLE.OID_NEGO = NEGO.OID_NEGO 	  AND DETALLE.OID_NEGO = GEN_NEGO.VAL_OID(+)) 