SELECT GEN_PAIS.VAL_I18N PAIS, 	   CLI.COD_CLIE CODIGO, 	   PERI.VAL_NOMB_PERI CAMP, 	   CUP_DET.NUM_CUPO CUOTA, 	   BANCO.DES_BANC BANCO, 	   MOV_BAN.NUM_LOTE PAGO, 	   CUP_DET.FEC_MOVI_BANC FECHA_VALOR, 	   CUP_TRA.FEC_DOCU FECHA_PROCESO, 	   CUP_DET.IMP_DETA IMPORTE  FROM CCC_CUPON_TRAMI_DEPUR CUP_TRA, 	 CCC_DETAL_CUPON_TRAMI_DEPUR CUP_DET, 	 CCC_SITUA_CUPON SIT_CUP, 	 CCC_BANCO BANCO, 	 MAE_CLIEN CLI, 	 CRA_PERIO PERI, 	 CCC_MOVIM_BANCA MOV_BAN, 	 ( 	 SELECT GEN.VAL_OID, GEN.VAL_I18N 	 FROM V_GEN_I18N_SICC GEN 	 WHERE GEN.ATTR_ENTI = 'SEG_PAIS' 	 	   AND GEN.IDIO_OID_IDIO =  1 		 	 ) GEN_PAIS 	  WHERE CUP_TRA.OID_CUPO_TRAM_DEPU = CUP_DET.CTDE_OID_CUPO_TRAM_DEPU 	  AND CUP_DET.SICU_OID_SITU_CUPO = SIT_CUP.OID_SITU_CUPO 	  AND (SIT_CUP.COD_SITU_CUPO = 'T' OR SIT_CUP.COD_SITU_CUPO = 'D') 	  AND BANCO.OID_BANC = CUP_DET.CBAN_OID_BANC 	  AND CUP_TRA.PAIS_OID_PAIS = GEN_PAIS.VAL_OID(+) 	  AND CLI.OID_CLIE = CUP_DET.CLIE_OID_CLIE 	  AND CUP_DET.PERI_OID_PERI = PERI.OID_PERI 	  AND CUP_DET.CMBA_OID_MOVI_BANC = MOV_BAN.OID_MOVI_BANC 	  AND CUP_TRA.PAIS_OID_PAIS =  1 	  	 	  AND TO_DATE(CUP_TRA.FEC_DOCU)  > = TO_DATE( '23/01/2000' , 'dd/mm/yyyy' )    	  AND TO_DATE(CUP_TRA.FEC_DOCU)  < = TO_DATE( '22/01/2050' , 'dd/mm/yyyy' )   	  AND TO_DATE(CUP_DET.FEC_MOVI_BANC)  > = TO_DATE( '22/01/2003' , 'dd/mm/yyyy' )    	  AND TO_DATE(CUP_DET.FEC_MOVI_BANC)  < = TO_DATE( '22/01/2051' , 'dd/mm/yyyy' )    	    AND CUP_DET.CBAN_OID_BANC IN ( 1 )   	        ORDER BY PAIS, 	  	  /* CRITERIODEORDENAMIENTO*/ ,  		 BANCO