SELECT  	   distinct PAIS.COD_PAIS, GEN_PAIS.VAL_I18N,  	   MARC.DES_MARC, GEN_CAN.VAL_I18N, 	   SUBG.COD_SUBG_VENT, SUBG.DES_SUBG_VENT,  	   REG.COD_REGI, REG.DES_REGI,  	   ZON.COD_ZONA, ZON.DES_ZONA,  	   SEC.COD_SECC, TER.COD_TERR, SEC.DES_SECCI, 	   GEO.DES_GEOG, GEO.ORDE_1, GEO.ORDE_2, GEO.ORDE_3, GEO.ORDE_4, GEO.ORDE_5, GEO.ORDE_6, GEO.ORDE_7, GEO.ORDE_8, GEO.ORDE_9, 	   (CLISUBG.VAL_APE1 || ' ' || CLISUBG.VAL_APE2 || ' ' || DECODE(CLISUBG.VAL_APEL_CASA,NULL,'','de ' || CLISUBG.VAL_APEL_CASA) || ' ' || CLISUBG.VAL_NOM1 || ' ' || CLISUBG.VAL_NOM2) as RESP_SUBG, 	   (CLIREG.VAL_APE1 || ' ' || CLIREG.VAL_APE2 || ' ' || DECODE(CLIREG.VAL_APEL_CASA,NULL,'','de ' || CLIREG.VAL_APEL_CASA) || ' ' || CLIREG.VAL_NOM1 || ' ' || CLIREG.VAL_NOM2) as RESP_REG, 	   (CLIZON.VAL_APE1 || ' ' || CLIZON.VAL_APE2 || ' ' || DECODE(CLIZON.VAL_APEL_CASA,NULL,'','de ' || CLIZON.VAL_APEL_CASA) || ' ' || CLIZON.VAL_NOM1 || ' ' || CLIZON.VAL_NOM2) as RESP_ZON, 	   (CLISEC.VAL_APE1 || ' ' || CLISEC.VAL_APE2 || ' ' || DECODE(CLISEC.VAL_APEL_CASA,NULL,'','de ' || CLISEC.VAL_APEL_CASA) || ' ' || CLISEC.VAL_NOM1 || ' ' || CLISEC.VAL_NOM2) as RESP_SEC, 	   decode(geo.cod_unid_geog,null,0,1) as CANTIDAD FROM  	 SEG_MARCA MARC, 	 SEG_PAIS PAIS, 	 ZON_SUB_GEREN_VENTA SUBG,  	 ZON_REGIO REG,  	 ZON_ZONA ZON, 	 ZON_SECCI SEC, 	 ZON_TERRI_ADMIN TERSEC, 	 ZON_TERRI TER, 	 ZON_VALOR_ESTRU_GEOPO GEO, 	 MAE_CLIEN CLISUBG, 	 MAE_CLIEN CLIREG, 	 MAE_CLIEN CLIZON, 	 MAE_CLIEN CLISEC, 	 ( 	  select val_oid, val_i18n 	  from v_gen_i18n_sicc 	  where attr_enti = 'SEG_PAIS' 	  		and idio_oid_idio =  1  	 )gen_pais, 	 ( 	  select val_oid, val_i18n 	  from v_gen_i18n_sicc 	  where attr_enti = 'SEG_CANAL' 	  		and idio_oid_idio =  1  	 )gen_can WHERE  	  	  SUBG.OID_SUBG_VENT = REG.ZSGV_OID_SUBG_VENT(+) 	  AND REG.OID_REGI = ZON.ZORG_OID_REGI(+) 	  AND ZON.OID_ZONA = SEC.ZZON_OID_ZONA(+) 	  AND SEC.OID_SECC = TERSEC.ZSCC_OID_SECC(+) 	  AND TERSEC.TERR_OID_TERR = TER.OID_TERR(+) 	  AND TER.VEPO_OID_VALO_ESTR_GEOP = GEO.OID_VALO_ESTR_GEOP(+) 	  AND PAIS.OID_PAIS = SUBG.PAIS_OID_PAIS 	  AND MARC.OID_MARC = SUBG.MARC_OID_MARC 	  AND SUBG.CLIE_OID_CLIE = CLISUBG.OID_CLIE(+) 	  AND REG.CLIE_OID_CLIE = CLIREG.OID_CLIE(+) 	  AND ZON.CLIE_OID_CLIE = CLIZON.OID_CLIE(+) 	  AND SEC.CLIE_OID_CLIE = CLISEC.OID_CLIE(+) 	  and subg.cana_oid_cana = gen_can.val_oid(+) 	  and subg.PAIS_OID_PAIS = gen_pais.val_oid(+) 	  AND SUBG.MARC_OID_MARC =  1   	  AND SUBG.CANA_OID_CANA =  1  	  AND SUBG.PAIS_OID_PAIS =  1   	  AND SUBG.IND_BORR = 0 ORDER BY 	  SUBG.COD_SUBG_VENT,  	  REG.COD_REGI,  	  ZON.COD_ZONA,  	  SEC.COD_SECC,  	  TER.COD_TERR, 	  GEO.DES_GEOG	  