SELECT           gen_pais.pais,          gen_soc.sociedad,          reg.VAL_SERI_DOCU_REFE,          reg.VAL_NUME_DOCU_REFE,          reg.IMP_IMPU,          gen_acc.acceso as sistema_emision,          gen_subac.subacceso as punto_emision,          t_doc.COD_TIPO_DOCU as tipo_dcmto,          reg.VAL_SERI_DOCU_LEGA as serie,          min(reg.NUM_DOCU_CONT_INTE) as doc_inicial,          max(reg.NUM_DOCU_CONT_INTE) as doc_final,          max(reg.NUM_DOCU_CONT_INTE) - min(reg.NUM_DOCU_CONT_INTE) as cant_doc,          sum(reg.IND_ESTA) as cant_anul,          max(reg.VAL_NUME_DOCU_LEGA) - min(reg.VAL_NUME_DOCU_LEGA) - count(reg.IND_ESTA) as cant_doc_no_regis,           sum(reg.VAL_BASE_IMPO) as base_imponible,          sum(reg.VAL_DESC) as descuento,          sum(reg.VAL_BASE_IMPO_NETO) as base_impo_neto,          sum(reg.IMP_IMPU) as igv,          sum(reg.IMP_TOTA) as importe_total,          sum(reg.IND_FACT_GRAT) as tg from fac_regis_unico_venta reg,        fac_tipo_docum t_doc,        fac_tipo_docum t_doc_r,        seg_subac subac,        seg_acces acc,        (         select val_oid, val_i18n pais         from v_gen_i18n_sicc         where attr_enti = 'SEG_PAIS'                   and idio_oid_idio =  1         )gen_pais,        (         select oid_soci val_oid, val_deno sociedad         from seg_socie        )gen_soc,        (         select val_oid, val_i18n acceso         from v_gen_i18n_sicc         where attr_enti ='SEG_ACCES'                and idio_oid_idio =  1         )gen_acc,        (         select val_oid, val_i18n subacceso         from v_gen_i18n_sicc         where attr_enti ='SEG_SUBAC'                and idio_oid_idio =  1         )gen_subac where          reg.SBAC_OID_SBAC = subac.OID_SBAC         and reg.PAIS_OID_PAIS = gen_pais.val_oid(+)         and reg.TIDO_OID_TIPO_DOCU = t_doc.OID_TIPO_DOCU(+)         and reg.TIDO_TIPO_DOCU_REFE = t_doc_r.OID_TIPO_DOCU(+)         and reg.SOCI_OID_SOCI = gen_soc.val_oid         and subac.ACCE_OID_ACCE = acc.oid_acce         and reg.SBAC_OID_SBAC in ( 1 )          and reg.PAIS_OID_PAIS =  1        	  and reg.TIDO_OID_TIPO_DOCU in ( 1 )          and subac.ACCE_OID_ACCE =  1          and acc.CANA_OID_CANA =  1          and TO_DATE(reg.FEC_EMIS) BETWEEN to_date( '01/02/2000' ,  'dd/mm/yyyy' )  and to_date( '11/02/2030' , 'dd/mm/yyyy' )         and acc.oid_acce = gen_acc.val_oid         and subac.OID_SBAC = gen_subac.val_oid       	 and reg.VAL_SERI_DOCU_LEGA LIKE  '1'         group by gen_pais.pais,          gen_soc.sociedad,          reg.VAL_SERI_DOCU_REFE,          reg.VAL_NUME_DOCU_REFE,          reg.IMP_IMPU,          gen_acc.acceso,          gen_subac.subacceso,          t_doc.COD_TIPO_DOCU,          reg.VAL_SERI_DOCU_LEGA 