  SELECT I18PAIS.VAL_I18N AS PAIS, MARC.DES_MARC AS MARCA, I18CANAL.VAL_I18N AS CANAL, I18ACCES.VAL_I18N AS ACCESO, VAL_NOMB_PERI, 	   LPAD(VAL_CODI_VENT,6,'0') AS VAL_CODI_VENT, des_tipo_estr, num_ofer, val_text_brev, des_cata, num_pagi_cata, val_posi_pagi, cod_tipo_ofer, imp_prec_cata, 1 AS CANTIDAD FROM pre_ofert_detal ofedet,        pre_ofert ofe,        pre_tipo_ofert tipof,        pre_tipo_estra tipes,        pre_catal cat,        pre_estra estra,        SEG_MARCA MARC,        PRE_CATAL_ORDEN CATORD,        PRE_MATRI_FACTU_CABEC MFC, 	   CRA_PERIO PERI, 	   ( 	   SELECT V.VAL_OID, V.VAL_I18N  	   FROM V_GEN_I18N_SICC V  	   WHERE V.ATTR_ENTI = 'SEG_PAIS' 	   AND V.IDIO_OID_IDIO =   1 ) I18PAIS, 	   ( 	   SELECT V.VAL_OID, V.VAL_I18N  	   FROM V_GEN_I18N_SICC V  	   WHERE V.ATTR_ENTI = 'SEG_CANAL'  	   AND V.IDIO_OID_IDIO =   1 ) I18CANAL, 	   ( 	   SELECT V.VAL_OID, V.VAL_I18N  	   FROM V_GEN_I18N_SICC V  	   WHERE V.ATTR_ENTI = 'SEG_ACCES'  	   AND V.IDIO_OID_IDIO =   1 ) I18ACCES WHERE ofedet.ofer_oid_ofer = ofe.oid_ofer    AND ofedet.ocat_oid_catal = cat.oid_cata    AND ofedet.tofe_oid_tipo_ofer = tipof.oid_tipo_ofer    AND estra.oid_estr = ofe.coes_oid_estr    AND estra.ties_oid_tipo_estr = tipes.oid_tipo_estr    AND CAT.OID_CATA = CATORD.OCAT_OID_CATA    AND CATORD.MARC_OID_MARC = MARC.OID_MARC    AND OFE.MFCA_OID_CABE = MFC.OID_CABE    AND MFC.PERD_OID_PERI = PERI.OID_PERI    AND  I18PAIS.VAL_OID (+)= CAT.PAIS_OID_PAIS    AND I18CANAL.VAL_OID (+)  = CATORD.CANA_OID_CANA    AND I18ACCES.VAL_OID (+) = OFE.ACCE_OID_ACCE    AND MFC.PERD_OID_PERI =  54     AND CAT.PAIS_OID_PAIS =  1     AND CATORD.MARC_OID_MARC =  1     AND CATORD.CANA_OID_CANA =  1       AND OFE.ACCE_OID_ACCE =  1        AND CAT.OID_CATA in ( 1 )      AND ofedet.ind_impr_gp =  1      AND CASE WHEN  1  = 2 THEN VAL_CODI_VENT ELSE '0' END  < = CASE WHEN  1  = 2 THEN '95000' ELSE '1' END       AND CASE WHEN  1  = 3 THEN VAL_CODI_VENT  ELSE '1' END  >  CASE WHEN  1  = 3 THEN '95000' ELSE '0' END ORDER BY   OFE.NUM_ORDE 