        document.all["ava1Div"].style.visibility='visible';
        document.all["separaDiv"].style.visibility='visible';
        document.all["btnSiguienteDiv"].style.visibility='visible';
        document.all["btnAnadirDiv"].style.visibility='visible';
		eval(ON_RSZ);
