var pantalla = '';

function setComboDefault(hid, cb){
	 iSeleccionado = new Array(); 
	 iSeleccionado[0] = get(hid);
	 set(cb,iSeleccionado);
}

// Se llama para limpiar los campos desde la pantalla en el modo modifcar
function CleanModificar() {
	//Hay que setear los valores anteriores
	set('frmMateriales.rdbLote',get('frmMateriales.hidLote'));
	setComboDefault('frmMateriales.hidUdDeMedidaDimensiones', 'frmMateriales.cbUdDeMedidaDimensiones');
