function inicializacion() {
	//Cargo la pantalla en la que se encuentra
	pantalla = get('frmMateriales.hidPantalla');

	cargarBarra(document.all.barra.value);
	cargarValoresPorDefecto();
	mostrarCodigoError();
	//Si es caso de uso de modificar, hay que deshabilitar los campos que no sean modificables ....
	if ( pantalla == 'modificar' ) {
		deshabilitarControlesModificar();
		if (get('frmMateriales.hidLote') == 'SI') {
			document.all.rdbLote[0].focus();
		}
		else {
			document.all.rdbLote[1].focus();
		}
		setTimeout('focaliza(\'frmMateriales.rdbLote\',\'\',0)', 100);
	}
	else {
		focaliza ('frmMateriales.txtCodigoSAP', '');
	}
}

function deshabilitarControles() {
	//Se deshabilitan los controles para modificar
	deshabilitarControlesModificar();
	//Y los que son modificables
	document.all.rdbLote[0].disabled = true;
	document.all.rdbLote[1].disabled = true;
	accion('frmMateriales.cbUdDeMedidaDimensiones','.disabled=true');
	accion('frmMateriales.cbUnidadMedidaPeso','.disabled=true');
	accion('frmMateriales.txtUdDentroPedido','.disabled=true');
	accion('frmMateriales.txtUnidadesPorCaja','.disabled=true');
	accion('frmMateriales.cbUnidadMedidaVenta','.disabled=true');
	accion('frmMateriales.txtCosteEstandar','.disabled=true');
	accion('frmMateriales.txtPrecioPosicionamiento','.disabled=true');
	document.all.rdbSituacion[0].disabled=true;
	document.all.rdbSituacion[1].disabled=true;
	accion('frmMateriales.cbEstatusDelProducto','.disabled=true');
	document.all.rdbIndicadorDentroDeCaja[0].disabled=true;
	document.all.rdbIndicadorDentroDeCaja[1].disabled=true;
	accion('frmMateriales.rdbSituacion','.disabled=true');
	accion('frmMateriales.txtCodigoPagoFraccionado','.disabled=true');
	accion('frmMateriales.txtCodigoImpuesto','.disabled=true');
	//Se deshabilitan los controles
}

