function buscar() {
	if ( ValidaCamposBuscar() ) {
		set ('frmMateriales.accion', 'buscar');
		enviarFormulario();
	}
}

function validarCaracteres(valor,permitidos) {
	var c;
	for(var i=0;i<valor.length;i++) {
		c=valor.charAt(i);
		if (permitidos.indexOf(c)==-1) {
			return false;}
	 }
	return true;
}

/* Parametros:
					 -Valor: el valor a validar
					 -ent: cantidad de la parte entera
					 -dec : cantidad de la parte decimal
					 -min: rango minimo
					 -max: rango maximo
					 -obligatorio: si el campo viene */
function ValidarNumerico( nombre, requerido, error, ent, dec, min, max ) {
	var variable = get(nombre).replace(",",".");
	if ( ( ! requerido ) && ( variable.length == 0 ) )
		return true;	
	//Ahora verifico que sea un numero valido
	if ( ! validarCaracteres(variable,'0123456789. ')) {
		GestionarMensaje ( '2455', null, null, null);
		focaliza(nombre,'');
		return false;
	}
	var resultado = fValidarFloat(variable, ent, dec, false, min, max, requerido);
	if  ( resultado != "OK" ) {
		GestionarMensaje ( error, null, null, null);
		focaliza(nombre,'');
		return false;
	}
	else {
		set(nombre,variable);
		return true;
	}
}

/* Parametros:
			 -nombre del componente a validar
			 -lon: la longitud maxima
			 -min : menor valor permitido
			 -max : maximo valor permitido
			 -error : codigo de error a devolver
			 -requerido : define si el campo es requerido o no */
function ValidarNumericoEntero( nombre, requerido, error, min, max, lon ) {
	var variable = get(nombre);
	if ( ( ! requerido ) && ( variable.length == 0 ) )
		return true;	
	//Ahora verifico que sea un numero valido		
	var resultado = 	fValidarNumero(variable, lon, min, max)
	if  ( resultado != "OK" ) {
		GestionarMensaje ( error, null, null, null);
		focaliza(nombre,'');
		return false;
	}
	else 
		return true;
}

function ValidarRequeridoCombo( nombre, requerido, error ) { 
	return fValidarCOMBO(nombre);
}

//Falta ver como se focaliza el radio boton
function ValidarRequeridoRadio( nombre, requerido, error ) { 
	var variable = get(nombre);
	if ( requerido && ( variable.length == 0 ) ) {
		GestionarMensaje ( error, null, null, null);
		return false;
	}
	else
		return true;
}

function ValidarRequerido( nombre, requerido, error ) { 
	var variable = get(nombre);
	if ( requerido && ( variable.length == 0 ) ) {
		GestionarMensaje ( error, null, null, null);
		return false;
	}
	else
		return true;
}

function ValidaCamposModificar() {

	if ( ! ValidarRequeridoRadio( 'frmMateriales.rdbLote', true, 2513 )  )
		return false;

	if ( ! ValidarRequeridoCombo( 'frmMateriales.cbUdDeMedidaDimensiones', true, 2513 )  )
		return false;

	if ( ! ValidarRequeridoCombo( 'frmMateriales.cbUnidadMedidaPeso', true, 2513 )  )
		return false;

	if ( !  ValidarNumericoEntero( 'frmMateriales.txtUdDentroPedido', false, 2455, 0, 999, 3 ) )
		return false;

	if ( !  ValidarNumericoEntero( 'frmMateriales.txtUnidadesPorCaja', false, 2455, 0, 99999, 5 ) )
		return false;

	if ( ! ValidarRequeridoCombo( 'frmMateriales.cbUnidadMedidaVenta', true, 2513 )  )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtCosteEstandar', false, 2510, 14, 2, 0, 99999999999999.99) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtPrecioPosicionamiento', false, 2510, 14, 2, 0, 99999999999999.99) )
		return false;

	//Situacion es nullable, no se valida por ser radio

	//Indicador dentro de caja,  es nullable, no se valida por ser un radio

	//Codigo Pago Fraccionado, es un txt y nullable, no se valida

	//Codigo impuesto, es un txt y nullable, no se valida

	return true;
}

function ValidaCamposBuscar() {
	if ( !  ValidarNumerico( 'frmMateriales.txtAlto', false, 2511, 15, 5, 0, 99999999999999.99999) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtLargo', false, 2511, 15, 5, 0, 99999999999999.99999) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtAncho', false, 2511, 15, 5, 0, 99999999999999.99999) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtPeso', false, 2511, 5, 5, 0, 99999.99999) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtVolumen', false, 2512, 15, 5, 0, 99999999999999.99999) )
		return false;

	if ( !  ValidarNumericoEntero( 'frmMateriales.txtUdDentroPedido', false, 2455, 0, 999, 3 ) )
		return false;

	if ( !  ValidarNumericoEntero( 'frmMateriales.txtUnidadesPorCaja', false, 2455, 0, 99999, 5 ) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtCosteEstandar', false, 2510, 14, 2, 0, 99999999999999.99) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtPrecioPosicionamiento', false, 2510, 14, 2, 0, 99999999999999.99) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtPrecioCatalogo', false, 2510, 14, 2, 0, 99999999999999.99) )
		return false;

	if ( !  ValidarNumerico( 'frmMateriales.txtPrecioContable', false, 2510, 14, 2, 0, 99999999999999.99) )
		return false;
	return true;
}

function modificar() {
  if (lstResultado.seleccion.longitud == 1) {
	  var posicion = obtenerPosicionLista (lstResultado.getSeleccion(), lstResultado );
	  set ('frmMateriales.elementosSeleccionados', lstResultado.getSeleccion() );

	  myString = new String(lstResultado.datos.ij(posicion,0));
	  splitString = myString.split('--');
	  if ( splitString.length != 0 ) {

			set('frmMateriales.hidCodigoSAP', splitString[0] );
			set('frmMateriales.hidCodigoAntiguo', splitString[1] );
			set('frmMateriales.hidDescripcionSAP', splitString[2] );
			set('frmMateriales.hidDescripcionCorta', splitString[3] );
			set('frmMateriales.hidLote', splitString[4] );
			set('frmMateriales.hidTipo', splitString[5] );
			set('frmMateriales.hidMarca', splitString[6] );
			set('frmMateriales.hidUnidadDeNegocio', splitString[7] );
			set('frmMateriales.hidNegocio', splitString[8] );
			set('frmMateriales.hidGenerico', splitString[9] );
			set('frmMateriales.hidSupergenerico', splitString[10] );
			set('frmMateriales.hidJerarquia1', splitString[11] );
			set('frmMateriales.hidJerarquia2', splitString[12] );
			set('frmMateriales.hidJerarquia3', splitString[13] );
			set('frmMateriales.hidLinea', splitString[14] );
			set('frmMateriales.hidAtributo1', splitString[15] );
			set('frmMateriales.hidAtributo2', splitString[16] );
			set('frmMateriales.hidAtributo3', splitString[17] );
			set('frmMateriales.hidAlto', splitString[18] );
			set('frmMateriales.hidLargo', splitString[19] );
			set('frmMateriales.hidAncho', splitString[20] );
			set('frmMateriales.hidUdDeMedidaDimensiones', splitString[21] );
			set('frmMateriales.hidVolumen', splitString[22] );
			set('frmMateriales.hidPeso', splitString[23] );
			set('frmMateriales.hidUdMedidaPeso', splitString[24] );
			set('frmMateriales.hidUdDentroPedido', splitString[25] );
			set('frmMateriales.hidUdCaja', splitString[26] );
			set('frmMateriales.hidUdMedidaVenta', splitString[27] );
			set('frmMateriales.hidCosteEstandar', splitString[28] );
			set('frmMateriales.hidPrecioPosicionamiento', splitString[29] );
			set('frmMateriales.hidSituacion', splitString[30] );
			set('frmMateriales.hidEstatusDelProducto', splitString[31] );
			set('frmMateriales.hidIndicadorDentroCaja', splitString[32] );
			set('frmMateriales.hidCodPagoFraccionado', splitString[33] );
			set('frmMateriales.hidCodImpuesto', splitString[34] );
			set('frmMateriales.hidPrecioCatalogo', splitString[35] );
			set('frmMateriales.hidPrecioContable', splitString[36] );

			set ('frmMateriales.conectorAction','LPModificarMateriales');
			set ('frmMateriales.accion', 'modificar');
			enviarFormulario();
	  }
  } else {
	  GestionarMensaje ('8', null, null, null);
  }
}

function guardar() {
	if ( ValidaCamposModificar() ) {
	  set ('frmMateriales.conectorAction','LPModificarMateriales');
	  set ('frmMateriales.accion', 'guardar');
	  enviarFormulario();
	}
}

function mostrarCodigoError() { 
  if ( get('frmMateriales.errDescripcion') != '' ) {
	  //var wnd = fMostrarMensajeError (get ('frmMateriales.errCodigo'), get ('frmMateriales.errDescripcion'));
	  cdos_mostrarAlert( get('frmMateriales.errDescripcion') );
  }
}

function pasarFocoAnteriorRDBLotes() {
	if ( pantalla == 'modificar' )
		focaliza('frmMateriales.txtCodigoImpuesto','');
	else
		focaliza('frmMateriales.txtDescripcionCorta','');
}

function pasarFocoAnteriorCodigoSAP() {
	focaliza('frmMateriales.txtPrecioContable','');
}

function pasarFocoSiguienteCodigoImpuesto() {	

	if ( pantalla == 'modificar' )
	  if (get('frmMateriales.hidLote') == 'SI') {
			document.all.rdbLote[0].focus();
		}
		else {
			document.all.rdbLote[1].focus();
		}
	else
		focaliza('frmMateriales.txtPrecioCatalogo','');
}

function pasarFocoSiguientePrecioContable() {
	focaliza('frmMateriales.txtCodigoSAP','');
}

function enviarFormulario() {
	//Se deshabilitan los campos para que no se puedan cambiar mientras se realiza la consulta al servidor
	deshabilitarControles();
	envia ('frmMateriales');
}

