PAGINA.JAVASCRIPT:


function cargarCampos(){	
	//********************** Criterios de seleccion ************************
	//Combo tipo comision
	set( 'formulario.cbTipoComision', [get('formulario.hCBTipoComision')]);
	//Concepto
	set('formulario.atxtConcepto', get('formulario.hConcepto'));	



	//****************************** Ventas ********************************
	//Aplicar comision
	set('formulario.cbAplicarComision', [get('formulario.hAplicarComision')]);
	onChangeAplicarComision();

	//Calcular incobrables
	set('formulario.ckCalcularIncobrables', get('formulario.varCalcularIncobrables'));

	var algo = get('formulario.ckCalcularIncobrables');
	if(algo=="S") {
		//Fecha
		if(get('formulario.hFechaEmision') == 'S'){
			set('formulario.rbFecha', 'S');
		}
		else{
			set('formulario.rbFecha', 'N');
		}
	}
	else {
		onChangeCalcularIncobrables();
	}
	//Dias incobrables
	set('formulario.txtDiasIncobrables', get('formulario.tempDiasIncobrables'));
	//Formula	
	set('formulario.cbFormula', [get('formulario.varFormula')]);
	//Devoluciones
	set('formulario.ckDevoluciones', get('formulario.varDevoluciones'));
	//Anulaciones
	set('formulario.ckAnulaciones', get('formulario.varAnulaciones'));
	//Faltantes no anunciados
	set('formulario.ckFaltantesNoAnunciados', get('formulario.varFaltantesNoAnunciados'));
	//% Comision
	set('formulario.txtPorcentaje', get('formulario.tempPorcentaje'));
	//Importe fijo
	set('formulario.txtImporteFijo', get('formulario.tempImporteFijo'));

	//Comparativo
	set('formulario.cbComparativo', [get('formulario.varComparativo')]);
	onChangeComparativo();
	//oids y descripciones de periodos
	//Los oids de setean desde la LP
	//txt_to('lblPeriodoInicialEvaluaciondt', get("formulario.desPeriodoInicial"));
	//txt_to('lblPeriodoFinalEvaluaciondt', get("formulario.desPeriodoFinal"));
	//Porcentaje incremento
	//set('formulario.txtPorcentajeIncremento', get('formulario.tempPorcentajeIncremento'));
	//Monto	
	//set('formulario.txtMonto', get('formulario.tempMonto'));
	//Devoluciones metas
	//set('formulario.ckDevolucionesMetas', get('formulario.varDevolucionesEnMetas'));	
	//Anulaciones metas
	//set('formulario.ckAnulacionesMetas', get('formulario.varAnulacionesEnMetas'));	



	//Periodos
	set('formulario.cbPeriodoEntradaVigor3', [get('formulario.hPeriodoEntradaEnVigor')]);
	set('formulario.cbPeriodoEntradaVigor5', [get('formulario.hPeriodoEntradaEnVigor')]);	
	//Observaciones
	set('formulario.atxtObservaciones3', get('formulario.hObservaciones'));
	set('formulario.atxtObservaciones52', get('formulario.hObservaciones'));



	//****************************** Cobranza ********************************
	//Los datos del marco cobranza se setean en su mayoria en variables ocultas desde la LP

}

function onLoadPag() {
	configurarMenuSecundario("formulario");        
	cargarCampos();

	var tipo = get('formulario.hCBTipoComision');
	mostrarCapas(tipo);

	if(get('formulario.hHabilitarEstadoSolicitudAutorizada') == 'S'){
		deshabilitarCamposEstadoSolicitudAceptada();
	}

	if(get('formulario.opcionMenu') == 'consultar'){
		deshabilitarTodo();
		var tipoCobranza = get('formulario.TIPO_COMISION_COBRANZA');
		if( tipo == tipoCobranza ) {
			focoComboTipoParticipante();
		}		
	}

	var tipoPlan = get("formulario.oidTipoPlantilla");
	if(tipoPlan!="") {
		var arraySeteo = new Array();
		arraySeteo[0] = tipoPlan;
		set("formulario.cbTipoComision", arraySeteo);
		document.getElementById("cbTipoComision").disabled = true;
	}

	if(get('formulario.opcionMenu') == 'insertar'){
		if(get('formulario.valDefecto') == 'S') {
			valoresPorDefecto();
		}

		focoComboTipoComision();
	}

	if(get('formulario.opcionMenu') == 'modificar') {
		btnProxy(2, '0');
		btnProxy(3, '1');
		btnProxy(7, '1');
		btnProxy(8, '1');
		btnProxy(9, '1');
	}
}

function comboAplicarComision() {
	var seteoAplicarComision = get("formulario.seteoAplicarComision");
	if(seteoAplicarComision=="true") {
		var arraySeteo = new Array();
		arraySeteo[0] = get("formulario.valVentaTotal");
		set("formulario.cbAplicarComision", arraySeteo);
		document.getElementById("cbAplicarComision").disabled = true;
	}

	onChangeAplicarComision();
}

function valoresPorDefecto() {
	set("formulario.ckCalcularIncobrables", "N");	
	set("formulario.ckDevoluciones", "S");
	set("formulario.ckAnulaciones", "S");
	set("formulario.ckFaltantesNoAnunciados", "N");
	set("formulario.txtPorcentaje", "");
	set("formulario.txtImporteFijo", "");
}

function mostrarCapas(tipo){
	var tipoVentas = get('formulario.TIPO_COMISION_VENTAS');
	var tipoCobranza = get('formulario.TIPO_COMISION_COBRANZA');

	if( tipo == tipoVentas ){ // Ventas
		document.all["capa2"].style.visibility= "visible";
		if(get('formulario.opcionMenu') == 'insertar'){
			document.all["capa3"].style.visibility= "hidden";
		}		
		else {
			document.all["capa3"].style.visibility= "visible";			
		}
		document.all["capa4"].style.visibility= "hidden";
		document.all["capa5"].style.visibility= "hidden";

	}
	else if( tipo == tipoCobranza ){ // Cobranza
		document.all["capa2"].style.visibility= "hidden";
		document.all["capa3"].style.visibility= "hidden";
		document.all["capa4"].style.visibility= "visible";
		if(get('formulario.opcionMenu') == 'insertar'){
			document.all["capa5"].style.visibility= "hidden";
		}
		else{
			document.all["capa5"].style.visibility= "visible";
		}		
	}
}

function fLimpiar(){
	onLoadPag();
}

function deshabilitarBotonAceptar(){
	deshabilitarHabilitarBoton('botonContenido', 'btnAceptar', 'D');
}

function deshabilitarCamposEstadoSolicitudAceptada(){
	//Se dejan habilitados solo los siguientes campos
	//Porcentaje de Incremento,
	//Monto de Incremento, 
	//Porcentaje de Comisión, 
	//Importe Fijo de la comisión. 
	//Periodo entrada vigor
	//Observaciones

	accion('formulario.txtPorcentajeIncremento','.disabled=false');
	accion('formulario.txtMonto','.disabled=false');
	accion('formulario.txtPorcentaje','.disabled=false');
	accion('formulario.txtImporteFijo','.disabled=false');
	accion('formulario.cbPeriodoEntradaVigor3','.disabled=false');
	accion('formulario.atxtObservaciones3','.disabled=false');
	accion('formulario.cbPeriodoEntradaVigor5','.disabled=false');
	accion('formulario.atxtObservaciones52','.disabled=false');

	//El resto deshabilitados

	accion('formulario.cbTipoComision','.disabled=true');
	accion('formulario.atxtConcepto','.disabled=true');
	accion('formulario.cbAplicarComision','.disabled=true');
	accion('formulario.ckCalcularIncobrables','.disabled=true');
	accion('formulario.rbFecha','.disabled=true');
	accion('formulario.txtDiasIncobrables','.disabled=true');
	accion('formulario.txtDiasIncobrables','.disabled=true');
	accion('formulario.cbFormula','.disabled=true');
	accion('formulario.ckDevoluciones','.disabled=true');
	accion('formulario.ckAnulaciones','.disabled=true');
	accion('formulario.ckFaltantesNoAnunciados','.disabled=true');
	accion('formulario.cbComparativo','.disabled=true');
	accion('formulario.ckDevolucionesMetas','.disabled=true');
	accion('formulario.ckAnulacionesMetas','.disabled=true');

	//se deshabilitan los radiob
	var ii = document.all.tags('INPUT');
	for(i = 0; i < ii.length; i++){
		if(ii.item(i).type == 'radio'){
			ii[i].disabled = true;
		}
	}
}

