	accion('formulario.txtPorcentaje','.disabled=true');
	accion('formulario.txtImporteFijo','.disabled=true');
	accion('formulario.cbComparativo','.disabled=true');
	accion('formulario.txtPorcentajeIncremento','.disabled=true');
