function focoComboTipoComision() {
	if(document.all["cbTipoComision"].disabled!=true) {
		focaliza("formulario.cbTipoComision");
	}
	else {
		focaliza("formulario.atxtConcepto");
	}
}

function reInicio() {
	focoComboTipoComision();
}

function fErrorAlGuardar(){
	focoComboTipoComision();
}

function fGuardar(){
	//Se realizan todas las validaciones

	//COMIENZAN VALIDACIONES

	//Se valida que haya seleccionado un valor en el combo tipo comision
	if(!sicc_validaciones_generales('grupoCriterios')){
		return false;
	}

	var capa2 = document.all["capa2"].style.visibility;
	var capa3 = document.all["capa3"].style.visibility;
	var capa4 = document.all["capa4"].style.visibility;
	var capa5 = document.all["capa5"].style.visibility;

	//Venta
	if(capa2!='hidden') {

		//Se valida que se haya ingresado un valor para los combos 
		//Aplicar comision y Formula
		if(!sicc_validaciones_generales('grupoVentas'))
			return false;

		//Si el campo dias incobrables esta habilitado
		//se valida que se ingrese un valor
		if(document.getElementById("txtDiasIncobrables").disabled != true) {
			if(!sicc_validaciones_generales('grupoVentasCalcular'))
				return false;
		}		

		//Se valida que se ingrese un valor en Comision o Importe fijo
		if(!sicc_validaciones_generales('grupoCalcularMetas')){
			return false;
		}

		//Si el combo comparativo esta habilitado 
		//se valida que se eliga un valor en dicho combo
		if(document.getElementById("cbComparativo").disabled!=true) {
			if(!sicc_validaciones_generales('grupoVentasComparativo')){
				return false;
			}		
		}		

		var comision = get("formulario.txtPorcentaje");
		var importe = get("formulario.txtImporteFijo");

		//Si los campos porcentaje comision e importe fijo
		//tienen valor, aparece el siguiente mensaje:
		//Los campos Comision e Importe Fijo son excluyentes
		if((comision!="") && (importe!="")) {		
			GestionarMensaje('1542',null,null,null)
			focoPorcentajeComision();
			return false;
		}

		//Si los campos porcentaje comision e importe fijo
		//no tienen valor, aparece el siguiente mensaje
		//Debe ingresar un valor para Comision o Importe Fijo
		if((comision=="") && (importe=="")) {		
			GestionarMensaje('1541',null,null,null)
			focoPorcentajeComision();
			return false;
		}

		//Si los campos Porcentaje incremento y Monto
		//incremento tienen valor, aparecera el siguiente menaje
		//Los campos Porcentaje incremento y Monto incremento son excluyentes
		var porIncremento = get("formulario.txtPorcentajeIncremento");
		var monIncremento = get("formulario.txtMonto");
		if((porIncremento!="") && (monIncremento!="")) {
			GestionarMensaje('1576',null,null,null)
			focaliza("formulario.txtPorcentajeIncremento");
			return false;
		}		

		//Se valida que los campos porcentaje incremento
		//y monto incremento tengan valores numericos
		if(!sicc_validaciones_generales('grupoVentaHistorica')){
			return false;
		}		
	}

	//Cobranza
	if(capa4!='hidden') {

		//Se valida que se haya ingresado una valor
		//para el combo tipo participante
		if(!sicc_validaciones_generales('grupoCobranza'))
		return false;
	}
	//FIN VALIDACIONES

	//Se asignan valores a las variables ocultas
	asignarCamposAHiddens();

	var datosClientes = get("formulario.datosPestanyaClientes");

	var capa2 = document.all["capa2"].style.visibility;
	var capa3 = document.all["capa3"].style.visibility;
	var capa4 = document.all["capa4"].style.visibility;
	var capa5 = document.all["capa5"].style.visibility;

	if((capa2=='hidden') && (capa3=='hidden') && (capa4=='hidden') && (capa5=='hidden')) {
		return false;
	}

	//Si el tipo de comision es venta...
	if(capa2!='hidden') {
		//Si no se ingresaron registros en la pestaña Clientes
		//Aparece el siguiente mensaje
		//La comisión está incompleta ¿desea grabarla en estado 'En creación' ?
		if(datosClientes=="false") {
			var resp = GestionarMensaje('1543',null,null,null);

			//Si el usuario presiona Aceptar, se manda a guardar
			if(resp==true) {
				set("formulario.conectorAction", "LPMantenimientoComisionesDatosCalculo");
				set("formulario.accion", "guardar");
				eval('formulario').oculto = 'S';
				enviaSICC('formulario', '', '', 'N');
			}
			//Si presiona Cancelar, sale
			else {
				return false;
			}
		}
		//Si el usuario habia ingresado registros en la pestaña Clientes
		//Se envia a guardar sin mostrar ningun mensaje
		else {
			set("formulario.conectorAction", "LPMantenimientoComisionesDatosCalculo");
			set("formulario.accion", "guardar");
			eval('formulario').oculto = 'S';
			enviaSICC('formulario', '', '', 'N');
		}	
	}

	//Si el tipo de comision es cobranza...
	if(capa4!='hidden') {
		//Si el usuario no ingreso registros en la pestaña
		//Clientes, aparece el siguiente mensaje
		//La comisión está incompleta ¿desea grabarla en estado 'En creación' ?
		if(datosClientes=="false") {
			var resp = GestionarMensaje('1543',null,null,null);

			//Si el usuario presiona Aceptar, se manda a guardar
			if(resp==true) {
				set("formulario.conectorAction", "LPMantenimientoComisionesDatosCalculo");
				set("formulario.accion", "guardar");
				eval('formulario').oculto = 'S';
				enviaSICC('formulario', '', '', 'N');
			}
			//Si el usuario presiona Cancelar, sale
			else {
				return false;
			}
		}
		//Si el usuario ingreso registros en la pestaña, se manda
		//a guardar sin mostrar ningun mensaje
		else {
			//se debe validar que se haya ingresado dias tras facturacion en la ventana modal y
			//que aunque sea se haya insertado una linea en datos de niveles

			var part = get('formulario.cbTipoParticipante', 'V');

			if(part == get('formulario.TIPO_PARTICIPANTE_CAPITAL')){
				if ((get('formulario.hTramosComisionesCapital')=='')||(get('formulario.hDiasTrasFacturacionCapital')=='')){
					GestionarMensaje('UICOM0010',null,null,null);
					return false;
				}
			}
			else if(part == get('formulario.TIPO_PARTICIPANTE_PROVINCIA')){
				if ((get('formulario.hTramosComisionesProvincia')=='')||(get('formulario.hDiasTrasFacturacionProvincia')=='')){
					GestionarMensaje('UICOM0010',null,null,null);
					return false;
				}
			}
			else if(part == get('formulario.TIPO_PARTICIPANTE_FRONTERA')){
				if ((get('formulario.hTramosComisionesFrontera')=='')||(get('formulario.hDiasTrasFacturacionFrontera')=='')){
					GestionarMensaje('UICOM0010',null,null,null);
					return false;
				}
			}

			set("formulario.conectorAction", "LPMantenimientoComisionesDatosCalculo");
			set("formulario.accion", "guardar");
			eval('formulario').oculto = 'S';
			enviaSICC('formulario', '', '', 'N');
		}	
	}
}

function asignarCamposAHiddens(){
	setMV('formulario.cbTipoParticipante', 'N');

	//****************************** Parametros comunes ****************************
	//1°Tipo de comision
	set('formulario.hTipoComision', get('formulario.cbTipoComision', 'V'));

	//2°Concepto
	set('formulario.hConcepto', get('formulario.atxtConcepto'));

	//*********************************** Ventas ***********************************
	//1°Aplicar comision
	set('formulario.hAplicarComision', get('formulario.cbAplicarComision'));

	//2°Calcular incobrables
	set('formulario.varCalcularIncobrables', get('formulario.ckCalcularIncobrables'));

	//3°Fecha
	var incTemp = get('formulario.ckCalcularIncobrables');
	if(incTemp=="N") {
		set('formulario.hFechaEmision', 'N');
		set('formulario.hFechaVencimiento', 'N');
	}
	else {
		if(get('formulario.rbFecha')=='S'){
			set('formulario.hFechaEmision', 'S');
			set('formulario.hFechaVencimiento', 'N');
		}
		else{
			set('formulario.hFechaEmision', 'N');
			set('formulario.hFechaVencimiento', 'S');
		}	
	}

	//4°Dias incobrables
	set('formulario.hDiasIncobrables', get('formulario.txtDiasIncobrables'));

	//5°Formula
	set('formulario.varFormula', get('formulario.cbFormula', 'V'));

	//6°Devoluciones
	set('formulario.varDevoluciones', get('formulario.ckDevoluciones'));

	//7°Anulaciones
	set('formulario.varAnulaciones', get('formulario.ckAnulaciones'));

	//8°Faltantes no anunciados
	set('formulario.varFaltantesNoAnunciados', get('formulario.ckFaltantesNoAnunciados'));

	//9°Porcentaje comision
	set('formulario.htxtPorcentaje', get('formulario.txtPorcentaje'));

	//10°Importe fijo
	set('formulario.htxtImporteFijo', get('formulario.txtImporteFijo'));

	//11°Comparativo
	set('formulario.varComparativo', get('formulario.cbComparativo', 'V'));

	if(document.getElementById("txtPorcentajeIncremento").disabled == true) {
		//12°oid periodo inicial
		set('formulario.oidPeriodoInicialEval', "");

		//13°oid periodo final
		set('formulario.oidPeriodoFinalEval', "");
	}	

	//14°Pocentaje incremento
	set('formulario.htxtPorcentajeIncremento', get('formulario.txtPorcentajeIncremento'));

	//15°Monto incremento
	set('formulario.htxtMonto', get('formulario.txtMonto'));

	//16°Devoluciones en metas
	set('formulario.varDevolucionesEnMetas', get('formulario.ckDevolucionesMetas'));

	//17°Anulaciones en metas
	set('formulario.varAnulacionesEnMetas', get('formulario.ckAnulacionesMetas'));

	//******************************* Cobranzas ********************************************		
	set('formulario.hTipoParticipante', get('formulario.cbTipoParticipante'));

	//************** Datos de la modificacion ****** Ventas y cobranzas ********************

	if(get('formulario.hTipoComision') == get('formulario.TIPO_COMISION_VENTAS')){
		set('formulario.hPeriodoEntradaEnVigor', get('formulario.cbPeriodoEntradaVigor3'));
		set('formulario.hObservaciones', get('formulario.atxtObservaciones3'));
	}
	else if(get('formulario.hTipoComision') == get('formulario.TIPO_COMISION_COBRANZA')){
		set('formulario.hPeriodoEntradaEnVigor', get('formulario.cbPeriodoEntradaVigor5'));
		set('formulario.hObservaciones', get('formulario.atxtObservaciones52'));
	}
}

function onChangeAplicarComision() {
	/* Si el combo TipoComisionVenta el OID == 1 "Venta Total" entonces desactivar los siguientes campos:
		   * Comparativo	NO ACTIVADO
		   * Período inicial evaluación	NO ACTIVADO
		   * Período final evaluación	NO ACTIVADO
		   * Porcentaje incremento	NO ACTIVADO
		   * Monto incremento	NO ACTIVADO
		   * Devoluciones en metas	NO ACTIVADO
		   * Anulaciones en metas	NO ACTIVADO

		Sino Si el combo el OID de TipoComisionVenta == 2 "Incremento" entonces desactivar los siguientes campos:
		   * Comparativo	        ACTIVADO
		   * Período inicial evaluación	NO ACTIVADO
		   * Período final evaluación	NO ACTIVADO
		   * Porcentaje incremento	NO ACTIVADO
		   * Monto incremento	NO ACTIVADO
		   * Devoluciones en metas	NO ACTIVADO
		   * Anulaciones en metas	NO ACTIVADO

		Fin si*/

	var oidAplicarComision = get("formulario.cbAplicarComision");

	if(oidAplicarComision=="1") {
	    set("formulario.cbComparativo", "");
		document.getElementById("cbComparativo").disabled = true;
		txt_to('lblPeriodoInicialEvaluaciondt', "");
		txt_to('lblPeriodoFinalEvaluaciondt', "");
		document.getElementById("txtPorcentajeIncremento").disabled = true;
		document.getElementById("txtMonto").disabled = true;
		document.getElementById("ckDevolucionesMetas").disabled = true;
		document.getElementById("ckAnulacionesMetas").disabled = true;
		document.getElementById("txtImporteFijo").disabled = true;

		set("formulario.ckDevolucionesMetas", "");
		set("formulario.ckAnulacionesMetas", "");
		set("formulario.txtPorcentajeIncremento", "");
		set("formulario.txtImporteFijo", "");
		set("formulario.txtMonto", "");
	}
	else if(oidAplicarComision=="2") {
		document.getElementById("cbComparativo").disabled = false;
		txt_to('lblPeriodoInicialEvaluaciondt', "");
		txt_to('lblPeriodoFinalEvaluaciondt', "");
		document.getElementById("txtPorcentajeIncremento").disabled = true;
		document.getElementById("txtMonto").disabled = true;
		document.getElementById("ckDevolucionesMetas").disabled = true;
		document.getElementById("ckAnulacionesMetas").disabled = true;
		set("formulario.txtImporteFijo", "");
		document.getElementById("txtImporteFijo").disabled = false;
	}
	else {
		document.getElementById("txtImporteFijo").disabled = false;
	}
}

function onChangeCalcularIncobrables() {
	/*
	Si Checked="S" entonces habilitar
    Radiobutton Fecha  con el valor por defecto="Emision"
    Días para Incobrables con valor por defecto en blanco

	Si Checked="N" entonces deshabilitar
    Radiobutton Fecha  dejar sin valor
    Días para Incobrables con valor por defecto en blanco
	*/

	var calcular = get("formulario.ckCalcularIncobrables");
	if(calcular=="S") {
		document.all.rbFecha[0].disabled= false;
	    document.all.rbFecha[1].disabled= false;
		set("formulario.rbFecha", "S");

		document.getElementById("txtDiasIncobrables").disabled = false;
		set("formulario.txtDiasIncobrables", "");
	}
	else {
		document.all.rbFecha[0].disabled= true;
	    document.all.rbFecha[1].disabled= true;
		set("formulario.rbFecha", "");

		document.getElementById("txtDiasIncobrables").disabled = true;
		set("formulario.txtDiasIncobrables", "");
	}
}

function onChangeComparativo() {
/* (1)Si el OID de la opción seleccionada = "Venta Historica" entonces 
			(2)Si las variables ocultas  oidPeriodoInicialAnterior  y oidPeriodoFinallAnterior <> null Entonces 
				Habilitar
				* Período inicial evaluación	Mostrar los valores
 				* Período final evaluación	Mostrar los valores
				* Porcentaje incremento	ACTIVAR Opcional
				* Monto incremento	ACTIVAR Opcional			
				(Estos dos campos son excluyentes, por lo que se debe validar que no se ingresen valores en estos dos campos a la vez)
	 	        * Devoluciones en metas	ACTIVAR por defecto debe ser ="S"
				* Anulaciones en metas	ACTIVAR por defecto debe ser ="S"

			(2) Sino Mostrar el mensaje de error UICOM0005 - "La comisión no puede ser incremental de tipo venta 			histórica, porque no existe el periodo del año anterior"

	   (1)Sino si el OID de la opción seleccion <> "Venta Historica" entonces deshabilitar y dejar en blanco 
           * Período inicial evaluación	
		   * Período final evaluación	
		   * Porcentaje incremento	
		   * Monto incremento	
		   * Devoluciones en metas	
		   * Anulaciones en metas	
   (1)Fin si */

	var arraySeteo = new Array();
	arraySeteo[0] = ""

	var comparativo = get("formulario.cbComparativo");

	if(comparativo=="1") {
		var temp1 = get("formulario.oidPeriodoInicialEval");
		var temp2 = get("formulario.desPeriodoInicial");
		var temp3 = get("formulario.oidPeriodoFinalEval");
		var temp4 = get("formulario.desPeriodoFinal");

		if(temp1!="" && temp3!="") {
			txt_to('lblPeriodoInicialEvaluaciondt', get("formulario.desPeriodoInicial"));
			txt_to('lblPeriodoFinalEvaluaciondt', get("formulario.desPeriodoFinal"));

			document.getElementById("txtPorcentajeIncremento").disabled = false;
			document.getElementById("txtMonto").disabled = false;
			document.getElementById("ckDevolucionesMetas").disabled = false;
			document.getElementById("ckAnulacionesMetas").disabled = false;

			set("formulario.ckDevolucionesMetas", get("formulario.varDevolucionesEnMetas"));
			set("formulario.ckAnulacionesMetas", get("formulario.varAnulacionesEnMetas"));
			set("formulario.txtPorcentajeIncremento", get("formulario.tempPorcentajeIncremento"));
			set("formulario.txtMonto", get("formulario.tempMonto"));
		}
		else {
			GestionarMensaje('UICOM0005',null,null,null);
			set("formulario.cbComparativo", arraySeteo);

			txt_to('lblPeriodoInicialEvaluaciondt', "");
			txt_to('lblPeriodoFinalEvaluaciondt', "");

			document.getElementById("txtPorcentajeIncremento").disabled = true;
			document.getElementById("txtMonto").disabled = true;
			document.getElementById("ckDevolucionesMetas").disabled = true;
			document.getElementById("ckAnulacionesMetas").disabled = true;

			set("formulario.ckDevolucionesMetas", "");
			set("formulario.ckAnulacionesMetas", "");
			set("formulario.txtPorcentajeIncremento", "");
			set("formulario.txtMonto", "");
		}		
	}
	else {
		txt_to('lblPeriodoInicialEvaluaciondt', "");
		txt_to('lblPeriodoFinalEvaluaciondt', "");

		document.getElementById("txtPorcentajeIncremento").disabled = true;
		document.getElementById("txtMonto").disabled = true;
		document.getElementById("ckDevolucionesMetas").disabled = true;
		document.getElementById("ckAnulacionesMetas").disabled = true;

		set("formulario.ckDevolucionesMetas", "");
		set("formulario.ckAnulacionesMetas", "");
		set("formulario.txtPorcentajeIncremento", "");
		set("formulario.txtMonto", "");
	}
}

function focoPorcentajeComision() {
	focaliza("formulario.txtPorcentaje");
}

function btnAceptarOnClick() {
	if(!sicc_validaciones_generales('grupoCriterios')){
		return;
	}

	var tipo = get('formulario.cbTipoComision', 'V');
	mostrarCapas(tipo);
}

function btnAceptarTipoParticipanteOnClick() {
	/* -> Levantar ventana modal "contenido_comisiones_datosComision_Insertar_detalle" 
	-> Recuperar arreglo de arreglos 
	(1) Si en "cmbTipoParticipante" se selecciona "Capital" entonces 
	-> guardar arreglo de arreglos en "tramosComisionesCapital" 
	(1) Si no Si en "cmbTipoParticipante" se selecciona "Provincia" entonces 
	-> guardar arreglo de arreglos en "tramosComisionesProvincia" 
	(1) Si no Si en "cmbTipoParticipante" se selecciona "Frontera" entonces 
	-> guardar arreglo de arreglos en "tramosComisionesFrontera" 
	(1) Fin Si */

	if(sicc_validaciones_generales('grupoCobranza')){
		var parametros = new Object();

		var tipoParticipante = get('formulario.cbTipoParticipante', 'V');
		var tipoPartCapital = get('formulario.TIPO_PARTICIPANTE_CAPITAL');
		var tipoPartProvincia = get('formulario.TIPO_PARTICIPANTE_PROVINCIA');
		var tipoPartFrontera = get('formulario.TIPO_PARTICIPANTE_FRONTERA');
		var tramosComisionesCapital = get('formulario.hTramosComisionesCapital');
		var tramosComisionesProvincia = get('formulario.hTramosComisionesProvincia');
		var tramosComisionesFrontera = get('formulario.hTramosComisionesFrontera');
		var hDiasTrasFacturacionCapital = get('formulario.hDiasTrasFacturacionCapital');
		var hDiasTrasFacturacionProvincia = get('formulario.hDiasTrasFacturacionProvincia');
		var hDiasTrasFacturacionFrontera = get('formulario.hDiasTrasFacturacionFrontera');
		var opcionMenu = get('formulario.opcionMenu');
		var titulo = get('formulario.titulo');		

		parametros.tipoParticipante = tipoParticipante;
		parametros.tipoPartCapital = tipoPartCapital;
		parametros.tipoPartProvincia = tipoPartProvincia;
		parametros.tipoPartFrontera = tipoPartFrontera;
		parametros.tramosComisionesCapital = tramosComisionesCapital;
		parametros.tramosComisionesProvincia = tramosComisionesProvincia;
		parametros.tramosComisionesFrontera = tramosComisionesFrontera;
		parametros.hDiasTrasFacturacionCapital = hDiasTrasFacturacionCapital;
		parametros.hDiasTrasFacturacionProvincia = hDiasTrasFacturacionProvincia;
		parametros.hDiasTrasFacturacionFrontera = hDiasTrasFacturacionFrontera;
		parametros.opcionMenu = opcionMenu;
		parametros.titulo = titulo;		

		var datos = mostrarModalSICC("LPMantenimientoComisionesDatosCalculo", "mostrarDetalleComisiones", parametros,800,600);

		if(datos != null){
			var part = get('formulario.cbTipoParticipante', 'V');
			var datosTemp = datos.valor.split("?");
			var datosLista = datosTemp[0];
			var diasFacturacion = datosTemp[1];

			if(part == get('formulario.TIPO_PARTICIPANTE_CAPITAL')){
				set('formulario.hTramosComisionesCapital', datosLista);
				set('formulario.hDiasTrasFacturacionCapital', diasFacturacion);
			}
			else if(part == get('formulario.TIPO_PARTICIPANTE_PROVINCIA')){
				set('formulario.hTramosComisionesProvincia', datosLista);
				set('formulario.hDiasTrasFacturacionProvincia', diasFacturacion);
			}
			else if(part == get('formulario.TIPO_PARTICIPANTE_FRONTERA')){
				set('formulario.hTramosComisionesFrontera', datosLista);
				set('formulario.hDiasTrasFacturacionFrontera', diasFacturacion);
			}
		}
	}
}

function poneManito(nombre) {           
	var nombre = document.getElementById(nombre);
	nombre.style.cursor = "hand";
}   

function onClickPestanya(valor) {
	//COMIENZAN VALIDACIONES

	//Se valida que haya seleccionado un valor en el combo tipo comision
	if(!sicc_validaciones_generales('grupoCriterios')){
		return false;
	}

	var capa2 = document.all["capa2"].style.visibility;
	var capa3 = document.all["capa3"].style.visibility;
	var capa4 = document.all["capa4"].style.visibility;
	var capa5 = document.all["capa5"].style.visibility;

	//Venta
	if(capa2!='hidden') {

		//Se valida que se haya ingresado un valor para los combos 
		//Aplicar comision y Formula
		if(!sicc_validaciones_generales('grupoVentas'))
			return false;

		//Si el campo dias incobrables esta habilitado
		//se valida que se ingrese un valor
		if(document.getElementById("txtDiasIncobrables").disabled != true) {
			if(!sicc_validaciones_generales('grupoVentasCalcular'))
				return false;
		}		

		//Se valida que se ingrese un valor en Comision o Importe fijo
		if(!sicc_validaciones_generales('grupoCalcularMetas')){
			return false;
		}

		//Si el combo comparativo esta habilitado 
		//se valida que se eliga un valor en dicho combo
		if(document.getElementById("cbComparativo").disabled!=true) {
			if(!sicc_validaciones_generales('grupoVentasComparativo')){
				return false;
			}		
		}		

		var comision = get("formulario.txtPorcentaje");
		var importe = get("formulario.txtImporteFijo");

		//Si los campos porcentaje comision e importe fijo
		//tienen valor, aparece el siguiente mensaje:
		//Los campos Comision e Importe Fijo son excluyentes
		if((comision!="") && (importe!="")) {		
			GestionarMensaje('1542',null,null,null)
			focoPorcentajeComision();
			return false;
		}

		//Si los campos porcentaje comision e importe fijo
		//no tienen valor, aparece el siguiente mensaje
		//Debe ingresar un valor para Comision o Importe Fijo
		if((comision=="") && (importe=="")) {		
			GestionarMensaje('1541',null,null,null)
			focoPorcentajeComision();
			return false;
		}

		//Si los campos Porcentaje incremento y Monto
		//incremento tienen valor, aparecera el siguiente menaje
		//Los campos Porcentaje incremento y Monto incremento son excluyentes
		var porIncremento = get("formulario.txtPorcentajeIncremento");
		var monIncremento = get("formulario.txtMonto");
		if((porIncremento!="") && (monIncremento!="")) {
			GestionarMensaje('1576',null,null,null)
			focaliza("formulario.txtPorcentajeIncremento");
			return false;
		}		

		//Se valida que los campos porcentaje incremento
		//y monto incremento tengan valores numericos
		if(!sicc_validaciones_generales('grupoVentaHistorica')){
			return false;
		}		
	}

	//Cobranza
	if(capa4!='hidden') {

		//Se valida que se haya ingresado una valor
		//para el combo tipo participante
		if(!sicc_validaciones_generales('grupoCobranza'))
		return false;
	}
	//FIN VALIDACIONES


	var estadoVentana = get("formulario.estadoVentana");
	var titulo = get("formulario.titulo");
	var opcionMenu = get("formulario.opcionMenu");
	var tipo = get('formulario.cbTipoComision', 'V');
	set('formulario.hTipoComision', tipo)

	if(valor=="DatosGenerales") {
		set("formulario.idPestanyaDest", "1");
	}
	else if(valor=="Productos") {
		set("formulario.idPestanyaDest", "2");
	}
	else if(valor == 'Clientes'){
		set("formulario.idPestanyaDest", "3");
	}
	else if(valor=="DatosCalculo") {
		set("formulario.idPestanyaDest", "4");
	}

	asignarCamposAHiddens();

	set("formulario.conectorAction", "LPMantenimientoComisionesDatosCalculo");
	set("formulario.estadoVentana", estadoVentana);
	set("formulario.accion", "almacenar");
	set("formulario.oidTipoPlantilla", get("formulario.oidTipoPlantilla"));
	set("formulario.titulo", titulo);
	set("formulario.opcionMenu", opcionMenu);
	enviaSICC('formulario', '', '', 'N');
}

