	if(document.getElementById("ckCalcularIncobrables").disabled == true) {
		focaliza("formulario.cbFormula");
	}
	else {
		focaliza("formulario.ckCalcularIncobrables");
	}
}

function focoComboTipoComision() {
	if(document.getElementById("cbTipoComision").disabled != true) {
		focaliza("formulario.cbTipoComision");
	}
	else {
		focaliza("formulario.atxtConcepto");
	}	
}

function onShTabTipoComision() {
	var tipoVentas = get('formulario.TIPO_COMISION_VENTAS');
	var tipoCobranza = get('formulario.TIPO_COMISION_COBRANZA');

	if(get("formulario.cbTipoComision")==tipoVentas) {
		if(get('formulario.opcionMenu') == 'insertar'){
			focalizaBotonHTML('btnCalcularMetas','btnAceptar');	
		}
		else {
			focoObservacionesVentas();
		}
	}
	else if(get("formulario.cbTipoComision")==tipoCobranza) {
		if(get('formulario.opcionMenu') == 'insertar'){
			focoBotonAceptarTipoParticipante();
		}
		else {
			focoObservacionesCobranzas();
		}
	}
	else {
		focoBotonAceptarCriterios();
	}
}

function focoObservacionesVentas() {
	focaliza("formulario.atxtObservaciones3");
}

function focoObservacionesCobranzas() {
	focaliza("formulario.atxtObservaciones52");
}

function onTabCalcularMetas() {
	if(get('formulario.opcionMenu') == 'insertar'){
		focoComboTipoComision();
	}
	else {
		focaliza("formulario.cbPeriodoEntradaVigor3");
	}
}

function fVolver() {
	if(get("formulario.estadoVentana")=="M") {
		window.close();
	}
	else {
		if(get("formulario.titulo")=="ConsultarComisiones") {
			set("formulario.conectorAction", "LPConsultarComisiones");
  			enviaSICC('formulario', '', '', 'N');
		}
		else {
			set("formulario.conectorAction", "LPEliminarComisiones");
  			enviaSICC('formulario', '', '', 'N');
		}
	}
}
