   function onLoadPag()   
   {
		configurarMenuSecundario("formulario");
		ocultaLista1();
		document.all["btnModificarDiv"].style.visibility='hidden';
		document.all["btnConfirmarDiv"].style.visibility='hidden';
		eval(ON_RSZ);	
		fMostrarMensajeError();	  
		focaliza("formulario.cbEstadoComision");

		setMenu();
   }

