    function ontab_btnBuscar(){        
        if (get('frmFormulario.botonVisible') != 'none') {            
            focalizaBotonHTML('botonContenido', get('frmFormulario.botonVisible'));
        }
        else {            
            focaliza('frmFormulario.cbMarca','');            
        }
    }
