           function inicializacion(){

		//cargarBarra('barra_grupos_zonas_insertar');
		configurarMenuSecundario("frmInsertarGruposZonas");
                posicionarCombo();

		//alert(get('frmInsertarGruposZonas.hCodigoDeGrupo'));
		set('frmInsertarGruposZonas.txtCodigoDeGrupo',get('frmInsertarGruposZonas.hCodigoDeGrupo'));
                set('frmInsertarGruposZonas.txtNombreDeGrupo',get('frmInsertarGruposZonas.hNombreDeGrupo'));
                var tipoFacturac = get('frmInsertarGruposZonas.hTipoDeFacturacion');

		//alert("fac:"+tipoFacturac);	
		if (tipoFacturac != ''){
		  var iSeleccionado = new Array();
		  iSeleccionado[ 0 ] = tipoFacturac;
		  set( 'frmInsertarGruposZonas.rbTipoDeFacturacion', iSeleccionado );
		  //set('frmInsertarGruposZonas.rbTipoDeFacturacion',tipoFacturac);
                }else{
                   //Se asume este valor por default
		  var iSeleccionado = new Array();
		  iSeleccionado[ 0 ] = '';
		  set( 'frmInsertarGruposZonas.rbTipoDeFacturacion', iSeleccionado );
                  // set('frmInsertarGruposZonas.rbTipoDeFacturacion','MONO');
		}

	        if (get('frmInsertarGruposZonas.hCampoFoco') != ''){
                   foco('frmInsertarGruposZonas.' + get('frmInsertarGruposZonas.hCampoFoco'));
                }

                if (get('frmInsertarGruposZonas.accion') == "cambioRegiones"){

		   var strV = '';
		   var strT = '';
		   var i;

		if(get('frmInsertarGruposZonas.hZonasSeleccionadas')!=''){
		   meter_en_combo(get('frmInsertarGruposZonas.hZonasSeleccionadas'));
		}

               var iSeleccionadoR = new Array();
               iSeleccionadoR[0] = get('frmInsertarGruposZonas.hRegion');
               set('frmInsertarGruposZonas.cbRegion',iSeleccionadoR);

               focaliza('frmInsertarGruposZonas.cbRegion','');

		}//fin ACCION = CAMBIO_REGIONES

  	        if (get('frmInsertarGruposZonas.accion') == "seleccionPaisMarcaCanal"){
		    focaliza('frmInsertarGruposZonas.txtCodigoDeGrupo','');
		}

		if (get('frmInsertarGruposZonas.hSaliendo')=='true'){
			if(get('frmInsertarGruposZonas.hZonasSeleccionadas')!=''){
			   meter_en_combo(get('frmInsertarGruposZonas.hZonasSeleccionadas'));
			}
			salida();
		}

		ShowError();

	   }//inicializacion()

           function ShowError(){           
	      if (get('frmInsertarGruposZonas.errDescripcion')!=''){              
		var wnd = fMostrarMensajeError(get('frmInsertarGruposZonas.errCodigo'), 
                                               get('frmInsertarGruposZonas.errDescripcion'));
                if (wnd == 0)
		   reintentar();
                else
                   cancelar();
              }           
	   }//ShowError()

           function reintentar(){
              set('frmInsertarGruposZonas.conectorAction','LPCrearGruposZonas');
              enviaSICC('frmInsertarGruposZonas');
           }

           function cancelar(){
              set('frmInsertarGruposZonas.accion','');
              set('frmInsertarGruposZonas.conectorAction','LPCrearGruposZonas');
              fLimpiar();
           }

           function posicionarCombo(){
               var iSeleccionado = new Array();
               iSeleccionado[0] = get('frmInsertarGruposZonas.hCanal');
               set('frmInsertarGruposZonas.cbCanales',iSeleccionado);

               var iSeleccionadoM = new Array();
               iSeleccionadoM[0] = get('frmInsertarGruposZonas.hMarca');
               set('frmInsertarGruposZonas.cbMarcas',iSeleccionadoM);

               //eliminarDuplicadosComboRegion();
               var iSeleccionadoR = new Array();
               iSeleccionadoR[0] = get('frmInsertarGruposZonas.hRegion');
               set('frmInsertarGruposZonas.cbRegion',iSeleccionadoR);
           }//posicionarCombo()

           // Elimina todos los elementos duplicados del combo de Regiones
           function eliminarDuplicadosComboRegion(){

	     var longitud = combo_get('frmInsertarGruposZonas.cbRegion','L');
             var elem = new Array(2);
             var regionCola = new Array();
             var regionPrueba = new Array();

             if (longitud > 1){
	       for( i = 1;i < longitud;i++){
                  var cReg = combo_get('frmInsertarGruposZonas.cbRegion','V',i);
                  elem = new Array(2);
                  elem[0] = cReg;
                  elem[1] = cReg;

                  if (i== 1){
                    regionCola.push(elem);
                  }else{
                    var elemCola = regionCola.pop();
                    regionCola.push(elemCola);
                    if (elemCola[0] != cReg){
                      regionCola.push(elem);
                    } 
                  }

                  regionPrueba.push(elem);
                }				
              }

	      set_combo('frmInsertarGruposZonas.cbRegion', regionCola);

           }//eliminarDuplicadosComboRegion()

           function obtenerPaisMarcaCanal(){
	     set('frmInsertarGruposZonas.hCanal',get('frmInsertarGruposZonas.cbCanales'));
             set('frmInsertarGruposZonas.hMarca',get('frmInsertarGruposZonas.cbMarcas'));
           }

           function obtenerCodNomGrupoTipoFact(){
	     set('frmInsertarGruposZonas.hCodigoDeGrupo',get('frmInsertarGruposZonas.txtCodigoDeGrupo'));
             set('frmInsertarGruposZonas.hNombreDeGrupo',get('frmInsertarGruposZonas.txtNombreDeGrupo'));
	     set('frmInsertarGruposZonas.hTipoDeFacturacion',get('frmInsertarGruposZonas.rbTipoDeFacturacion'));
           }

           //Se pide la carga de Regiones segun P-M-C seleccionados
           function cargarRegZonDip(){
	     if (get('frmInsertarGruposZonas.cbMarcas') != "" && get('frmInsertarGruposZonas.cbCanales') != ""){
		 set('frmInsertarGruposZonas.accion','seleccionPaisMarcaCanal');
                 obtenerPaisMarcaCanal();
                 obtenerCodNomGrupoTipoFact();
		 set('frmInsertarGruposZonas.hRegion',get('frmInsertarGruposZonas.cbRegion'));
                 enviaSICC('frmInsertarGruposZonas');
             }
            }//cargarRegZonDip()

           function fLimpiar(){
              set('frmInsertarGruposZonas.cbCanales','');
              set('frmInsertarGruposZonas.cbMarcas','');
              set('frmInsertarGruposZonas.txtCodigoDeGrupo','');
              set('frmInsertarGruposZonas.txtNombreDeGrupo','');
              set('frmInsertarGruposZonas.rbTipoDeFacturacion','MONO');
              set('frmInsertarGruposZonas.cbRegion','');
              set_combo('frmInsertarGruposZonas.cbZonasDisponibles',[]);
              set_combo('frmInsertarGruposZonas.cbZonasSeleccionadas',[]);
              set('frmInsertarGruposZonas.chkTodasZonas','N');
              focaliza('frmInsertarGruposZonas.cbMarcas','');
           }

	   //Valida que un campo sea numérico
           function validaNumerico(value){              
	    if(fValidarNumero(value,3,0,999) != "OK"){
              fMostrarMensajeError('',fValidarNumero(value,3,0,999));
              foco('frmInsertarGruposZonas.txtCodigoDeGrupo');
              return 0;
            }
	    return 1;
           }

