	     setTimeout("guardar2()", 100);
           }

           //Se pulso el boton guardar de la barra de herramientas.
           function guardar2(){

	      if (fValidarCOMBO('frmInsertarGruposZonas.cbMarcas') &&
                  fValidarCOMBO('frmInsertarGruposZonas.cbCanales') &&
                  fValidarCTEXT('frmInsertarGruposZonas.txtCodigoDeGrupo') &&
                  validaNumerico(get('frmInsertarGruposZonas.txtCodigoDeGrupo')) &&
                  fValidarCTEXT('frmInsertarGruposZonas.txtNombreDeGrupo')){

                  obtenerPaisMarcaCanal();
                  obtenerCodNomGrupoTipoFact();
                  set('frmInsertarGruposZonas.hRegion',get('frmInsertarGruposZonas.cbRegion'));
                  var strZonasSeleccionadas = '';
                  var longitud = combo_get('frmInsertarGruposZonas.cbZonasSeleccionadas','L');

 		  if (longitud > 0)
                     strZonasSeleccionadas = combo_get('frmInsertarGruposZonas.cbZonasSeleccionadas','V',0);

		  for(var i = 1;i < longitud;i++){
                     strZonasSeleccionadas += '|' + combo_get('frmInsertarGruposZonas.cbZonasSeleccionadas','V',i);
                  }

                  set('frmInsertarGruposZonas.hZonasSeleccionadas',strZonasSeleccionadas);
                  set('frmInsertarGruposZonas.accion','crear');
		  ///////
			 obtenerPaisMarcaCanal();
			 //obtenerCodNomGrupoTipoFact();
			 set('frmInsertarGruposZonas.hRegion',get('frmInsertarGruposZonas.cbRegion'));
		  ///////
                  enviaSICC('frmInsertarGruposZonas');
              }
	   }//guardar2()

           function crearOpcion(lista,indice){
              opcion = new Option();
              opcion.text = lista.options[indice].text;
              opcion.value = lista.options[indice].value;
              return opcion;
           }

           function ordenar(lista){
  	      box = eval('document.'+lista);
              quickSort(box,0,combo_get(lista,'L') - 1);
           }

           function comparar(item1,item2){

	       if (item1 < item2){
                 return -1;
               }

               if (item1 > item2){
                 return 1;
               }

               return 0;

            }//comparar()

            function quickSort(box,inicio,fin){

		primero = inicio;
                ultimo = fin;

                if (primero >= ultimo){
                   return; 
                }

                textoMedio = box.options[parseInt(((primero + ultimo) / 2).toString())].text;

                while(primero < ultimo){
                    while(primero < ultimo && (comparar(box.options[primero].text, textoMedio) == -1)){
                        primero++;
                    }

                    for(; primero < ultimo && (comparar(box.options[ultimo].text, textoMedio) == 1); ultimo--){}

                    if(primero < ultimo){
                       opcionPrimero = crearOpcion(box,primero);
                       opcionUltimo = crearOpcion(box,ultimo);
                       box.options[primero] = opcionUltimo;
                       box.options[ultimo] = opcionPrimero;
                    }
                 }

                 if(ultimo < primero){
                    aux = ultimo;
                    ultimo = primero;
                    primero = aux;
                 }

                 quickSort(box,inicio,primero);
                 quickSort(box,primero != inicio ? primero : primero + 1,fin);

           }//quickSort()

           //Pasa un elemento de la lista origen a la destino
           function traspasar(listaOrigen,listaDestino){
              vectorValue = get(listaOrigen,'V');
              vectorDesc = get(listaOrigen,'T');

              for (var i=0;i <= vectorValue.length - 1;i++){
                if ((vectorValue[i] != null) && (vectorValue[i] != '')){
                  del_combo(listaOrigen,'V', vectorValue[i]);
                  combo_add(listaDestino,vectorValue[i],vectorDesc[i]);
                  //ordenar(listaDestino);
                }
              }

	   }//traspasar()

           //Se pulso el chk de todas en InsertarGruposZonas
           function selecTodasZonas(){                   
	      var checked = get('frmInsertarGruposZonas.chkTodasZonas','');

	      if (checked == 'S'){
                 select_combo('frmInsertarGruposZonas.cbZonasDisponibles');
              }else{
                 deselect_combo('frmInsertarGruposZonas.cbZonasDisponibles');
              }
	   }

           function deselect_combo(campo){
              var l=combo_get(campo,'L');
              var arrai=new Array();
              set(campo,arrai);
           }

