	  function comboToStr(campo){
		   var l=combo_get(campo,'L');
		   var arrai=new Array();
		   var datos='';
		   for(var i=0;i<l;i++) {
		     datos += combo_get(campo,'V',i)+'~~';
		     datos += combo_get(campo,'T',i)+'~~';
		   }
		   return datos;
	  }

	  function meter_en_combo(datos){

		var vectorValue = datos.split('~~');
		alert(get('frmInsertarGruposZonas.cbZonasSeleccionadas'));
	      alert(get('frmInsertarGruposZonas.cbZonasDisponibles'));

		for (var i=0;i <= vectorValue.length - 1;i++){
		   i++;
		   if ((vectorValue[i] != null) && (vectorValue[i] != '')){		
			combo_add('frmInsertarGruposZonas.cbZonasSeleccionadas', vectorValue[i], vectorValue[i]);
		   }

		}
alert(get('frmInsertarGruposZonas.cbZonasSeleccionadas'));
	      alert(get('frmInsertarGruposZonas.cbZonasDisponibles'));

	  }


	  function fSalir(){

		//llama a la LP para que corrobore si quedan zonas sin asignar 
		//y cuando vuelve en el inicio de ser afirmativo manda el mensaje y si no sale directamente

		if (get('frmInsertarGruposZonas.cbMarcas') != "" && get('frmInsertarGruposZonas.cbCanales') != ""){
			//para que cuando vuelva preserve los valores
			set('frmInsertarGruposZonas.hRegion',get('frmInsertarGruposZonas.cbRegion'));
			set('frmInsertarGruposZonas.hZonasSeleccionadas',''+comboToStr('frmInsertarGruposZonas.cbZonasSeleccionadas'));
			//alert(""+comboToStr('frmInsertarGruposZonas.cbZonasSeleccionadas'));
			set('frmInsertarGruposZonas.hCodigoDeGrupo',get('frmInsertarGruposZonas.txtCodigoDeGrupo'));
			set('frmInsertarGruposZonas.hNombreDeGrupo',get('frmInsertarGruposZonas.txtNombreDeGrupo'));
			set('frmInsertarGruposZonas.hTipoDeFacturacion',get('frmInsertarGruposZonas.rbTipoDeFacturacion'));

			set('frmInsertarGruposZonas.accion','verificarZonasRestantes');
			enviaSICC('frmInsertarGruposZonas');
		} else {
			salida();
		}

	}
