PAGINA.JAVASCRIPT:


   function onLoad(){
      configurarMenuSecundario('Formulario');
      fMostrarMensajeError();
      set('Formulario.txtFecha', get('Formulario.fechaDefault'));
      focaliza('Formulario.cbCanal');
   }
//------------------------------------------------------------------------------   
   function generar(){

      if(sicc_validaciones_generales()){
         if(validarFecha()){

            /*recolectarParametros();

            set('Formulario.conectorAction', 'CRAActividadesHoyGrupo');           
            set('Formulario.accion', 'generar');

            enviaSICC('Formulario');*/
            var a = new Object();
            a.accion = 'generar';
            a.marca = get('Formulario.cbMarca');  
            a.canal = get('Formulario.cbCanal');
            a.periodo = quitarItemVacio(get('Formulario.cbPeriodo'));
            a.fecha = get('Formulario.txtFecha');
            a.actividades = quitarItemVacio(get('Formulario.cbActividad'));         
            mostrarModalSICC('CRAActividadesHoyGrupo','generar', a);  
         }

      }

   }

//------------------------------------------------------------------------------   
   function recolectarParametros(){



      set('Formulario.marca', get('Formulario.cbMarca') );  
      set('Formulario.canal', get('Formulario.cbCanal'));
      set('Formulario.periodo', quitarItemVacio(get('Formulario.cbPeriodo') ));
      set('Formulario.fecha', get('Formulario.txtFecha'));
      set('Formulario.actividades', quitarItemVacio(get('Formulario.cbActividad')));




   }


//******************************************************************************

   function quitarItemVacio(elemento){
      var elementoADevolver = new Array();

      for(var k=0;k<elemento.length;k++) {
         if (elemento[k] != ''){ 			 
            elementoADevolver[elementoADevolver.length] = elemento[k];
         }
      }

      return elementoADevolver;

   }

//------------------------------------------------------------------------------      



   function fLimpiar(){
      var aCombo = new Array(new Array("",""));
      set_combo('Formulario.cbPeriodo', aCombo, []);
      set_combo('Formulario.cbActividad', aCombo, []);
      focaliza('Formulario.cbCanal');

   }

//------------------------------------------------------------------------------       
    function onchangeMarcaCanal(){

       marca  = get('Formulario.cbMarca');
       canal  = get('Formulario.cbCanal');
       pais   = get('Formulario.pais');
       idioma = get('Formulario.idioma');


       if(marca != '' && canal != ''){

          var arr = new Array();
          var arr2 = new Array();

          arr[arr.length] = ["oidIdioma", idioma]; 
          arr[arr.length] = ["oidPais", pais]; 
          arr[arr.length] = ["marca", marca];
          arr[arr.length] = ["canal", canal];

          arr2[arr2.length] = ['oidMarca', marca];
          arr2[arr2.length] = ['oidCanal', canal];
          arr2[arr2.length] = ['oidPais', pais];
          arr2[arr2.length] = ['oidIdioma', idioma];

          recargaCombo("Formulario.cbPeriodo","PRECargarPeriodos","es.indra.sicc.dtos.cra.DTOPeriodo",arr);
          recargaCombo("Formulario.cbActividad","CRAObtieneActividad", "es.indra.sicc.dtos.cra.DTOActividad", arr2); 

       }else{
          var aCombo = new Array(new Array("",""));
          set_combo('Formulario.cbPeriodo', aCombo, []);
          set_combo('Formulario.cbActividad', aCombo, []);
       }

    } 

//------------------------------------------------------------------------------       

    function validarFecha(){
       var fecha = get('Formulario.txtFecha');
       var formatoFecha = get('Formulario.hFormatoFechaPais');

       if (! EsFechaValida_SICC(fecha , "Formulario")){
          GestionarMensaje("1006", formatoFecha, null, null);
          focaliza("Formulario.txtFecha");
          return false;
       }
       return true;
    }
