// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
var tableCache = new Array();

function LoadTableCache(){
   for(var i =0;i < listado1.datos.length;i++) 
      tableCache[i] = new Array(listado1.datos[i][0],listado1.datos[i][1],listado1.datos[i][2]);
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function LoadTableFromCache(tblCache){   
   for(var i =0;i < tblCache.length;i++) {
      var nuevaFila = new Array();
                  nuevaFila[0] = tblCache[i][0];
             nuevaFila[1] = tblCache[i][1];
             nuevaFila[2] = tblCache[i][2];
                           listado1.insertar(nuevaFila);
                           listado1.repinta();
   }   
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function asignarCombosPeriodos(){
    //alert("Marca:" + _get('cbMarca2')); 
         //alert("canal:" + _get('cbCanal')); 
         if ((_get('cbMarca2')!='') && (_get('cbCanal')!='')){
                  asignar([['COMBO',formName + '.cbPeriodoInicio', 
                                                                         'ConectorDruidaTransactionQuery', 
                                                                         'dtoSalida.resultado_ROWSET',
                                                                         formarCadenaDT('MAEBusquedaPeriodos', 'es.indra.sicc.dtos.mae.DTOBusquedaPeriodos', 
                                                                         MakeParameters(_get('cbMarca'),_get('cbCanal'))), 
                                                                         'recargarPeriodoIni(datos);']]);	
                  asignar([['COMBO',formName + '.cbPeriodoFin', 
                                                                         'ConectorDruidaTransactionQuery', 
                                                                         'dtoSalida.resultado_ROWSET',
                                                                         formarCadenaDT('MAEBusquedaPeriodos', 'es.indra.sicc.dtos.mae.DTOBusquedaPeriodos', 
                                                                         MakeParameters(_get('cbMarca'),_get('cbCanal'))), 
                                                                         'recargarPeriodoFin(datos);']]);

                  arr = new Array();
                  arr[arr.length] = new Array('oidPais', get('frmKitProductos.hid_oidPais'));
                  arr[arr.length] = new Array('oid', get('frmKitProductos.cbMarca2'));

                  asignar([['COMBO',formName + '.cbMarca', 
                                                                         'ConectorDruidaTransactionQuery', 
                                                                         'dtoSalida.resultado_ROWSET',
                                                                         formarCadenaDT('SEGRecuperarMarcasProductoMP', 'es.indra.sicc.util.DTOOID', 
                                                                         arr), 
                                                                         'recargarMarcaProductos(datos);']]);




         }
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function setCombosOrig(){
         // Posicionamos valores en los combos y radiob 	
         var ind = _get("hid_cbIndicadorPS");

         if(ind == "true"){
            _set("hid_cbIndicadorPS","1");
         }else{
            _set("hid_cbIndicadorPS","0");
         }

         var canal = _get("hid_cbCanal");	
         _selectV("cbIndicadorPS");   
         _selectV("cbGenerico");


         _selectV("cbMarca2");



         _selectV("cbSuperGenerico");
         _selectV("cbEstatusProducto");
         _selectV("cbSituacion");
         _selectV("cbUnidadMedidaDimensiones");
         _selectV("cbLinea");          _selectV("cbUnidadMedidaVolumen");
         _selectV("cbNegocio");        _selectV("cbUnidadMedidaPeso");
         _selectV("cbUnidadNegocio"); _selectV("cbIndicadorDentroCaja");
         _selectV("cbCodigoPagoFraccionado");
         _selectV("rbIndicadorLote");

         cargaCanal("1");
         _selectV("cbCanal");
}

