// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function anadir(){
    if(ValidaNum(get(formName + ".txtNumeroUnidades").toString()) == "OK"){
         if(get(formName + ".txtProductos").toString() != "" && get(formName + ".txtNumeroUnidades").toString() != "") {
             var nuevaFila = new Array(); nuevaFila[0] = "D"; 
             nuevaFila[1] = get(formName + ".txtProductos").toString();
             nuevaFila[2] = get(formName + ".txtNumeroUnidades").toString();
                           listado1.insertar(nuevaFila);
                           listado1.repinta(); 
									set(formName + ".txtProductos",'');
									set(formName + ".txtNumeroUnidades",'');
         }
    } else {
            GestionarMensaje(1197, "", "", "");
            _foc("txtNumeroUnidades");
         }
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function recargarPeriodoFin(datos){
    set_combo(formName + '.cbPeriodoFin',datos, []);
    if (get(formName + '.hid_cbPeriodoFin')!=''){
      iSeleccionadob = new Array();          
      iSeleccionadob[0] = get(formName + '.hid_cbPeriodoFin');      
      set(formName + '.cbPeriodoFin',iSeleccionadob);        
    }   
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function recargarPeriodoIni(datos){

    set_combo(formName + '.cbPeriodoInicio',datos, []);
    if (get(formName + '.hid_cbPeriodoInicio')!=''){
      iSeleccionadob = new Array();          
      iSeleccionadob[0] = get(formName + '.hid_cbPeriodoInicio');      
      set(formName + '.cbPeriodoInicio',iSeleccionadob);        
    }   
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function recargarMarcaProductos(datos){

    set_combo(formName + '.cbMarca',datos, []);
    if (get(formName + '.hid_cbMarca')!=''){
      iSeleccionadob = new Array();          
      iSeleccionadob[0] = get(formName + '.hid_cbMarca');      
      set(formName + '.cbMarca',iSeleccionadob);        
    }   
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function cargarPeriodos(){
    var marca = get(formName + '.cbMarca2').toString();
    var canal = get(formName + '.cbCanal').toString();
         if(marca == '' || canal == '') {
           set_combo(formName + '.cbPeriodoInicio','');
           set_combo(formName + '.cbPeriodoFin','');
         }
    if(marca != "" && canal != "" ) {        
         recargaCombo(formName + '.cbPeriodoInicio', 'MAEBusquedaPeriodos', 'es.indra.sicc.dtos.mae.DTOBusquedaPeriodos',  MakeParameters(marca,canal));
         recargaCombo(formName + '.cbPeriodoFin', 'MAEBusquedaPeriodos', 'es.indra.sicc.dtos.mae.DTOBusquedaPeriodos', MakeParameters(marca,canal));
    }
         return true;
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function cargaCanal(id){
    set_combo(formName + '.cbCanal','');
         set_combo(formName + '.cbPeriodoInicio','');
         combo_add(formName + '.cbPeriodoInicio','','');
         set_combo(formName + '.cbPeriodoFin','');
         combo_add(formName + '.cbPeriodoFin','','');
    combo_add(formName + '.cbCanal','','');

    for(var i = 0; i < lstOculta.datos.longitud;i++) {

//         if(get(formName + ".cbTipoPeriodo").toString() == lstOculta.datos.ij(i,2))

             combo_add(formName + '.cbCanal', lstOculta.datos.ij(i,0), lstOculta.datos.ij(i,1));
    }
}

// ------------------------------------------------------------------------------------------------------------------------------------------------------------------
function MakeParameters(marca,canal){
    var parametros = new Array([],[],[]);
    parametros[0][0] = "oidPais";
         parametros[0][1] = _get("hid_oidPais");
    parametros[1][0] = "marca";
    parametros[1][1] = marca;
    parametros[2][0] = "canal";
    parametros[2][1] = canal;
    //alert('Parametros: ' + parametros);
    return parametros;
}

