function reload_insertar(){   
         fLimpiarGenericoSICC();
         btnProxy(2, '0');
         //set_combo(formName + '.cbCanal',eval('[[""],[""]]'));
         //set_combo(formName + '.cbPeriodoInicio',eval('[[""],[""]]'));
    //set_combo(formName + '.cbPeriodoFin',eval('[[""],[""]]'));

         set_combo(formName + '.cbCanal','');
         set_combo(formName + '.cbPeriodoInicio','');
         set_combo(formName + '.cbPeriodoFin','');

         var arrCheck = document.all["rbIndicadorLote"];

         for(var i=0; i< arrCheck.length; i++){
                  if(arrCheck[i].type && arrCheck[i].type.toUpperCase() == "RADIO"){
                           document.all["rbIndicadorLote"][i].checked = false;
                  }
         }

         listado1.setDatos(new Array());



}

