function checkPorcentaje(field, cod){
  if(_get(field)!=""){
            if(ValidaNum(_get(field))!="OK" || parseInt(_get(field)) > 100 || parseInt(_get(field)) == 0 )  {
                   cdos_mostrarAlert(GestionarMensaje(cod, null , null, null));
                   _foc(field);
                   return true;
            }
    }
}

