var doc = "document.all."; 

function disable(field){
   if(field.substr(0,2)=="rb")
                  for(i=0;   i<(eval(doc+field).length-1) && eval(doc+field+"["+ i++ +"].disabled=true;"););	        
     else accion(formName+'.'+field,'.disabled=true'); 
}
//--------------------------------------------------------------------------------------------

function _selectV(id){
  if(id.substr(0,2) == "cb") {
    //alert(id + " : " + _get("hid_" + id));
    set(formName + "." + id,new Array(_get("hid_" + id)));
  } else {
    set(formName + "." + id,(_get("hid_" + id) == "true")?1:0);
  }
}
